from class_user import User
from hashlib import md5
from update import Update
import localization

args = request.arguments

if "login" in args:
	login = args["login"][0].encode("utf8")
	password = args["password"][0].encode("utf8")
	password = password and md5(password).hexdigest() or ""
	back_url = args["back_url"][0] if "back_url" in args else "/Documents_share.vdom"


	user = User.login(login, password) if login and password else None

	if user:
		try:
			self.connector.fields_list()
		except Exception, e:
			pass

		Update.run()

		self.action("goTo",[back_url])
		#request.redirect(back_url)
	else:
		self.container_center.form_login.login.action("setValue",[login.decode("UTF8")]);
		self.container_center.form_login.back_url.action("setValue",[back_url]);

		self.growl.action("show",[ loc["hp_errtitle"], loc["hp_errinput"] ])

from utils_old import getVal from class_user import User
guid = getVal(request.arguments, "guid")
if guid: a_user = User.get_by_guid(guid) if a_user: a_user.delete() self.action("goTo",["/Users_and_groups.vdom"])

