from class_user import User from class_group import Group from helper import scalar, ssn_scalar
is_group = scalar("is_group","0") object_to_select = scalar("guid", "", User.get_by_guid if is_group=="0" else Group.get_by_guid)
dialog_subjects = ssn_scalar("dialog_subjects")
if not object_to_select: raise Exception("BAD_GUID_OF_SUBJECT")
if is_group=="0": dialog_subjects.add_user_to_selection(object_to_select) else: dialog_subjects.add_group_to_selection(object_to_select)
dialog_subjects.render()

from class_user import User from class_group import Group from helper import scalar, ssn_scalar
is_group = scalar("is_group","0") object_to_deselect = scalar("guid", "", User.get_by_guid if is_group=="0" else Group.get_by_guid)
dialog_subjects = ssn_scalar("dialog_subjects")
if not object_to_deselect: raise Exception("BAD_GUID_OF_SUBJECT")
if is_group=="0": dialog_subjects.remove_user_from_selection(object_to_deselect) else: dialog_subjects.remove_group_from_selection(object_to_deselect)
dialog_subjects.render()

from helper import ssn_scalar
share_object = ssn_scalar("share_object") users = ssn_scalar("users_acl_temp",[]) groups = ssn_scalar("groups_acl_temp",[])
if share_object: users_old, groups_old = share_object.users_acl(), share_object.groups_acl()
users2delete = list( set(users_old) - set(users) ) users2install = list( set(users) - set(users_old)) groups2delete = list( set(groups_old) - set(groups) ) groups2install = list( set(groups) - set(groups_old))
for user in users2delete: share_object.delete_access_for(user.guid, user.rules["SEE"])
for user in users2install: share_object.add_access_to(user.guid, user.rules["SEE"])
for group in groups2delete: share_object.delete_access_for(group.guid, group.rules["SEE"])
for group in groups2install: share_object.add_access_to(group.guid, group.rules["SEE"])
import localization, cgi loc = localization.get_lang() self.growl.action("show",["%s '%s'" % (loc["RULES_OF"], cgi.escape(share_object.name)), loc["HAVE_BEEN_UPDATED"]])

dialog_subjects = session.get("dialog_subjects")

if dialog_subjects: dialog_subjects.destroy()

del session["dialog_subjects"]

from utils_old import getVal
op = getVal(request.arguments, "op") input_text = getVal(request.arguments, "input_text")
operators = { 'AND' : '%s AND', 'OR' : '%s OR', 'NOT' : '%s NOT', 'XOR' : '%s XOR', '()' : '(%s)', '+ , -' : '+%s', 'quotes': '\\"%s\\"', 'NEAR' : '%s NEAR', 'ADJ' : '%s ADJ', ' * ' : '%s*', }
if op in operators: self.cnt_srch.form.search.action("setValue",[ operators[str(op)] % input_text ])

from class_user import User


debug("---------------------------- Get File Shares -----------------------------\n")

err = "<error><code>%(code)s</code><description>%(description)s</description></error>"


try:
	user = User.current()

	if user:
		res="<file_shares>"
		for file_share in user.get_file_shares():
			res += """<file_share> <guid>%(guid)s</guid><name>%(name)s</name></file_share>"""%{ "name":file_share.name, "guid":file_share.guid}

		res+="</file_shares>"
		debug("Ok\n "+ res)
		session["response"] = res
	else:
		err = err%{"code":"5", "description":"User is not logined"}
		session["response"] = err

except Exception, e:
	import traceback
	er = traceback.format_exc()
	debug("Error: "+  str(er ))
	session["response"] = "Error: "+ str(er)

from class_user import User

args = request.arguments

User.logoff()

debug("---------------------------- L O G I N -----------------------------\n")
err = "<error><code>%(code)s</code><description>%(description)s</description></error>"

data = args["xml_data"]

if data:

	try:

		from xml.dom.minidom import parseString
		dom_param = parseString(data.encode("UTF-8"))

		log = pwd = ""
		login = dom_param.getElementsByTagName("login")
		if login and login[0].firstChild:
			log = login[0].firstChild.nodeValue

		password = dom_param.getElementsByTagName("password")
		if password and password[0].firstChild:
			pwd = password[0].firstChild.nodeValue

		user = User.login(log, pwd)

		if user:
			res = """<user><guid>%(guid)s</guid><login>%(login)s</login><photo_url>%(photo_url)s</photo_url></user>
					"""%{"guid":user.guid, "login":user.email, "photo_url":user.picture}
			session["response"] = res
		else:
			err = err%{"code":"1", "description":"Wrong login or password"}
			session["response"] = err

	except Exception, e:
		import traceback
		er = traceback.format_exc()
		debug("Error: "+  str(er ))

		err = err%{"code":"0", "description":"Server error:"+str(er)}
		session["response"] = err
else:
	err = err%{"code":"14", "description":"xml_data is absent"}
	session["response"] = err

import xml.dom

from class_user import User
from class_file_share import File_share

args = request.arguments

#User.logoff()
debug("---------------------------- CREATE FiLE SHARE -----------------------------\n")


err = "<error><code>%(code)s</code><description>%(description)s</description></error>"

if "xml_data" in args:

	try:
		user = User.current()
		if user:
			data = args["xml_data"]

			from xml.dom.minidom import parseString
			dom_param = parseString(data.encode("UTF-8"))
			"""
			<file_share>
			    <name>name</name>
			</file_share>
			"""

			new_name =  ""
			name = dom_param.getElementsByTagName("name")
			if name and name[0].firstChild:
				new_name = name[0].firstChild.nodeValue
			else:
				err = err%{"code":"13", "description":"Wrong name"}
				session["response"] = err

			if File_share.get_by_path(new_name):
				err = err%{"code":"16", "description":"File Share already exist"}
				session["response"] = err

			fale_share = File_share()
			fale_share.name = new_name
			fale_share.save()


			if fale_share:
				res = """<file_share><guid>%(guid)s</guid><name>%(name)s</name></file_share>
						"""%{"guid":fale_share.guid, "name":fale_share.name}
				debug("Ok\n" + res)
				session["response"] = res
			else:
				err = err%{"code":"15", "description":"File Share is not Created"}
				session["response"] = err
		else:
			err = err%{"code":"5", "description":"User is not logged"}
			session["response"] = err
	except Exception, e:
		import traceback
		er = traceback.format_exc()
		debug("Error: "+  str(er ))

		err = err%{"code":"0", "description":"Server error:"+str(er)}
		session["response"] = err
else:
	err = err%{"code":"14", "description":"xml_data is absent"}
	session["response"] = err

from class_user import User
from class_ACL import ACL
from class_ACL_proxy import ACL_record
args = request.arguments

#User.logoff()

debug("---------------------------- G E T   A C L  -----------------------------\n")
err = "<error><code>%(code)s</code><description>%(description)s</description></error>"

rules = {"read":0, "write":1, "user_admin":2, "admin":3}

if "xml_data" in args:

	try:
		user = User.current()
		if user:
			data = args["xml_data"]
			debug(str(data))
			"""
			<get_acl>
			    <subject_guid>user_guid1</subjct_guid>
			    <object_guids>guid1,guid2,guid3,...</object_guids>
			</get_acl>
			"""

			from xml.dom.minidom import parseString
			dom_param = parseString(data.encode("UTF-8"))

			log = pwd = ""
			subject_guid = dom_param.getElementsByTagName("subject_guid")
			if subject_guid and subject_guid[0].firstChild:
				subj_guid = subject_guid[0].firstChild.nodeValue
			else:
				err = err%{"code":"3", "description":"Wrong subject_guid"}
				session["response"] = err

			object_guids = dom_param.getElementsByTagName("object_guids")
			if object_guids and object_guids[0].firstChild:
				obj_guids = object_guids[0].firstChild.nodeValue
			else:
				err = err%{"code":"4", "description":"Wrong object_guid"}
				session["response"] = err

			res = "<acls>"
			for obj_guid in obj_guids.split(","):
				acls = ACL_record.get_acls(subj_guid, obj_guid)
				if len(acls) == 0:
					continue

				acl_rule = acls[0].rule
				acl_rule_int = rules[acl_rule]

				if len(acls) > 1:
					for a in acls:
						if rules[a.rule] > acl_rule_int:
							acl_rule = a.rule
							acl_rule_int = rules[a.rule]

				res +="""<acl><subject_guid>%(subject_guid)s</subject_guid> <object_guid>%(object_guid)s</object_guid><rule>%(rule)s</rule>
					</acl>"""%{"subject_guid":subj_guid, "object_guid":obj_guid, "rule":acl_rule}
			res += "</acls>"
			debug("Ok")
			session["response"] = res
		else:
			err = err%{"code":"5", "description":"User is not logged"}
			session["response"] = err
	except Exception, e:
		import traceback
		er = traceback.format_exc()
		debug("Error: "+  str(er ))

		err = err%{"code":"0", "description":"Server error:"+str(er)}
		session["response"] = err
else:
	err = err%{"code":"14", "description":"xml_data is absent"}
	session["response"] = err

try:
	import xml.dom
	from utils.uuid import uuid4

	from class_user import User
	from class_file_share import File_share

	debug("---------------------------- A D D   F I L E  -----------------------------\n")
	err = "<error><code>%(code)s</code><description>%(description)s</description></error>"

	user = User.current()
	args = request.arguments
	if user is None:
		err = err%{"code":"5", "description":"User is not logged"}
		session["response"] = err

	if "xml_data" in args:
		data = request.arguments["xml_data"]
		"""
		<file>
		    <share_guid>guid</share_guid>
		    <path>/path/to/folder</path>
		    <filename>filename</filename>
		    <data>[base64 encoded contents of the file]</data>
		</file>

		"""

		from xml.dom.minidom import parseString
		dom_param = parseString(data.encode("UTF-8"))

		share_guid = dom_param.getElementsByTagName("share_guid")
		if share_guid and share_guid[0].firstChild:
			share_guid = share_guid[0].firstChild.nodeValue
			file_share = File_share.get_by_guid(share_guid)

			if file_share is None:
				err = err%{"code":"7", "description":"file_share is None"}
				session["response"] = err

			elif user.has_write_access_into(file_share) == False:
				err = err%{"code":"8", "description":"can not write into share"}
				session["response"] = err


		else:
			#session["response"] = ("response","Error: Wrong in share_guid")
			err = err%{"code":"9", "description":"Wrong in share_guid"}
			session["response"] = err


		path_0 = ""
		path = dom_param.getElementsByTagName("path")
		if path and path[0].firstChild:
			path_0 = path[0].firstChild.nodeValue
		else:
			#session["response"] = ("response","Error: Wrong in sharepath")
			err = err%{"code":"10", "description":"Wrong in path"}
			session["response"] = err

		filename = dom_param.getElementsByTagName("filename")
		if filename and filename[0].firstChild:
			filename = filename[0].firstChild.nodeValue
		else:
			#session["response"] = ("response","Error: Wrong in filename")
			err = err%{"code":"11", "description":"Wrong in filename"}
			session["response"] = err


		data = dom_param.getElementsByTagName("data")
		if data and data[0].firstChild:
			data=data[0].firstChild.nodeValue

			import base64
			bb=str(data)
			file_data = base64.b64decode(bb)

			if file_share.add_file(path_0, filename, file_data):
				debug("Ok")
				session["response"] = "<result>ok</result>"
			else:
				err = err%{"code":"12", "description":"Wrong in filename"}
				session["response"] =err
		else:
			err = err%{"code":"13", "description":"Wrong in data"}
			session["response"] = err

	else:
		#session["response"] = ("response","Error: xml_data is absent")
		err = err%{"code":"14", "description":"xml_data is absent"}
		session["response"] = err
except Exception, e:
	import traceback
	er = traceback.format_exc()
	debug("Error: "+  str(er ))

	err = err%{"code":"0", "description":"Server error:"+str(er)}
	session["response"] = err

import xml.dom

from class_user import User
from class_folder import Folder

args = request.arguments

#User.logoff()
debug("---------------------------- DS_LIST -----------------------------\n")


err = "<error><code>%(code)s</code><description>%(description)s</description></error>"
res = ""

if "xml_data" in args:

	try:
		user = User.current()
		if user:
			data = args["xml_data"]

			#from log.log import VDOM_log_manager
			#VDOM_log_manager().info_user(data)

			from xml.dom.minidom import parseString
			dom_param = parseString(data.encode("UTF-8"))
			"""P
			<xml>
				<guid>guid</guid>
			</xml>
			"""

			new_name =  ""
			res += "<path>"
			guid = dom_param.getElementsByTagName("guid")
			if guid and guid[0].firstChild:
				folder_guid = guid[0].firstChild.nodeValue
				folder = Folder.get_by_guid(folder_guid)
				if folder:
					for a_folder in user.get_subfolders(folder):
						res += """<folder><guid>%(guid)s</guid><name>%(name)s</name></folder>"""%{"guid":a_folder.guid, "name":a_folder.name}

					for doc_share in user.get_documents_shares(folder):
						res += """<document_share><guid>%(guid)s</guid><name>%(name)s</name></document_share>"""%{"guid":doc_share.guid, "name":doc_share.name}
				else:
					err = err%{"code":"21", "description":"Wrong folder guid"}
					session["response"] = err
			else:
				for a_folder in  user.get_top_folders():
					res += """<folder><guid>%(guid)s</guid><name>%(name)s</name></folder>"""%{"guid":a_folder.guid, "name":a_folder.name}

			res += "</path>"
			debug("Ok\n" + res)
			session["response"] = res

		else:
