def get_folder(folder):
	_res =  """<folder><guid>%(guid)s</guid><name>%(name)s</name><content>"""%{"guid":folder.guid, "name":folder.name}
	for a_folder in user.get_subfolders(folder):
		_res += get_folder(a_folder)

	for doc_share in user.get_documents_shares(folder):
		_res += """ <document_share><guid>%(guid)s</guid><name>%(name)s</name></document_share>"""%{"guid":doc_share.guid, "name":doc_share.name}
	_res += "</content></folder>"
	return _res

