Attributes.Attribute:
function AddToFileShareDialog( ui, application )
{
	this.ui = ui;
	this.application = application;

	this.ui.windowIcon = new QIcon( "eye_on_files_icon.gif" );

	var now = new Date();

	DD = ("0" + now.getDate()).slice(-2);
	MM = ("0" + (now.getMonth() + 1)).slice(-2);
	YYYY = ("000" + now.getFullYear()).slice(-4);
	HH = ("0" + now.getHours()).slice(-2);
	mm = ("0" + now.getMinutes()).slice(-2);
	ss = ("0" + now.getSeconds()).slice(-2);

	file_name = "file_" + DD + "-" + MM + "-" + YYYY + "_" + HH + "-" + mm + "-" + ss;

	this.ui.formtextFileName.text = file_name;

	var api = this.application.Object( "API" );
	var result = api.CallAction( "FS_list_shares", "" );

	// clear shares dropdown list
	this.ui.formdropdownShare.clear();

	var doc = new QDomDocument();
	doc.setContent( result );

	for ( var current = doc.documentElement().firstChildElement( "file_share" ); !current.isNull(); current = current.nextSiblingElement( "file_share" ) )
	{
		for ( var tag = current.firstChildElement(); !tag.isNull(); tag = tag.nextSiblingElement() )
		{
			switch ( tag.tagName() )
			{
				case 'guid':
					var guid = tag.text();
					break;

				case 'name':
					var name = tag.text();
					break;
			}
		}

		// if valid guid
		if ( guid.match( /^[\da-f]{8}\-[\da-f]{4}\-[\da-f]{4}\-[\da-f]{4}\-[\da-f]{12}$/ ) )
		{
			this.ui.formdropdownShare.addItem( name, guid );
		}

	}

	with (ui)
	{
		formbuttonOK.clicked.connect( this, this.tryToUpload );
		formbuttonCancel.clicked.connect( this, this.reject );
	}

}

AddToFileShareDialog.prototype.Run = function()
{
	if ( this.ui.exec() )
	{
		return true;
	}
	else
	{
		return false;
	}
}

AddToFileShareDialog.prototype.tryToUpload = function()
{

	// index of selected file share entry
	var index = this.ui.formdropdownShare.currentIndex;

	// if there is no file shares
	if (index == -1)
		return;

	var share_guid = this.ui.formdropdownShare.itemData( index );

	var api = this.application.Object( "API" );

	var params_xml = "<file>" +
					"<share_guid>" +
						share_guid +
					"</share_guid>" +
					"<path>" +
					"</path>" +
					"<filename>" +
						"<!" + "[CDATA[" + this.getFileName() + ".pdf" + "]" + "]>" +
					"</filename>" +
					"<data>" +
						"<!" + "[CDATA[" + Document.PdfFile().toBase64() + "]" + "]>" +
					"</data>" +
				"</file>";

	var result = api.CallAction( "FS_put_file", params_xml ); 

	if( result == "<result>ok</result>\n" )
	{					
		this.accept();
	}
	else
	{
		this.reject();
	}
}

AddToFileShareDialog.prototype.accept = function()
{
	this.ui.accept();
}

AddToFileShareDialog.prototype.reject = function()
{
	this.ui.reject();
}

AddToFileShareDialog.prototype.getFileName = function()
{
	return this.ui.formtextFileName.text;
}

// Entry point

function AddToFileShareStartPoint()
{
	service.ExceptionsEnabled = false;

	application = service.Application( "c1d6041c-6b33-49e8-ac72-8df57ac01304" );

	application.GetResource( "237327f0-7a67-4194-8f3a-332c7dcf5767" ).saveToFile( "p2w_hgmb_authentication.ui" );
	application.GetResource( "ac5579db-93b5-463d-bdac-c4fbedff89fe" ).saveToFile( "p2w_hgmb_add_to_file_share.ui" );

	application.GetResource( "e39c4a51-4671-4a18-bc76-dfd0e8dbeae5" ).saveToFile( "eye_on_files_icon.gif" );
	application.GetResource( "f2f315bd-0963-4311-9dd1-7d06a21ab258" ).saveToFile( "eye_on_files_logo.png" );

	var authenticationDialog = new AuthenticationDialog( uiloader.loadFromFile( 'p2w_hgmb_authentication.ui' ), application );

	// this is true on successfull login
	if( authenticationDialog.Run() )
	{
		var addDocumentDialog = new AddToFileShareDialog( uiloader.loadFromFile( 'p2w_hgmb_add_to_file_share.ui' ), application );
		addDocumentDialog.Run();
	}
}

function AddToDocumentShareDialog( ui, application )
{
	this.ui = ui;
	this.application = application;

	this.ui.windowIcon = new QIcon( "eye_on_files_icon.gif" );

	this.ui.formbuttonOK.enabled = false;

	var now = new Date();

	DD = ("0" + now.getDate()).slice(-2);
	MM = ("0" + (now.getMonth() + 1)).slice(-2);
	YYYY = ("000" + now.getFullYear()).slice(-4);
	HH = ("0" + now.getHours()).slice(-2);
	mm = ("0" + now.getMinutes()).slice(-2);
	ss = ("0" + now.getSeconds()).slice(-2);

	file_name = "file_" + DD + "-" + MM + "-" + YYYY + "_" + HH + "-" + mm + "-" + ss;

	//var dt=QDateTime.currentDateTime().toString("dd-MM-yyyy_hh-mm-ss");

	this.ui.formtextFileName.text = file_name;

	var api = this.application.Object( "API" );
	var result = api.CallAction( "DS_tree", "" ); 

	this.ui.treeShares.clear();

	var doc = new QDomDocument();
	doc.setContent( result );

	this.parseTreeXML( doc.documentElement(), 0 );	

	with (ui)
	{
		treeShares.currentItemChanged.connect( this, this.updateOKButton );
		formbuttonOK.clicked.connect( this, this.fetchDocumentShareMetafields );
		formbuttonCancel.clicked.connect( this, this.reject );
	}

}

AddToDocumentShareDialog.prototype.parseTreeXML = function( parent, item )
{
	for (var current = parent.firstChildElement(); !current.isNull(); current = current.nextSiblingElement())
	{
		var guid_element = current.firstChildElement( "guid" );
		var name_element = current.firstChildElement( "name" );

		if ( current.nodeName() == "folder" )
		{
			var content_element = current.firstChildElement( "content" )
			// QTreeWidgetItem::UserType == 1000
			var newitem = new QTreeWidgetItem( 1000 );
			newitem.setText( 0, name_element.text() );
			// Qt::UserRole == 32
			newitem.setData( 0, 32, guid_element.text() );
			newitem.setIcon( 0, new QIcon( "folder_icon.png" ) );

			if ( item != 0 )
			{
				item.addChild( newitem );
			}
			else
			{
				this.ui.treeShares.addTopLevelItem( newitem );
			}

			this.parseTreeXML( content_element, newitem );

		}
		else if ( current.nodeName() == "document_share" )
		{
			var newitem = new QTreeWidgetItem( 1001 );
			newitem.setText( 0, name_element.text() );
			newitem.setData( 0, 32, guid_element.text() );
			newitem.setIcon( 0, new QIcon( "ds_icon.png" ) );
			item.addChild( newitem );
		}
	}
}

AddToDocumentShareDialog.prototype.getDocumentShareGuid = function()
{
	return this.ui.treeShares.currentItem().data( 0, 32 );
}

AddToDocumentShareDialog.prototype.getFileName = function()
{
	return this.ui.formtextFileName.text + ".pdf";
}

AddToDocumentShareDialog.prototype.Run = function()
{
	if ( this.ui.exec() )
	{
		return true;
	}
	else
	{
		return false;
	}
}

AddToDocumentShareDialog.prototype.updateOKButton = function()
{
	if ( this.ui.treeShares.currentItem().type() == 1001 )
		this.ui.formbuttonOK.enabled = true;
	else
		this.ui.formbuttonOK.enabled = false;
}

AddToDocumentShareDialog.prototype.fetchDocumentShareMetafields = function()
{

	var api = this.application.Object( "API" );

	var params_xml = "<xml>" +
					"<guid>" +
						this.getDocumentShareGuid() +
					"</guid>" +
				"</xml>";

	var result = api.CallAction( "DS_share_info", params_xml ); 

	var doc = new QDomDocument();
	doc.setContent( result );

	var metafields_element = doc.documentElement().firstChildElement( "metafields" );

	if ( metafields_element.text() != "" )
	{
		// there are some metafields exist, we need to parse them, generate metafields dialog and show to user

		var metafieldsDialog = new MetafieldsDialog( uiloader.loadFromFile( 'p2w_hgmb_add_to_document_share_metafields.ui' ), application, metafields_element );

		if ( metafieldsDialog.Run() )
		{
			// user pressed OK, so start uploading
			this.tryToUpload( metafieldsDialog.getMetafieldsArray() );
		}
		else
		{
			// user pressed Cancel
		}
	}
	else
	{
		// there is no metafields, we can start uploading immediately
		this.tryToUpload( null );
	}

}

AddToDocumentShareDialog.prototype.accept = function()
{
	this.ui.accept();
}

AddToDocumentShareDialog.prototype.reject = function()
{
	this.ui.reject();
}

AddToDocumentShareDialog.prototype.tryToUpload = function( metafields )
{

	var api = this.application.Object( "API" );

	var params_xml = "<file>" +
					"<share_guid>" +
						this.getDocumentShareGuid() +
					"</share_guid>" +
					"<filename>" +
						"<!" + "[CDATA[" + this.getFileName() + "]" + "]>" +
						//"PoÃ¨me ÑÐµÑÑ.txt" +
					"</filename>" +
					"<data>" +
						"<!" + "[CDATA[" + Document.PdfFile().toBase64() + "]" + "]>" +
					"</data>";

	// if there are any metafields
	if ( Array.isArray( metafields ) )
	{
		params_xml += "<metafields>";
		for (var pos = 0; pos < metafields.length; pos++)
		{
			if ( !( metafields[ pos ] instanceof Metafield ) )
				continue;

			params_xml +=	"<field>" +
							"<id>" +
								metafields[ pos ].id +
							"</id>";

			params_xml += "<value>";

			if ( metafields[ pos ].value instanceof VDOM_formtext )
				params_xml += metafields[ pos ].value.text;
			else if ( metafields[ pos ].value instanceof VDOM_formdropdown )
				params_xml += metafields[ pos ].value.currentText;
			else if ( metafields[ pos ].value instanceof QDateEdit )
			{
				// Why the hell when we obtain QDate it's implicitly (!) converted to buildin ECMA Date?
				// So, print( mf.value.date.toString( Qt.ISODate ) );
				// gives smth like: Thu Dec 19 2019 00:00:00 GMT+0600 (Naaa?iay Oaio?. Acey (ceia))
				// what is the result of ECMA's Date().toString() and it's even not customizable!

				var date = metafields[ pos ].value.date;
				DD = ("0" + date.getDate()).slice(-2);
				MM = ("0" + (date.getMonth() + 1)).slice(-2);
				YYYY = ("000" + date.getFullYear()).slice(-4);			

				params_xml += YYYY + "-" + MM + "-" + DD;
			}

			params_xml +=		"</value>" +
						"</field>";
		}
		params_xml += "</metafields>";
	}

	params_xml += "</file>";

	//params_xml = "<file><share_guid>dcb054a6-0b8b-4360-a3e5-4b249e16c392</share_guid><filename>PoÃ¨me.txt</filename><text></text><metafileds></metafileds><data></data></file>"

	var result = api.CallAction( "DS_put_document", params_xml );

	//print( params_xml );
	//print( result );

	if ( result == "<result>ok</result>\n" )
	{
		// successfull upload
		this.accept();	
	}
	else
	{
		// unsuccessfull upload
		this.reject();	
	}


}

// MetafieldsDialog

function MetafieldsDialog( ui, application, metafields )
{
	this.ui = ui;
	this.application = application;

	this.ui.windowIcon = new QIcon( "eye_on_files_icon.gif" );

	this.array = new Array();

	var formLayout = new QFormLayout();

	// setting the layout of scrollAreaWidgetContents container to just created form layout
	this.ui.scrollArea.widget().setLayout( formLayout );

	for ( var current = metafields.firstChildElement( "field" ); !current.isNull(); current = current.nextSiblingElement( "field" ) )
	{
		for ( var tag = current.firstChildElement(); !tag.isNull(); tag = tag.nextSiblingElement() )
		{
			switch ( tag.tagName() )
			{
				case 'id':
					var id = tag.text();
					break;

				case 'name':
					var name = tag.text();
					break;

				case 'type':
					// 1 - string
					// 2 - date
					// 3 - float
					// 4 - list
					var type = tag.text();
					break;

				case 'default':
					var default_value = tag.text();
					break;

				case 'constraint':
					var constraint = tag.text();
					break;

				case 'required':
					var required = tag.text();
					break;

				case 'user_can_modify':
					var user_can_modify = tag.text();
					break;

				case 'visible':
					var visible = tag.text();
					break;
			}
		}

		if ( visible != "1" )
			continue;

		//print( "type = " + type + " constraint = " + constraint );

		switch ( type )
		{
			case '1': case '3':
				var formtextMetafield = new VDOM_formtext();
				formtextMetafield.text = default_value;
				formtextMetafield.toolTip = "<p style=\"margin-top:10px; margin-bottom:10px; margin-left:10px; margin-right:10px;\">";
				formtextMetafield.toolTip += type == '1' ? "String" : "Float";	
				formtextMetafield.toolTip += "</p>";
				break;
			case '2':
				var formtextMetafield = new QDateEdit();
				formtextMetafield.toolTip = "<p style=\"margin-top:10px; margin-bottom:10px; margin-left:10px; margin-right:10px;\">";
				formtextMetafield.toolTip += "YYYY-MM-DD";
				formtextMetafield.toolTip += "</p>";
				var calendarWidget = new QCalendarWidget();
				calendarWidget.setStyleSheet( "color: #000; border: 0px; border-radius: 0px;" );
				calendarWidget.setGridVisible( true );
				formtextMetafield.date = default_value != "" ? QDate.fromString(default_value, Qt.ISODate) : QDate.currentDate();
				formtextMetafield.calendarPopup = true;			
				formtextMetafield.setCalendarWidget(calendarWidget);
				formtextMetafield.displayFormat = "yyyy-MM-dd";
				break;
			case '4':
				var formtextMetafield = new VDOM_formdropdown();
				var selectors = constraint.split( ',' );

				for ( var pos = 0; pos < selectors.length; pos++ )
				{
					//selectors[ pos ] = selectors[ pos ].trim();

					var str = selectors[ pos ];

					// there is no trim() in Qt 4.6 (2010.04)
					// appeared just in Qt 4.7 (2010.05)
					for ( var start = 0;
					       (str.charAt(start) == ' ' || str.charAt(start) == '\t' || str.charAt(start) == '\n') && start < str.length;
					       start++ );
					for ( var end = str.length - 1;
					       (str.charAt(end) == ' ' || str.charAt(end) == '\t' || str.charAt(end) == '\n') && end > -1;
					       end-- );

					selectors[ pos ] = (start < end) ? str.substring(start, end + 1) : "";
				}

				formtextMetafield.addItems( selectors );
				formtextMetafield.currentText = default_value;
				break;
		}

		formtextMetafield.objectName = name;
		formtextMetafield.setStyleSheet( "background-color: white; border-style: solid; border: 1px solid #aaf; border-radius: 6px;" );

		if ( user_can_modify != "1" )
			formtextMetafield.setEnabled( false ); 

		if ( required == "1" )
			name += " *";

		formLayout.addRow( name, formtextMetafield );

		var mf = new Metafield( id, name, type, required, formtextMetafield );
		this.array.push( mf );

	}

	//var obj = { a: array, b: array, c: 123 };

	with (ui)
	{
		formbuttonOK.clicked.connect( this, this.accept );
		formbuttonCancel.clicked.connect( this, this.reject );
	}
}

MetafieldsDialog.prototype.accept = function()
{
	// metafields validation, i know, QValidator is better way, but anyways

	var failedStyleSheet = "background-color: white; border-style: solid; border: 1px solid red; border-radius: 6px;";
	var okStyleSheet = "background-color: white; border-style: solid; border: 1px solid #aaf; border-radius: 6px;";

	var fail = false;

	for (var pos = 0; pos < this.array.length; pos++)
	{
		var mf = this.array[ pos ];

		if ( !( mf instanceof Metafield ) )
			continue;

		if ( mf.value instanceof VDOM_formtext )
		{
			// regexp(s) for each type should be provided in DS_tree or another server action's reply
			if ( mf.required == "1" && mf.value.text == ""/* || !mf.value.text.match( regexp )*/ )
			{
				mf.value.setStyleSheet( failedStyleSheet );
				fail = true;
			}
			else
			{
				mf.value.setStyleSheet( okStyleSheet );
			}
		}
	}

	if ( fail )
		return;

	this.ui.accept();
}

MetafieldsDialog.prototype.reject = function()
{
	this.ui.reject();
}

MetafieldsDialog.prototype.Run = function()
{
	if ( this.ui.exec() )
	{
		return true;
	}
	else
	{
		return false;
	}
}

MetafieldsDialog.prototype.getMetafieldsArray = function()
{
	return this.array;
}

// id - id of metafield
// name - name of metafield
// value - pointer to VDOM_formtext, containing current input value
function Metafield( id, name, type, required, formtext )
{
	this.id = id;
	this.name = name;
	this.type = type;
	this.required = required;
	this.value = formtext;
}

