function AddToDocumentShareStartPoint()
{
	service.ExceptionsEnabled = false;

	application = service.Application( "c1d6041c-6b33-49e8-ac72-8df57ac01304" );

	application.GetResource( "237327f0-7a67-4194-8f3a-332c7dcf5767" ).saveToFile( "p2w_hgmb_authentication.ui" );
	application.GetResource( "519b41a8-5fe1-49b1-b829-61a014ba97db" ).saveToFile( "p2w_hgmb_add_to_document_share.ui" );
	application.GetResource( "22a956d2-aec5-499b-9fba-28938e4b5847" ).saveToFile( "p2w_hgmb_add_to_document_share_metafields.ui" );

	application.GetResource( "e39c4a51-4671-4a18-bc76-dfd0e8dbeae5" ).saveToFile( "eye_on_files_icon.gif" );
	application.GetResource( "f2f315bd-0963-4311-9dd1-7d06a21ab258" ).saveToFile( "eye_on_files_logo.png" );

	application.GetResource( "732a2116-5368-4a8d-a2f1-e13ffcc827db" ).saveToFile( "folder_icon.png" );
	application.GetResource( "459b71e3-bc74-4a47-a771-09aa587c946b" ).saveToFile( "ds_icon.png" );

	var authenticationDialog = new AuthenticationDialog( uiloader.loadFromFile( 'p2w_hgmb_authentication.ui' ), application );

	// this is true on successfull login
	if( authenticationDialog.Run() )
	{
		var addDocumentDialog = new AddToDocumentShareDialog( uiloader.loadFromFile( 'p2w_hgmb_add_to_document_share.ui' ), application );
		addDocumentDialog.Run();
	}

}

function AuthenticationDialog( ui, application )
{
	this.ui = ui;
	this.application = application;

	this.ui.windowIcon = new QIcon( "eye_on_files_icon.gif" );

	// creating settings object
	var settings = new QSettings( "VDOM", this.application.Guid );
	this.settings = settings;

	// loading settings
	var lastLogin = settings.value( "AuthenticationDialog/lastLogin", "" ).toString();
	this.ui.formtextLogin.text = lastLogin;

	// some issues with toBool() ?
	var passwordSaved = settings.value( "AuthenticationDialog/passwordSaved", "false" ).toString();

	if ( passwordSaved == "true" )
	{
		this.ui.formcheckboxSavePassword.checked = true;
		var lastPassword = settings.value( "AuthenticationDialog/lastPassword", "" ).toString();
		this.ui.formpasswordPassword.text = lastPassword;
	}
	else
	{
		this.ui.formcheckboxSavePassword.checked = false;
	}

	with ( ui )
	{
		formbuttonLogin.clicked.connect( this, this.tryToConnect );
		formbuttonCancel.clicked.connect( this, this.reject );
	}
}

AuthenticationDialog.prototype.getLogin = function()
{
	return this.ui.formtextLogin.text;
}

AuthenticationDialog.prototype.getPassword = function()
{
	return this.ui.formpasswordPassword.text;
}

AuthenticationDialog.prototype.getSavePassword = function()
{
	return this.ui.formcheckboxSavePassword.checked;
}

AuthenticationDialog.prototype.Run = function()
{
	if( this.ui.exec() )
	{
		return true;
	}
	else
	{
		return false;
	}
}

AuthenticationDialog.prototype.accept = function()
{
	this.ui.accept();
}

AuthenticationDialog.prototype.reject = function()
{
	this.ui.reject();
}

AuthenticationDialog.prototype.tryToConnect = function()
{
	// get API object
	var api = this.application.Object( "API" );

	// prepare parameters xml string
	var params_xml = "<auth>" +
					"<login>" +
						this.getLogin() +
					"</login>" +
					"<password>" +
						Document.CalculateMD5Hash( this.getPassword() ) +
					"</password>" +
				"</auth>";

	// invoke server action
	var result = api.CallAction( "log_in", params_xml );

	print("result=" + result);
	// analyze returned value
	var re = /<user>/;
	var match = result.match( re );

	// if regexp matched
	if ( match != undefined )
	{
		// successfull login

		// saving settings

		this.settings.setValue( "AuthenticationDialog/lastLogin", this.getLogin() );
		this.settings.setValue( "AuthenticationDialog/passwordSaved", this.getSavePassword() );

		if ( this.getSavePassword() )
		{
			this.settings.setValue( "AuthenticationDialog/lastPassword", this.getPassword() );
		}
		else
		{
			this.settings.setValue( "AuthenticationDialog/lastPassword", "" );
		}

		this.accept();
	}
	else
	{
		// unsuccessfull login
		this.ui.formtextLogin.styleSheet = "color: red";
		this.ui.formpasswordPassword.styleSheet = "color: red";
	}
}

