from class_user import User
from html_wrapper import Html_wrapper
import html
import localization
from class_folder import Folder
from class_document_share import Document_share

user = User.current()
args = request.arguments
loc  = localization.get_lang()

# "arguments required to be used in specific actions"
rename_args 	= ["f_btn_rename","f_name","f_guid","f_dstype"]
delete_args 	= ["f_btn_delete","f_guid","f_dstype"]
copy_args 		= ["f_btn_copy","f_guid"]
paste_args		= ["f_count","f_to_guid"]

self.title = loc["header_hgmb"] + " &raquo; " + loc["header_map_edit"]

def popup_growl(message):
	self.growl.title = loc["hp_errtitle"]
	self.growl.text = loc[message] if message in loc else message
	self.growl.active = "1"

if user:
	if user.is_admin():
		# "if RENAME" #
		if all((arg in args) for arg in rename_args):
			f_guid = args["f_guid"]
			f_name = args["f_name"]
			f_isFolder = args["f_dstype"]

			if f_isFolder == 'true':
				folder = Folder.get_by_guid(f_guid)
				if folder: 		folder.rename(f_name)
			else:
				doc_share = Document_share.get_by_guid(f_guid)
				if doc_share: 	doc_share.rename(f_name)

		# "if DELETE" #
		if all((arg in args) for arg in delete_args):
			f_guid = args["f_guid"]
			f_isFolder = args["f_dstype"]

			if f_isFolder == 'true':
				del_folder = Folder.get_by_guid(f_guid)
				if del_folder:
					del_folder.set_xapy_access(self.connector, self.search)
					try:
						del_folder.delete()
					except Exception, e:
						popup_growl(str(e))
			else:
				doc_share = Document_share.get_by_guid(f_guid)
				if doc_share:
					doc_share.set_connector(self.connector)
					doc_share.set_search(self.search)
					try:
						doc_share.delete()
					except Exception, e:
						popup_growl(str(e))

		# "if COPY" #, then save guid in session
		if all((arg in args) for arg in copy_args):
			f_guid = args["f_guid"]
			session[ "f_guid_copy"] =  f_guid
			self.form_paste.f_name.value = u"" if not "f_name" in args else args["f_name"]

		self.form_paste.visible = '1' if session["f_guid_copy"] else '0'

		# "if PASTE" #
		if all((arg in args) for arg in paste_args) and session["f_guid_copy"]:
			f_from_guid = [session["f_guid_copy"]] # possible more than one in future
			f_to_guid = args["f_to_guid"]
			f_count = args["f_count"]
			if f_from_guid and f_to_guid:
				try:
					folder = Folder.get_by_guid(f_to_guid)
					if folder is None:
						doc_share = Document_share.get_by_guid(f_to_guid)
						f_to_guid = doc_share.get_document_share_parent(f_to_guid)
						folder = Folder.get_by_guid(f_to_guid)

					folder.copy_structure(
						user, self.connector, self.search,
						f_from_guid, f_to_guid,
						count=int(f_count)
					)
				except Exception, e:
					popup_growl(str(e))

			del session["f_guid_copy"]
			self.form_paste.visible = '0'
			pass # paste operations

		# "---tree drawing---"
		folder = args["map"] if "map" in args and args["map"] else ""
		if folder:
			folder = Folder.get_by_guid(folder)
		self.tree.data = Html_wrapper.tree_builder(user, folder, self.connector, self.search, use_url=0)

		# "---buttons drawing---"
		self.form_copy_del.f_btn_copy.label = u"%(fs_copy)s" 		% { "fs_copy" 	: loc["fs_copy"] }
		self.form_copy_del.f_btn_delete.label = u"%(fs_delete)s" 	% { "fs_delete" : loc["fs_delete"] }
		self.form_paste.f_btn_paste.label = u"%(fs_paste)s" 		% { "fs_paste" 	: loc["fs_paste"] }
		self.form_rename.f_btn_rename.label = u"%(save)s" 			% { "save" 		: loc["save"] }
		self.form_rename.f_text_title.value = u"""%(ds_change)s:""" % { "ds_change" : loc["ds_change"]}

	else:
		response.redirect("/Documents_share.vdom")
else:
	back_url = Html_wrapper.get_logout_back_url()
	response.redirect("/home_page.vdom?back_url="+back_url)

