from class_user import User
from helper import scalar, xml_error_response
from xml.dom.minidom import parseString
from xml.parsers.expat import ExpatError
from engine_core import engine

from engine_core import global_log
global_log.insert(0,u"<b>WF_get_actions</b> action launched!")

success_xml = """<actions>%s</actions>"""
user = User.current()

if user:
	try:
		#xml_data = request.arguments.get("xml_data","empty")

		xml_data = request.arguments.get("xml_data", "empty")#request.arguments["xml_data"]
		if 		xml_data == "empty":
			session["response"] = xml_error_response("02","xml_data")
		elif 	not xml_data:
			session["response"] = xml_error_response("03","xml_data", xml_data)
		else:
			data = xml_data.encode("utf8")
			if data.strip() not in ['<readActions/>','<readActions></readActions>']:
				session["response"] = xml_error_response("03","xml_data", xml_data)
			else:
				result = engine.get_actions(user.guid)
				session["response"] = success_xml % "".join(result)

	except Exception, e:
		session["response"] = xml_error_response("13")
else:
	session["response"] = xml_error_response("00")

