from helper import ssn_scalar
share_object = ssn_scalar("share_object") users = ssn_scalar("users_acl_temp",[]) groups = ssn_scalar("groups_acl_temp",[])
if share_object: users_old, groups_old = share_object.users_acl(), share_object.groups_acl()
users2delete = list( set(users_old) - set(users) ) users2install = list( set(users) - set(users_old)) groups2delete = list( set(groups_old) - set(groups) ) groups2install = list( set(groups) - set(groups_old))
for user in users2delete: share_object.delete_access_for(user.guid, user.rules["SEE"])
for user in users2install: share_object.add_access_to(user.guid, user.rules["SEE"])
for group in groups2delete: share_object.delete_access_for(group.guid, group.rules["SEE"])
for group in groups2install: share_object.add_access_to(group.guid, group.rules["SEE"])
self.growl.action("show",["RULES OF %s" % share_object.name,"HAVE BEEN UPDATED"])

