Attributes.Attribute:
function Config()
{
	this.settings = new QSettings("VDOM", "Workflow Designer");
	this.settings.beginGroup(vdom.service.Host);
}

Config.prototype.read = function()
{
	this.grid = this.settings.value("grid", "true") == "true" ? true : false;
	this.snapToGrid = this.settings.value("snapToGrid", "true") == "true" ? true : false;
	this.antialiasing = this.settings.value("antialiasing", "true") == "true" ? true : false;
	//this.textAntialiasing = this.settings.value("textAntialiasing", true);
	this.lastWorkflowLocation = this.settings.value("lastWorkflowLocation", QDir.homePath());
	this.lastWorkflowGuid = this.settings.value("lastWorkflowGuid", "");
	this.openLastWorkflow = this.settings.value("openLastWorkflow", "true") == "true" ? true : false;
	this.language = this.settings.value("language", QLocale.system().name().substring(0, 2));
	this.geometry = this.settings.value("geometry");
	this.state = this.settings.value("state");
}

Config.prototype.write = function()
{
	this.settings.setValue("grid", this.grid);
    this.settings.setValue("snapToGrid", this.snapToGrid);
	this.settings.setValue("antialiasing", this.antialiasing);
	//this.settings.setValue("textAntialiasing", this.textAntialiasing);
	this.settings.setValue("lastWorkflowLocation", this.lastWorkflowLocation);
	this.settings.setValue("lastWorkflowGuid", workflowDesigner.activeWorkflow);
	this.settings.setValue("openLastWorkflow", this.openLastWorkflow);
	this.settings.setValue("language", translationManager.language());
	this.settings.setValue("geometry", workflowDesigner.saveGeometry());
	this.settings.setValue("state", workflowDesigner.saveState());
}

// TODO: vary behavior depending on argc
function ArrowItem(startItem, endItem, pixmap, parent)
{
	QGraphicsLineItem.call(this, new QLineF(), parent, 0);

	this.setStartItem(startItem);
	this.setEndItem(endItem);

	this.arrowHead = new QPolygonF();
	this.selectionPolygon = new QPolygonF();

	this.color = Qt.black;

	this.pixmapMaximumHeight = 20;
	this.pixmapMargin = 4;

	this.extra = 15;

	this.arrowSize = 20;

	this.setFlag(QGraphicsItem.ItemIsSelectable, true);

	//this.setHandlesChildEvents(true);

	//this.setPen(new QPen(this.color, 2, Qt.SolidLine, Qt.RoundCap, Qt.RoundJoin));

	// Label
	this.textItem = new QGraphicsTextItem(this);
	this.textItem.setTextInteractionFlags(Qt.TextEditorInteraction);
	var font = new QFont();
	font.setBold(true);
	font.setStyleStrategy(QFont.PreferAntialias);
	this.textItem.setFont(font);
	//this.textItem.setPlainText("Label");
	this.textItem.__QGraphicsItem__.setParentItem.call(this.textItem, this);
	this.textItem.__QGraphicsItem__.setFlag.call(this.textItem, QGraphicsItem.ItemIsSelectable, true);
	this.textItem.__QGraphicsItem__.setSelected.call(this.textItem, true);
	// limit to single line
	// bug: text is cleared when enter is pressed
	this.textItem.document().maximumBlockCount = 1;
	this.textItem.document().contentsChanged.connect(this, this.contentsChanged);

	// pixmap
	this.setPixmap(pixmap);

	// status pixmap
	this.statusPixmapItem = new QGraphicsPixmapItem(this);

	this.labelChanged = signal();

	this.setZValue(-1000.0);

	//this.setPos(0, 0);
	this.updatePosition();
}

ArrowItem.prototype = new QGraphicsLineItem();

ArrowItem.prototype.setStartItem = function(item) {
	if (item instanceof QGraphicsItem)
		this.startItem = item;
}

ArrowItem.prototype.setEndItem = function(item) {
	if (item instanceof QGraphicsItem)
		this.endItem = item;
}

ArrowItem.prototype.setPixmap = function(pixmap) {
	if (pixmap instanceof QPixmap) {
		// create pixmap if no such one
		if (this.pixmapItem == undefined) {
			this.pixmapItem = new QGraphicsPixmapItem(this);
			this.pixmapItem.setTransformationMode(Qt.SmoothTransformation);
			//this.pixmapItem.setZValue(2000.0);
		}
		this._pixmap = pixmap;
		this.pixmapItem.setPixmap(pixmap);
		this.pixmapItem.setOffset(- pixmap.width() / 2, - pixmap.height() / 2);
	}
}

ArrowItem.prototype.setStatusPixmap = function(pixmap) {
	if (pixmap instanceof QPixmap) {
		this.statusPixmapItem.setPixmap(pixmap);
	}
}

ArrowItem.prototype.setText = function(text) {
	this.textItem.setPlainText(text);
}

ArrowItem.prototype.contentsChanged = function()
{
	this.adjust();
	//var value = this.textItem.document().toPlainText();
	//this.labelChanged.emit(value);
}

ArrowItem.prototype.adjust = function()
{
	this.adjustText();
	this.adjustPixmap();
	this.adjustStatusPixmap();
}

ArrowItem.prototype.adjustText = function()
{
	if (this.textItem != undefined) {
		var textBoundingRect = this.textItem.__QGraphicsItem__.boundingRect.call(this.textItem);
		var t = ((this.line().length() + textBoundingRect.width()) / 2) / this.line().length();
		this.textItem.__QGraphicsItem__.setPos.call(this.textItem, this.line().pointAt(t));
	}
}

ArrowItem.prototype.adjustPixmap = function()
{
	if (this.pixmapItem != undefined) {
		var t = (this.line().length() - this.pixmapItem.pixmap().width() / 2) / this.line().length();
		var point = this.line().pointAt(t);
		this.pixmapItem.setPos(point);
	}
}

ArrowItem.prototype.adjustStatusPixmap = function() {
	if (this.statusPixmapItem != undefined) {
		var pixmap = this.statusPixmapItem.pixmap();
		var t = (this.arrowSize + pixmap.width() / 2 + 5) / this.line().length();
		var point = this.line().pointAt(t);
		this.statusPixmapItem.setPos(point.x() - pixmap.width() / 2, point.y() - pixmap.height() / 2);
	}
}

ArrowItem.prototype.boundingRect = function()
{
    var extra = this.pen().width() / 2.0 + this.extra;

    return new QRectF(this.line().p1(), new QSizeF(this.line().p2().x() - this.line().p1().x(),
												   this.line().p2().y() - this.line().p1().y()))
        .normalized()
        .adjusted(-extra, -extra, extra, extra);
}

ArrowItem.prototype.shape = function()
{
	var shapeMethod = ArrowItem.prototype.shape;
    ArrowItem.prototype.shape = undefined;
	var path = QGraphicsItem.prototype.shape.call(this);
	ArrowItem.prototype.shape = shapeMethod;

	//path.addPolygon(this.arrowHead);
	path.addPolygon(this.selectionPolygon);
    return path;
}

ArrowItem.prototype.updatePosition = function()
{
	if (this.startItem != undefined && this.endItem != undefined) {
		var line = new QLineF(this.mapFromItem(this.startItem, 0, 0), this.mapFromItem(this.endItem, 0, 0));
		this.setLine(line);
	}

	if (this.textItem != undefined) {
		var angle = this.line().angleTo(new QLineF(0, 0, 0, 1));
		this.textItem.__QGraphicsItem__.setRotation.call(this.textItem, 90 + angle);
		this.textItem.__QGraphicsItem__.setPos.call(this.textItem, this.line().pointAt(0.5));
	}
}

QLineF.prototype.intersectPoint = function(l)
{
	// ipmlementation is based on Graphics Gems III's "Faster Line Segment Intersection"
    var pt1 = this.p1();
	var pt2 = this.p2();
	var lpt1 = l.p1();
	var lpt2 = l.p2();

	//var a = pt2.operator_subtract_assign(pt1);
    //var b = lpt1.operator_subtract_assign(lpt2);
    //var c = pt1.operator_subtract_assign(lpt1);

	var a = new QPointF(pt2.x() - pt1.x(), pt2.y() - pt1.y());
	var b = new QPointF(lpt1.x() - lpt2.x(), lpt1.y() - lpt2.y());
	var c = new QPointF(pt1.x() - lpt1.x(), pt1.y() - lpt1.y());

    var denominator = a.y() * b.x() - a.x() * b.y();
	if (denominator == 0/* || !qt_is_finite(denominator)*/)
        return undefined;

    var reciprocal = 1 / denominator;
    var na = (b.y() * c.x() - b.x() * c.y()) * reciprocal;

	//return pt1.operator_add_assign(a.operator_multiply_assign(na));
	return new QPointF(pt1.x() + a.x() * na, pt1.y() + a.y() * na);
}

ArrowItem.prototype.paint = function(painter, option, widget)
{
	//print("ArrowItem.prototype.paint()");

	if (this.startItem == undefined || this.endItem == undefined)
		return;

	var collides = this.startItem.collidesWithItem(this.endItem);

	if (this.textItem != undefined)
		this.textItem.__QGraphicsItem__.setVisible.call(this.textItem, !collides);

	if (this.pixmapItem != undefined)
		this.pixmapItem.setVisible(!collides);

	if (collides)
        return;

	var pen = this.pen();
    pen.setColor(this.color);

	painter.setPen(pen);
    painter.setBrush(new QBrush(new QColor(this.color), Qt.SolidPattern));

	var centerLine = new QLineF(this.startItem.pos(), this.endItem.pos());

	var items = new Array();
	items.push(this.startItem);
	items.push(this.endItem);

	var intersectPoints = new Array();
	var rectPoints = new Array();

	var Pi = 3.14;

	for (var q = 0; q < 2; ++q)	{
		var item = items[q];

		switch (item.data(WorkflowScene.ItemSubType)) {
			case WorkflowScene.StartStep:
				var t = (item.boundingRect().width() / 2 - 2) / centerLine.length();
				intersectPoint = centerLine.pointAt(t);
				break;
			case WorkflowScene.StopStep:
				var t = (centerLine.length() - item.boundingRect().width() / 2) / centerLine.length();
				intersectPoint = centerLine.pointAt(t);
				break;
			case WorkflowScene.GatewayStep:
				var polygon = item.polygon();
				var p1 = polygon.at(0);
				p1.operator_add_assign(item.pos());
				var p2;
				var intersectPoint = undefined;
				var polyLine;
				for (var i = 1; i < polygon.size(); ++i) {
					p2 = polygon.at(i);
					p2.operator_add_assign(item.pos());
					polyLine = new QLineF(p1, p2);
					var intersectType = polyLine.intersect(centerLine, intersectPoint);
					if (intersectType == QLineF.BoundedIntersection) {
						intersectPoint = polyLine.intersectPoint(centerLine);
						break;
					}
					p1 = p2;
				}
				break;
			default:
				var rect = item.boundingRect();
				var p1 = rect.bottomLeft()
				p1.operator_add_assign(item.pos());
				var p2;
				var intersectPoint = undefined;
				var polyLine;

				rectPoints = [];
				rectPoints.push(rect.topLeft());
				rectPoints.push(rect.topRight());
				rectPoints.push(rect.bottomRight());
				rectPoints.push(rect.bottomLeft()); // close polygon

				for (var i = 0; i < rectPoints.length; ++i)	{
					p2 = rectPoints[i];
					p2.operator_add_assign(item.pos());
					polyLine = new QLineF(p1, p2);
					var intersectType = polyLine.intersect(centerLine, intersectPoint);
					if (intersectType == QLineF.BoundedIntersection) {
						intersectPoint = polyLine.intersectPoint(centerLine);
						break;
					}
					p1 = p2;
				}
				break;
		}

		if (intersectPoint != undefined)
			intersectPoints.push(intersectPoint);

	}

	// something went wrong
	if (intersectPoints.length != 2)
		return;

	this.setLine(new QLineF(intersectPoints[1], intersectPoints[0]));

	var Pi = 3.14;

	// polar angle
	var angle = Math.acos(this.line().dx() / this.line().length());

    if (this.line().dy() >= 0)
        angle = (Pi * 2) - angle;

	var arrowAngle = 2 * Pi / 5;

	var arrowTip = this.line().p1();

	var arrowP1 = new QPointF(Math.sin(angle + arrowAngle) * this.arrowSize,
							  Math.cos(angle + arrowAngle) * this.arrowSize);
	arrowP1.operator_add_assign(arrowTip);

	var arrowP2 = new QPointF(Math.sin(angle + Pi - arrowAngle) * this.arrowSize,
							  Math.cos(angle + Pi - arrowAngle) * this.arrowSize);
	arrowP2.operator_add_assign(arrowTip);

    this.arrowHead.clear();
    this.arrowHead.append(arrowTip);
	this.arrowHead.append(arrowP1);
	this.arrowHead.append(arrowP2);

	// draw just a part of line after pixmap to avoid overlapping
	//var t = (this.line().length() - this.pixmapItem.pixmap().width()) / this.line().length();
	var t = this.line().length();
	if (this.pixmapItem != undefined)
		t -= this.pixmapItem.pixmap().width();
	t /= this.line().length();
	var point = this.line().pointAt(t);
    painter.drawLine(new QLineF(this.line().p1(), point));

    painter.drawPolygon(this.arrowHead);

	this.updateSelectionPolygon();

	if (this.isSelected()) {
		painter.setPen(new QPen(new QColor(this.color), 1, Qt.DashLine));
		painter.setBrush(Qt.NoBrush);
		painter.drawPolygon(this.selectionPolygon);
    }

	this.adjust();

}

ArrowItem.prototype.updateSelectionPolygon = function()
{
	var Pi = 3.14;

	var angle = Math.acos(this.line().dx() / this.line().length());

    if (this.line().dy() >= 0)
        angle = (Pi * 2) - angle;

	var lineP1 = this.line().p1();
	var lineP2 = this.line().p2();

	var p1 = new QPointF(Math.sin(angle - Pi / 4) * this.extra, Math.cos(angle - Pi / 4) * this.extra);
	p1.operator_add_assign(lineP1);
	var p2 = new QPointF(Math.sin(angle - 3 * Pi / 4) * this.extra, Math.cos(angle - 3 * Pi / 4) * this.extra);
	p2.operator_add_assign(lineP1);
	var p3 = new QPointF(Math.sin(angle + 3 * Pi / 4) * this.extra, Math.cos(angle + 3 * Pi / 4) * this.extra);
	p3.operator_add_assign(lineP2);
	var p4 = new QPointF(Math.sin(angle + Pi / 4) * this.extra, Math.cos(angle + Pi / 4) * this.extra);
	p4.operator_add_assign(lineP2);

	this.selectionPolygon.clear();
	this.selectionPolygon.append(p1);
	this.selectionPolygon.append(p2);
	this.selectionPolygon.append(p3);
	this.selectionPolygon.append(p4);
}

function CircleItem(radius, brushColor, parent)
{
	QGraphicsEllipseItem.call(this, -radius, -radius, radius * 2, radius * 2, parent);

	this.setFlag(QGraphicsItem.ItemIsMovable);
    this.setFlag(QGraphicsItem.ItemIsSelectable);

	this.setAcceptsHoverEvents(true);
	this.setAcceptDrops(true);

	//var pen = new QPen();
	//pen.setWidth(1);
	//this.setPen(pen);

	this.setZValue(1000.0);

	this.brush = new QBrush(Qt.SolidPattern);
	this.setBrushColor(brushColor);
}

CircleItem.prototype = new QGraphicsEllipseItem();

CircleItem.prototype.setRadius = function(radius) {
	this.setRect(-radius, -radius, radius * 2, radius * 2);
}

CircleItem.prototype.setBrushColor = function(brushColor) {
	if (brushColor instanceof QColor) {
		//brushColor.setAlpha(255);
		this.brushColor = brushColor;
		this.brush.setColor(brushColor);
		this.setBrush(this.brush);
		this.update();
	}
}

CircleItem.prototype.hoverEnterEvent = function(event) {
	this.brushColor.setAlpha(50);
	this.setBrushColor(this.brushColor);
	this.update();
}

CircleItem.prototype.hoverLeaveEvent = function(event) {
	this.brushColor.setAlpha(100);
	this.setBrushColor(this.brushColor);
	this.update();
}

function PixmapCircleItem(pixmap, parent)
{
	QGraphicsPixmapItem.call(this);

	this.setFlag(QGraphicsItem.ItemIsMovable);
    this.setFlag(QGraphicsItem.ItemIsSelectable);

	//this.setAcceptsHoverEvents(true);
	//this.setAcceptDrops(true);

	this.setPixmap(pixmap);

	this.statusPixmapItem = new QGraphicsPixmapItem(this);

	this.statusPixmapItem.setY(this.boundingRect().y());

	this.adjustStatusPixmap();

	//QMessageBox.information(0, "", this.boundingRect());
}

PixmapCircleItem.prototype = new QGraphicsPixmapItem();

PixmapCircleItem.prototype.adjustStatusPixmap = function()
{
	if (this.statusPixmapItem != undefined) {
		this.statusPixmapItem.setX(this.boundingRect().right() - this.statusPixmapItem.pixmap().width());
	}
}

PixmapCircleItem.prototype.setPixmap = function(pixmap) {
	if (pixmap instanceof QPixmap) {
		QGraphicsPixmapItem.prototype.setPixmap.call(this, pixmap);
		this.setOffset(- pixmap.width() / 2 - 1, - pixmap.height() / 2);
	}
}

PixmapCircleItem.prototype.setStatusPixmap = function(pixmap) {
	if (pixmap instanceof QPixmap) {
		this.statusPixmapItem.setPixmap(pixmap);
	}
}

// default bounding rect is kinda weird
PixmapCircleItem.prototype.boundingRect = function() {
	var pixmap = this.pixmap();
	return new QRectF(-pixmap.width() / 2, -pixmap.height() / 2, pixmap.width(), pixmap.height());
}

