function RectItem(pixmap, parent)
{
	QGraphicsItem.call(this, parent);

	this.setFlag(QGraphicsItem.ItemIsMovable, true);
    this.setFlag(QGraphicsItem.ItemIsSelectable, true);
	this.setFlag(QGraphicsItem.ItemSendsGeometryChanges, true);

	//this.setAcceptsHoverEvents(true);
	//this.setAcceptDrops(true);

	//this.setHandlesChildEvents(true);

	this.cornerRadius = 5;

	this.textMargin = WorkflowScene.cellWidth / 2;
	this.pixmapMargin = WorkflowScene.cellWidth / 2;
	this.statusPixmapMargin = 3;

	// text child item
	this.textItem = new QGraphicsTextItem(this);
	this.textItem.setTextInteractionFlags(Qt.TextEditorInteraction);
	this.textItem.setDefaultTextColor(new QColor(Qt.white));
	var font = new QFont();
	font.setBold(true);
	font.setStyleStrategy(QFont.PreferAntialias);
	this.textItem.setFont(font);
	//this.textItem.font().setBold(true);
	this.textItem.__QGraphicsItem__.setParentItem.call(this.textItem, this);
	this.textItem.__QGraphicsItem__.setFlag.call(this.textItem, QGraphicsItem.ItemIsSelectable, true);
	this.textItem.__QGraphicsItem__.setSelected.call(this.textItem, true);
	// limit to single line
	// bug: text is cleared when enter is pressed
	this.textItem.document().maximumBlockCount = 1;
	this.textItem.document().contentsChanged.connect(this, this.contentsChanged);

	// pixmap child item
	this.pixmapItem = new QGraphicsPixmapItem(this);
	this.setPixmap(pixmap);

	// status pixmap
	this.statusPixmapItem = new QGraphicsPixmapItem(this);

	// look
	this.borderColor = new QColor("#aeb0af");
	this.gradientColor1 = new QColor("#1bb6ff");
	this.gradientColor2 = new QColor("#07a3eb");

	// pen
	this.pen = new QPen();
	this.pen.setWidth(1);
	this.pen.setColor(this.borderColor);

	// bounding rect
	this._boundingRect = new QRectF();
	this.boundingRect = function() { return this._boundingRect; }
	// assume that height is constant
	height = WorkflowScene.cellWidth * 3 - 1;
	this._boundingRect.setY(-height / 2 - this.pen.width() / 2);
	this._boundingRect.setHeight(height + this.pen.width());

	// gradient
	this.gradient = new QLinearGradient(this._boundingRect.topLeft(), this._boundingRect.bottomLeft());
	this.setGradientColors(this.gradientColor1, this.gradientColor2)

	// signals
	this.labelChanged = signal();

	// text item vertical position
	var textBoundingRect = this.textItem.__QGraphicsItem__.boundingRect.call(this.textItem);
	var dy = height - textBoundingRect.height();
	this.textItem.__QGraphicsItem__.setY.call(this.textItem, this._boundingRect.y() + dy / 2)

	// pixmap item vertical position
	this.pixmapItem.setY(this._boundingRect.y() + this.pixmapMargin);

	// status pixmap item vertical position
	this.statusPixmapItem.setY(this._boundingRect.y() + this.statusPixmapMargin + this.pen.width() / 2);

	// adjust horizontal position of child items
	this.adjust();
}

RectItem.prototype = new QGraphicsItem();

RectItem.prototype.setBorderColor = function(color)
{
	if (color instanceof QColor) {
		this.borderColor = color;
		this.pen.setColor(color);
	}
}

RectItem.prototype.setGradientColors = function(color1, color2)
{
	if (color1 instanceof QColor && color2 instanceof QColor) {
		this.gradient.setColorAt(0, color1);
		this.gradientColor1 = color1;
		this.gradient.setColorAt(1, color2);
		this.gradientColor2 = color2;
		this.brush = new QBrush(this.gradient);
	}
}

RectItem.prototype.setPixmap = function(pixmap) {
	if (pixmap instanceof QPixmap) {
		this._pixmap = pixmap;
		this.pixmapItem.setPixmap(pixmap);
		//this.pixmapItem.setOffset(- pixmap.width() / 2, - pixmap.height() / 2);
	}
}

RectItem.prototype.setStatusPixmap = function(pixmap) {
	if (pixmap instanceof QPixmap) {
		this.statusPixmapItem.setPixmap(pixmap);
	}
}

RectItem.prototype.setText = function(text) {
	this.textItem.setPlainText(text);
}

RectItem.prototype.paint = function(painter, option, widget) {
	//painter.setBrush(new QBrush(new QColor(0, 0, 255, 100), Qt.SolidPattern));
	painter.setPen(this.pen);
	painter.setBrush(this.brush);
	//painter.setRenderHint(QPainter.Antialiasing, false);
	painter.drawRoundedRect(this._boundingRect,	this.cornerRadius, this.cornerRadius);

	// ###
	// BORDER IS WEIRD
	// ###

	if (this.isSelected()) {
		// draw border here
		var pad = this.pen.width() / 2;
		pad = 0;

		var penWidth = 0; // cosmetic pen

		/*
		var fgcolor = option.palette.windowText().color();
		var bgcolor = QColor( // ensure good contrast against fgcolor
			fgcolor.red()   > 127 ? 0 : 255,
			fgcolor.green() > 127 ? 0 : 255,
			fgcolor.blue()  > 127 ? 0 : 255);
		*/

		var fgcolor = new QColor(Qt.black);
		var bgcolor = new QColor(Qt.white);

		painter.setPen(new QPen(bgcolor, penWidth, Qt.SolidLine));
		painter.setBrush(Qt.NoBrush);
		painter.drawRect(this.boundingRect().adjusted(pad, pad, -pad, -pad));
		//painter.drawRect(this.boundingRect().adjusted(-pad, -pad, pad, pad));

		//painter.setPen(new QPen(option.palette.windowText(), 0, Qt.DashLine));
		painter.setPen(new QPen(fgcolor, 0, Qt.DashLine));
		painter.setBrush(Qt.NoBrush);
		painter.drawRect(this.boundingRect().adjusted(pad, pad, -pad, -pad));
		//painter.drawRect(this.boundingRect().adjusted(-pad, -pad, pad, pad));

		//painter.setPen(new QPen(new QColor(Qt.red), 2, Qt.SolidLine));
		//painter.setBrush(Qt.NoBrush);
		//painter.drawRect(this.boundingRect().adjusted(pad, pad, -pad, -pad));

	}
}

RectItem.prototype.contentsChanged = function() {
	this.adjust();
	//var value = this.textItem.document().toPlainText();
	//this.labelChanged.emit(value);
}

RectItem.prototype.adjust = function() {
	var boundingRect = this._boundingRect;
	var textBoundingRect = this.textItem.__QGraphicsItem__.boundingRect.call(this.textItem);

	this.prepareGeometryChange();

	// update boundingRect
	var width = textBoundingRect.width() + this.textMargin * 2 + this.pen.width();

	if (this._pixmap != undefined)
		width += this._pixmap.width() + this.textMargin;

	// it seems this prevent blur when drawing antialiased border of selected items
	// not sure though
	width = Math.round(width);

	// we want rects to be aligned to cellWidth (snap to grid)
	// TODO: make an settings option for that
	width = Math.round(width / WorkflowScene.cellWidth) * WorkflowScene.cellWidth;

	this._boundingRect.setX(-width / 2);
	this._boundingRect.setWidth(width);

	// delta to move text item
	var dx = width - textBoundingRect.width() - this.textMargin * 2 - this.pen.width();
	if (this._pixmap != undefined)
		dx -= this._pixmap.width() + this.textMargin;
	dx /= 2;

	// update text item horizontal position
	var x = this._boundingRect.x() + this.textMargin + this.pen.width() / 2 + dx;
	if (this._pixmap != undefined)
		x += this._pixmap.width() + this.textMargin;
	this.textItem.__QGraphicsItem__.setX.call(this.textItem, x);

	// update pixmap horizontal position
	this.pixmapItem.setX(boundingRect.x() + this.pixmapMargin + this.pen.width() / 2);

	// status pixmap
	this.adjustStatusPixmap();
}

RectItem.prototype.adjustStatusPixmap = function() {
	if (this.statusPixmapItem != undefined)
		this.statusPixmapItem.setX(this._boundingRect.right() - this.statusPixmapItem.pixmap().width() - this.statusPixmapMargin - this.pen.width() / 2);
}

function DiamondItem(parent)
{
	QGraphicsPolygonItem.call(this);

	this.setFlag(QGraphicsItem.ItemIsMovable, true);
    this.setFlag(QGraphicsItem.ItemIsSelectable, true);
	this.setFlag(QGraphicsItem.ItemSendsGeometryChanges, true);

	//this.setAcceptsHoverEvents(true);
	//this.setAcceptDrops(true);

	//this.setHandlesChildEvents(true);

	this.textMargin = WorkflowScene.cellWidth;
	this.statusPixmapMargin = 3;

	// text child item
	this.textItem = new QGraphicsTextItem(this);
	this.textItem.setTextInteractionFlags(Qt.TextEditorInteraction);
	this.textItem.setDefaultTextColor(new QColor(Qt.white));
	var font = new QFont();
	font.setBold(true);
	font.setStyleStrategy(QFont.PreferAntialias);
	this.textItem.setFont(font);
	//this.textItem.font().setBold(true);
	this.textItem.__QGraphicsItem__.setParentItem.call(this.textItem, this);
	this.textItem.__QGraphicsItem__.setFlag.call(this.textItem, QGraphicsItem.ItemIsSelectable, true);
	this.textItem.__QGraphicsItem__.setSelected.call(this.textItem, true);
	// limit to single line
	// bug: text is cleared when enter is pressed
	this.textItem.document().maximumBlockCount = 1;
	this.textItem.document().contentsChanged.connect(this, this.contentsChanged);

	// status pixmap
	this.statusPixmapItem = new QGraphicsPixmapItem(this);

	// look
	this.borderColor = new QColor("#aeb0af");
	this.gradientColor1 = new QColor("#1bb6ff");
	this.gradientColor2 = new QColor("#07a3eb");

	// pen
	var pen = new QPen();
	pen.setWidth(1);
	pen.setColor(this.borderColor);
	this.setPen(pen);

	this.adjustPolygon(WorkflowScene.cellWidth);

	var height = WorkflowScene.cellWidth * 2;

	// gradient
	this.gradient = new QLinearGradient(this.boundingRect().topLeft(), this.boundingRect().bottomLeft());
	this.setGradientColors(this.gradientColor1, this.gradientColor2)

	// signals
	this.labelChanged = signal();

	// text item vertical position
	var textBoundingRect = this.textItem.__QGraphicsItem__.boundingRect.call(this.textItem);
	var dy = height - textBoundingRect.height();
	this.textItem.__QGraphicsItem__.setY.call(this.textItem, this.boundingRect().y() + dy / 2)

	// adjust position of child items
	this.adjust();
}

DiamondItem.prototype = new QGraphicsPolygonItem();

DiamondItem.prototype.setBorderColor = function(color)
{
	if (color instanceof QColor) {
		this.borderColor = color;
		this.pen().setColor(color);
	}
}

DiamondItem.prototype.setGradientColors = function(color1, color2)
{
	if (color1 instanceof QColor && color2 instanceof QColor) {
		this.gradient.setColorAt(0, color1);
		this.gradientColor1 = color1;
		this.gradient.setColorAt(1, color2);
		this.gradientColor2 = color2;
		this.brush = new QBrush(this.gradient);
		this.setBrush(this.brush);
	}
}

DiamondItem.prototype.setStatusPixmap = function(pixmap) {
	if (pixmap instanceof QPixmap) {
		this.statusPixmapItem.setPixmap(pixmap);
	}
}

DiamondItem.prototype.setText = function(text) {
	this.textItem.setPlainText(text);
}

DiamondItem.prototype.contentsChanged = function() {
	this.adjust();
}

DiamondItem.prototype.adjust = function() {
	var boundingRect = this._boundingRect;
	var textBoundingRect = this.textItem.__QGraphicsItem__.boundingRect.call(this.textItem);

	this.prepareGeometryChange();

	// update boundingRect
	var width = textBoundingRect.width() + this.textMargin * 2 + this.pen().width();

	// it seems this prevent blur when drawing antialiased border of selected items
	// not sure though
	width = Math.round(width);

	// we want rects to be aligned to cellWidth (snap to grid)
	// TODO: make an settings option for that
	width = Math.round(width / WorkflowScene.cellWidth) * WorkflowScene.cellWidth;

	this.adjustPolygon(width / 2 - this.pen().width() / 2);

	var dx = (width - textBoundingRect.width() - this.textMargin * 2 - this.pen().width()) / 2;

	// update text item horizontal position
	var x = this.boundingRect().x() + this.textMargin + this.pen().width() / 2 + dx;
	this.textItem.__QGraphicsItem__.setX.call(this.textItem, x);

	// status pixmap
	this.adjustStatusPixmap();
}

DiamondItem.prototype.adjustStatusPixmap = function() {
	if (this.statusPixmapItem != undefined) {
		this.statusPixmapItem.setX(this.boundingRect().right() - this.statusPixmapItem.pixmap().width() - this.statusPixmapMargin - this.pen().width() / 2);
		this.statusPixmapItem.setY(this.boundingRect().y() + this.statusPixmapMargin + this.pen().width() / 2);
	}
}

DiamondItem.prototype.adjustPolygon = function(halfDiag) {
	var polygon = new QPolygonF();
	polygon.append(new QPointF(-halfDiag, 0));
	polygon.append(new QPointF(0, halfDiag));
	polygon.append(new QPointF(halfDiag, 0));
	polygon.append(new QPointF(0, -halfDiag));
	polygon.append(new QPointF(-halfDiag, 0));
	this.setPolygon(polygon);
}

