function ResourceManager()
{
	this.settings = new QSettings("VDOM", "Workflow Designer");
	this.resourceCachePrefix = "Cache/Resources/";

	// vdom resources utilised by workflow designer
	this.resources = {
		// workflow steps
		'start_step': 'b4920b84-0866-4c9a-9aed-9b1483e85a7a', // start.png, start event
		'document_event_step': '2472d854-be7a-4fde-81c0-023546bb24c6', // action.png, document event step
		'users_step': 'f55aec72-e30f-4356-8064-29422073c422', // question.png, users step
		'move_step': '3dd0492b-cb2f-487b-9eb2-a4101ac7b815', // move.png, move step
		'notification_step': 'f7a9f9fe-22c1-4018-a9b0-db962a064c03', // notify.png, notification step
		'archive_step': '8bcad626-0e58-42d5-b4c9-b21b950feb24', // archive.png, archive step
		'script_step': 'f411a05f-13ec-44ab-b884-98f1fa3eb9e7', // script step
		'gateway_step': '7a52c042-741e-4b5f-b508-dfd7773c4d93', // simple.png, simple step ###
		'stop_step': '5817238b-d7e9-4589-a14f-144fcbf550db', // stop.png, stop event

		// workflow transitions
		'automatic_transition': '6e8360e4-9ce0-43c1-a40e-29530ce2f2b1',
		'conditional_transition': '0cf18562-d589-46fd-804a-1b24cb019a06',
