ResourceManager.prototype.loadResources = function()
{
	var resourceCount = this.resourceList.length;

	var progress = new QProgressDialog(qsTr("Loading resources..."), qsTr("Abort"), 0, resourceCount, this);
	progress.minimumDuration = 0;
	progress.windowIcon = this.getFromCacheAsIcon('eof');
	progress.windowModality = Qt.WindowModal;
	progress.windowTitle = qsTr("Starting Workflow Designer");

	for (var i = 0; i < resourceCount; ++i) {
		progress.setValue(i);
		if (progress.wasCanceled)
			break;
		var guid = this.resourceList[i].trim();
		if (guid != "" && !this.resourceCached(guid))
			this.cacheResource(guid);
	}

	progress.setValue(resourceCount);
}

ResourceManager.prototype.deleteObsolete = function()
{
	this.settings.beginGroup(this.resourceCachePrefix);
	var keys = this.settings.allKeys();
	for (var i = 0; i < keys.length; ++i)
		if (this.resourceObsolete(keys[i]))
			this.settings.remove(keys[i]);
	this.settings.endGroup();
}

ResourceManager.prototype.resourceObsolete = function(resourceString)
{
	var result = guidRegex.exec(resourceString);
	if (result == undefined)
		return false;
	return this.resourceList.indexOf(result[1]) == -1 ? true : false;
}

ResourceManager.prototype.resourceCached = function(resourceString)
{
	var result = guidRegex.exec(resourceString);
	if (result == undefined)
		return false;
	return this.settings.value(this.resourceCachePrefix + result[1]) != undefined;
}

ResourceManager.prototype.cacheResource = function(resourceString)
{
	var result = guidRegex.exec(resourceString);
	if (result == undefined)
		return;
	var guid = result[1];
	var resource = vdom.application.GetResource(guid);
	this.settings.setValue(this.resourceCachePrefix + guid, resource.toByteArray());
}

ResourceManager.prototype.clearCache = function()
{
	this.settings.remove(this.resourceCachePrefix);
}

ResourceManager.prototype.getFromCache = function(resourceString)
{
	var guid = this.resources.hasOwnProperty(resourceString) ? this.resources[resourceString] : resourceString;
	var result = guidRegex.exec(guid);
	if (result == undefined)
		return false;
	return this.settings.value(this.resourceCachePrefix + result[1]);
}

ResourceManager.prototype.getFromCacheAsPixmap = function(guid)
{
	var pixmap = new QPixmap();
    pixmap.loadFromData(this.getFromCache(guid));
    return pixmap;
}

ResourceManager.prototype.getFromCacheAsIcon = function(guid)
{
	return new QIcon(this.getFromCacheAsPixmap(guid));
}

// some convinience funcs
ResourceManager.prototype.getStepIcon = function(stepId)
{
	return this.getFromCacheAsIcon(this.getStepIconResourceGuid(stepId));
}

ResourceManager.prototype.getStepPixmap = function(stepId)
{
	return this.getFromCacheAsPixmap(this.getStepIconResourceGuid(stepId));
}

ResourceManager.prototype.getStepIconResourceGuid = function(stepId)
{
	for (var step in this.steps) {
		if (step == stepId && this.steps.hasOwnProperty(step))
			return this.steps[step];
	}
}

ResourceManager.prototype.getTransitionIcon = function(transitionId)
{
	return this.getFromCacheAsIcon(this.getTransitionIconResourceGuid(transitionId));
}

ResourceManager.prototype.getTransitionPixmap = function(transitionId)
{
	return this.getFromCacheAsPixmap(this.getTransitionIconResourceGuid(transitionId));
}

ResourceManager.prototype.getTransitionIconResourceGuid = function(transitionId)
{
	for (var transition in this.transitions)
		if (transition == transitionId && this.transitions.hasOwnProperty(transition))
			return this.transitions[transition];
}

ResourceManager.prototype.getTranslation = function(translationFile)
{
	return this.resources.hasOwnProperty(translationFile) ? this.getFromCache(this.resources[translationFile]) : undefined;
}

function StepsListWidget(parent)
{
	QListWidget.call(this, parent);

	this.focusPolicy = Qt.NoFocus;
	this.frameShape = QFrame.NoFrame;

	this.dragEnabled = true;
	// maximum within all step icons
	this.iconSize = new QSize(35, 42);
	this.acceptDrops = true;
	this.showDropIndicator = true;

	this.spacing = 1;

	this.textElideMode = Qt.ElideMiddle;
	//this.verticalScrollBarPolicy = Qt.ScrollBarAlwaysOff;
    this.horizontalScrollBarPolicy = Qt.ScrollBarAlwaysOff;

	// remove selected item highlight
	this.styleSheet = "selection-background-color: transparent;"

	this.createSteps();
}

StepsListWidget.prototype = new QListWidget();

StepsListWidget.prototype.createSteps = function()
{
	this.clear();
	for (var i = WorkflowScene.StartStep; i <= WorkflowScene.StopStep; ++i)
		this.createStep(i);
	this.retranslate();
}

StepsListWidget.prototype.createStep = function(stepId)
{
	var item = new QListWidgetItem(resourceManager.getStepIcon(stepId), "", this);
	item.setData(Qt.UserRole, stepId);
	this.addItem(item);
}

StepsListWidget.prototype.startDrag = function(supportedActions)
{
	var item = this.currentItem();

	this.clearSelection();

	var stepId = item.data(Qt.UserRole);
	//var pixmap = item.icon().pixmap();
	var pixmap = resourceManager.getStepPixmap(stepId);

	var data = new QByteArray();

	var dataStream = new QDataStream(data, QIODevice.WriteOnly);
	dataStream.writeInt(stepId);

	var mimeData = new QMimeData;
	mimeData.setData("text/x-vdom-workflow-step", data);

	var drag = new QDrag(this);
    drag.setMimeData(mimeData);
    drag.setHotSpot(new QPoint(pixmap.width() / 2, pixmap.height() / 2));
    drag.setPixmap(pixmap);

	drag.exec(Qt.CopyAction);
}

StepsListWidget.prototype.mouseReleaseEvent = function(event)
{
	this.clearSelection();
	var eventMethod = StepsListWidget.prototype.mouseReleaseEvent;
    StepsListWidget.prototype.mouseReleaseEvent = undefined;
	QListView.prototype.mouseReleaseEvent.call(this, event);
    StepsListWidget.prototype.mouseReleaseEvent = eventMethod;
}

StepsListWidget.prototype.changeEvent = function(event)
{
	if (event.type() == QEvent.LanguageChange) {
		this.retranslate();
	} else {
		var eventMethod = StepsListWidget.prototype.changeEvent;
		StepsListWidget.prototype.changeEvent = undefined;
		QListWidget.prototype.changeEvent.call(this, event);
		StepsListWidget.prototype.changeEvent = eventMethod;
	}
}

StepsListWidget.prototype.retranslate = function()
{
	var stepLabels = new Array(qsTr("Start"), qsTr("Document Event"), qsTr("Users"), qsTr("Move"),
								qsTr("Notification"), qsTr("Archive"), qsTr("Script"), qsTr("Gateway"),
								qsTr("Stop"));
	for (var i = 0; i < this.count; ++i)
		this.item(i).setText(stepLabels[i]);
}

/* Augment function, improved. */
/* See Pro JavaScript Design Patterns, p.50 */

function augment(receivingClass, givingClass) {
	if(arguments[2]) { // Only give certain methods.
		for(var i = 2, len = arguments.length; i < len; i++) {
			receivingClass.prototype[arguments[i]] = givingClass.prototype[arguments[i]];
		}
	}
	else { // Give all methods.
		for(methodName in givingClass.prototype) {
			if(!receivingClass.prototype[methodName]) {
				receivingClass.prototype[methodName] = givingClass.prototype[methodName];
			}
		}
	}
}

function TranslationManager()
{
	this.translators = {};
	this.qtTranslators = {};
	this.currentTranslator = undefined;
	this.currentQtTranslator = undefined
	this.currentLanguage = "en";
}

TranslationManager.prototype.loadTranslations = function()
{
	this.loadTranslation("fr");
	this.loadTranslation("ru");
}

TranslationManager.prototype.loadTranslation = function(languageString)
{
	this.loadTranslationFromFile(this.translators, "workflow_" + languageString);
	this.loadTranslationFromFile(this.qtTranslators, "qt_" + languageString);
}

TranslationManager.prototype.loadTranslationFromFile = function(translatorsHash, translationFileName, force)
{
	var languageString = translationFileName.substring(translationFileName.length - 2, translationFileName.length);

	if (translatorsHash.hasOwnProperty(languageString) && !force)
		return false;

	var translationFile = new QTemporaryFile(QDir.tempPath() + "/" + translationFileName + ".qm" + ".XXXXXX");
	if (!translationFile.open())
		return false;

	var translationByteArray = resourceManager.getTranslation(translationFileName);
	if (translationByteArray == undefined)
		return false;

	translationFile.write(translationByteArray);
	translationFile.flush();

	var translator = new QTranslator();

	var loaded = translator.load(translationFile.fileName(), QDir.tempPath());
	if (loaded)
		translatorsHash[languageString] = translator;

	translationFile.close();
	translationFile.remove();

	return loaded;
}

TranslationManager.prototype.availableLanguages = function()
{
	var languages = new Array("en");
	for (var language in this.translators) {
		if (this.translators.hasOwnProperty(language))
			languages.push(language);
	}
	return languages;
}

TranslationManager.prototype.languageLabel = function(language)
{
	switch (language) {
		case "fr": return "FranÃ§aise";
		case "en": return "English";
		case "ru": return "Ð ÑÑÑÐºÐ¸Ð¹";
		default:   return undefined;
	}
}

TranslationManager.prototype.setLanguage = function(language)
{
	if (this.currentLanguage == language)
		return;

	if (this.currentTranslator != undefined)
		QCoreApplication.removeTranslator(this.currentTranslator);
	if (this.currentQtTranslator != undefined)
		QCoreApplication.removeTranslator(this.currentQtTranslator);

	// trying load translation
	//if (this.translators[language] == undefined)
	//	this.loadTranslation(language);

	this.currentLanguage = language;

	this.currentTranslator = this.translators[language];
	if (this.currentTranslator != undefined)
		QCoreApplication.installTranslator(this.currentTranslator);

	this.currentQtTranslator = this.qtTranslators[language];
	if (this.currentQtTranslator != undefined)
		QCoreApplication.installTranslator(this.currentQtTranslator);

//	var qtTranslator = new QTranslator();
//	qtTranslator.load("qt_" + language, QLibraryInfo.location(QLibraryInfo.TranslationsPath));
//	QCoreApplication.installTranslator(qtTranslator);
}

TranslationManager.prototype.language = function()
{
	return this.currentLanguage;
}

function WorkflowDesigner()
{
	QMainWindow.call(this);

	this.scene = new WorkflowScene();
	this.view = new WorkflowView();
	this.view.setScene(this.scene);

	this.undoStack = new QUndoStack;
	this.undoView = new QUndoView(this.undoStack);
	this.undoView.cleanIcon = resourceManager.getFromCacheAsIcon('ok');
	this.undoCommandFactory = new UndoCommandFactory();

	this.undoStack.cleanChanged.connect(this, this.documentWasModified);
	this.undoStack.indexChanged.connect(this.scene, this.scene.undoStackIndexChanged);
	this.undoStack.indexChanged.connect(this, this.updateSaveAction);

	this.scene.stepsMoved.connect(this, this.stepsMoved);
	//this.scene.selectionChanged.connect(this, this.sceneSelectionChanged);

	this.configDialog = new ConfigDialog();
	this.configDialog.configChanged.connect(this, this.applyConfig);
	this.configDialog.configChanged.connect(config, config.write);

	this.createActions();
	this.createMenus();
	this.createToolBars();

	this.createDockWidgets();

	//this.createConnections();

	this.tabWidget = new QTabWidget;

	var splitter = new QSplitter(Qt.Vertical);
	//splitter.childrenCollapsible = false;

	splitter.addWidget(this.view);
	splitter.addWidget(this.tabWidget);

	var hboxLayout = new QHBoxLayout;
	hboxLayout.setContentsMargins(0, 0, 0, 0);
	hboxLayout.addWidget(splitter, 1, 0);

	var widget = new QWidget;
	widget.setLayout(hboxLayout);

	this.setCentralWidget(widget);

	this.statusBar().showMessage(qsTr("Ready"));

	this.windowIcon = resourceManager.getFromCacheAsIcon('eof');
	this.unifiedTitleAndToolBarOnMac = true;

	this.resize(800, 600);

	/*
	//QApplication.setStyle("Plastique");
	var styleSheetFile = new QFile("C:/Dev/Qt/workflow/style.qss");
	var opened = styleSheetFile.open(QIODevice.ReadOnly);
	print("opened == " + opened);
	var stream = new QTextStream(styleSheetFile);
	var styleSheet = stream.readAll();
	print("styleSheet == " + styleSheet)
	qApp.styleSheet = styleSheet;
	*/

}

WorkflowDesigner.prototype = new QMainWindow();

WorkflowDesigner.prototype.prepare = function()
{
	this.restoreGeometry(config.geometry);
	this.restoreState(config.state);

	this.applyConfig();

	//this.scene.createUndoCommands();

	print("config.openLastWorkflow="+config.openLastWorkflow);

	this.scene.getBoxFolders();

	this.scene.removeUndoCommands();

	if (config.openLastWorkflow) {
		var result = guidRegex.exec(config.lastWorkflowGuid);
		if (result != undefined) {
			this.doOpen(result[1]);
		} else {
			this.activeWorkflow = "";
			this.scene.fillDefaultData();
		}
	} else {
		this.activeWorkflow = "";
		this.scene.fillDefaultData();
	}

	this.scene.activateWidgets(this.scene.widgets);
	this.scene.createUndoCommands();

	this.createConnections();

	this.updateSaveAction();
	this.updateEditActions();

	this.retranslate();
}

WorkflowDesigner.prototype.createActions = function()
{
	this.newAct = new QAction(resourceManager.getFromCacheAsIcon('file-new'), "", this);
	this.newAct.shortcut = new QKeySequence(QKeySequence.New); // qsTr("Ctrl+N")
	this.newAct.triggered.connect(this, this.newWorkflow);

	this.openAct = new QAction(resourceManager.getFromCacheAsIcon('file-open'), "", this);
	this.openAct.shortcut = new QKeySequence(QKeySequence.Open); // qsTr("Ctrl+O")
	this.openAct.triggered.connect(this, this.openWorkflow);

	this.importAct = new QAction("", this);
	this.importAct.shortcut = new QKeySequence(qsTr("Ctrl+I"));
	this.importAct.triggered.connect(this, this.importWorkflow);

	this.exportAct = new QAction("", this);
	this.exportAct.shortcut = new QKeySequence(qsTr("Ctrl+E"));
	this.exportAct.triggered.connect(this, this.exportWorkflow);

	this.saveAct = new QAction(resourceManager.getFromCacheAsIcon('file-save'), "", this);
	this.saveAct.shortcut = new QKeySequence(QKeySequence.Save); // qsTr("Ctrl+S")
	this.saveAct.triggered.connect(this, this.saveWorkflow);

	this.saveAsAct = new QAction(resourceManager.getFromCacheAsIcon('file-saveas'), "", this);
	this.saveAsAct.shortcut = new QKeySequence(qsTr("Ctrl+Shift+S"));
	this.saveAsAct.triggered.connect(this, this.saveWorkflowPreview);

	this.printAct = new QAction(resourceManager.getFromCacheAsIcon('file-print'), "", this);
	this.printAct.shortcut = new QKeySequence(QKeySequence.Print); // qsTr("Ctrl+P")
	this.printAct.triggered.connect(this, this.print);

	this.exitAct = new QAction(resourceManager.getFromCacheAsIcon('file-exit'), "", this);
	this.exitAct.shortcut = new QKeySequence(qsTr("Ctrl+Q"));
	this.exitAct.triggered.connect(qApp.closeAllWindows);

	this.undoAct = this.undoStack.createUndoAction(this);
	this.undoAct.icon = resourceManager.getFromCacheAsIcon('edit-undo');
    this.undoAct.shortcut = new QKeySequence(QKeySequence.Undo); // qsTr("Ctrl+Z")

	this.redoAct = this.undoStack.createRedoAction(this);
	this.redoAct.icon = resourceManager.getFromCacheAsIcon('edit-redo');
    this.redoAct.shortcut = new QKeySequence(QKeySequence.Redo); // qsTr("Ctrl+Z")

    this.cutAct = new QAction(resourceManager.getFromCacheAsIcon('edit-cut'), "", this);
	this.cutAct.shortcut = new QKeySequence(QKeySequence.Cut); // qsTr("Ctrl+X")
	this.cutAct.triggered.connect(this, this.cut);

	this.copyAct = new QAction(resourceManager.getFromCacheAsIcon('edit-copy'), "", this);
	this.copyAct.shortcut = new QKeySequence(QKeySequence.Copy); // qsTr("Ctrl+C")
	this.copyAct.triggered.connect(this, this.copy);

	this.pasteAct = new QAction(resourceManager.getFromCacheAsIcon('edit-paste'), "", this);
	this.pasteAct.shortcut = new QKeySequence(QKeySequence.Paste); // qsTr("Ctrl+V")
	this.pasteAct.triggered.connect(this, this.paste);

	this.deleteAct = new QAction(resourceManager.getFromCacheAsIcon('delete'), "", this);
	this.deleteAct.shortcut = new QKeySequence(QKeySequence.Delete);
	this.deleteAct.triggered.connect(this, this.deleteItem);

	this.selectAllAct = new QAction(this);
	this.selectAllAct.shortcut = new QKeySequence(QKeySequence.SelectAll);
	this.selectAllAct.triggered.connect(this, this.selectAll);

	this.prevUnfinishedAct = new QAction(resourceManager.getFromCacheAsIcon('edit-prev'), "", this);
	this.prevUnfinishedAct.shortcut = new QKeySequence(qsTr("Ctrl+K"));
	this.prevUnfinishedAct.triggered.connect(this, this.prevUnfinished);

	this.nextUnfinishedAct = new QAction(resourceManager.getFromCacheAsIcon('edit-next'), "", this);
	this.nextUnfinishedAct.shortcut = new QKeySequence(qsTr("Ctrl+L"));
	this.nextUnfinishedAct.triggered.connect(this, this.nextUnfinished);

	this.settingsAct = new QAction(resourceManager.getFromCacheAsIcon('tools-settings'), "", this);
	this.settingsAct.triggered.connect(this.configDialog, this.configDialog.exec);

	this.aboutAct = new QAction(this);
	this.aboutAct.triggered.connect(this, this.about);

	this.aboutQtAct = new QAction(this);
	this.aboutQtAct.triggered.connect(qApp.aboutQt);

	this.languagesGroup = new QActionGroup(this);
	this.languagesGroup.exclusive = true;
	this.languagesGroup["triggered(QAction*)"].connect(this, this.languageChanged);

	this.languageActs = {};
	var availableLanguages = translationManager.availableLanguages();
	for (var i = 0; i < availableLanguages.length; ++i) {
		var language = availableLanguages[i];
		this.languageActs[language] = new QAction(resourceManager.getFromCacheAsIcon('flag_' + language),
												  translationManager.languageLabel(language), this);
		this.languageActs[language].checkable = true;
		if (language == translationManager.language())
			this.languageActs[language].checked = true;
		this.languageActs[language].setData(language);
		this.languagesGroup.addAction(this.languageActs[language]);
	}
}

WorkflowDesigner.prototype.createMenus = function()
{
	this.fileMenu = this.menuBar().addMenu("");

	this.fileMenu.addAction(this.newAct)
	this.fileMenu.addAction(this.openAct);
	this.fileMenu.addAction(this.importAct);
	this.fileMenu.addAction(this.exportAct);
	this.fileMenu.addSeparator();
	this.fileMenu.addAction(this.saveAct);
	this.fileMenu.addAction(this.saveAsAct);
	this.fileMenu.addSeparator();
	this.fileMenu.addAction(this.printAct);
	this.fileMenu.addSeparator();
	this.fileMenu.addAction(this.exitAct);

	this.editMenu = this.menuBar().addMenu("");
	this.editMenu.addAction(this.undoAct);
	this.editMenu.addAction(this.redoAct);
	this.editMenu.addSeparator();
	this.editMenu.addAction(this.cutAct);
	this.editMenu.addAction(this.copyAct);
	this.editMenu.addAction(this.pasteAct);
	this.editMenu.addAction(this.deleteAct);
	this.editMenu.addSeparator();
	this.editMenu.addAction(this.selectAllAct);
	this.editMenu.addSeparator();
	this.editMenu.addAction(this.prevUnfinishedAct);
	this.editMenu.addAction(this.nextUnfinishedAct);

	this.viewMenu = this.menuBar().addMenu("");
	this.languageMenu = this.viewMenu.addMenu("");

	for (var language in this.languageActs) {
		if (this.languageActs.hasOwnProperty(language))
			this.languageMenu.addAction(this.languageActs[language]);
	}

	this.toolsMenu = this.menuBar().addMenu("");

	this.menuBar().addSeparator();
	this.toolsMenu.addAction(this.settingsAct);

	this.helpMenu = this.menuBar().addMenu("");
	this.helpMenu.addAction(this.aboutAct);
	this.helpMenu.addAction(this.aboutQtAct);
}

WorkflowDesigner.prototype.createToolBars = function()
{
	this.fileToolBar = this.addToolBar("");
	this.fileToolBar.objectName = "fileToolBar";

	this.fileToolBar.addAction(this.newAct);
	this.fileToolBar.addAction(this.openAct);
	this.fileToolBar.addAction(this.saveAct);
	this.fileToolBar.addAction(this.saveAsAct);

	this.editToolBar = this.addToolBar("");
	this.editToolBar.objectName = "editToolBar";

	this.editToolBar.addAction(this.undoAct);
	this.editToolBar.addAction(this.redoAct);
	this.editToolBar.addSeparator();
	this.editToolBar.addAction(this.cutAct);
	this.editToolBar.addAction(this.copyAct);
	this.editToolBar.addAction(this.pasteAct);
	this.editToolBar.addAction(this.deleteAct);
	this.editToolBar.addSeparator();
	this.editToolBar.addAction(this.prevUnfinishedAct);
	this.editToolBar.addAction(this.nextUnfinishedAct);

	this.toolsToolBar = this.addToolBar("");
	this.toolsToolBar.objectName = "toolsToolBar";

	this.toolsToolBar.addAction(this.settingsAct);
}

WorkflowDesigner.prototype.createDockWidgets = function()
{
	this.stepsDockWidget = new QDockWidget("", this);
	this.stepsDockWidget.objectName = "stepsDockWidget";

	this.stepsListWidget = new StepsListWidget(this.stepsDockWidget);
	this.stepsDockWidget.setWidget(this.stepsListWidget);
	this.addDockWidget(Qt.LeftDockWidgetArea, this.stepsDockWidget);

	this.undoDockWidget = new QDockWidget("", this);
	this.undoDockWidget.objectName = "undoDockWidget";
	this.undoDockWidget.setWidget(this.undoView);
	this.addDockWidget(Qt.LeftDockWidgetArea, this.undoDockWidget);
}

WorkflowDesigner.prototype.cut = function()
{
	print("cut()");
	if (this.scene.selectedSteps.length > 0) {
		this.copy();
		var deleteCommand = new DeleteCommand();
		this.undoStack.push(deleteCommand);
	}
}

WorkflowDesigner.prototype.copy = function()
{
	print("copy()");
	if (this.scene.selectedSteps.length > 0)
		QApplication.clipboard().setText(this.scene.selectionToXml());
}

WorkflowDesigner.prototype.paste = function()
{
	print("paste()");

	// fast dirty validity check
	var contents = QApplication.clipboard().text();
	var stream = new QXmlStreamReader(contents);
	if (!stream.readNextStartElement() || stream.name() != Tag.graph)
		return;
	if (!stream.readNextStartElement() || stream.name() != Tag.steps)
		return;
	stream.skipCurrentElement();
	if (!stream.readNextStartElement() || stream.name() != Tag.transitions)
		return;
	stream.clear();

	print("valid clipboard xml");

	var pasteCommand = new PasteCommand(contents);
	this.undoStack.push(pasteCommand);
}

WorkflowDesigner.prototype.deleteItem = function()
{
	if (this.scene.selectedItems().length == 0)
		return;

	var deleteCommand = new DeleteCommand();
	this.undoStack.push(deleteCommand);
	//workflowDesigner.scene.clearSelection();
	//insertCommand.step.setSelected(true);
}

