{
	QMessageBox.information(0, qsTr("Not implemented"), qsTr("This is not yet implemented"));
}

WorkflowDesigner.prototype.createConnections = function()
{
	this.scene.generalWidget.nameLineEdit.textChanged.connect(this, this.updateWindowTitle);
}

WorkflowDesigner.prototype.updateWindowTitle = function(workflowName)
{
	//print("WorkflowDesigner.prototype.updateWindowTitle");
	this.windowTitle = qsTr("%1[*] [%2] - Workflow Designer").arg(workflowName).arg(vdom.service.Host);
}

WorkflowDesigner.prototype.documentWasModified = function(clean)
{
	//print("WorkflowDesigner.prototype.documentWasModified()");
	this.windowModified = !clean;
}

WorkflowDesigner.prototype.stepsMoved = function(selectedSteps, oldPositions)
{
	//print("WorkflowDesigner.prototype.slotStepsMoved");
	this.lastUndoCommand = new MoveCommand(selectedSteps, oldPositions);
	this.undoStack.push(this.lastUndoCommand);
}

WorkflowDesigner.prototype.updateSaveAction = function()
{
	this.saveAct.enabled = !this.undoStack.isClean();
}

WorkflowDesigner.prototype.updateEditActions = function()
{
	var cutCopy = this.scene.selectedSteps.length > 0;
	var del = this.scene.selectedSteps.length > 0 || this.scene.selectedTransitions.length > 0;
	this.cutAct.enabled = cutCopy;
	this.copyAct.enabled = cutCopy;
	this.deleteAct.enabled = del;
}

WorkflowDesigner.prototype.newWorkflow = function()
{
	if (this.maybeSave()) {
		this.scene.clearAll();
		this.scene.fillDefaultData();
		this.undoStack.clear();
		this.activeWorkflow = "";
		this.windowModified = false;
	}
}

WorkflowDesigner.prototype.openWorkflow = function()
{
	if (this.maybeSave()) {

		var dialog = form.load("widgets.workflowdialog", this);
		if (!dialog)
			return;
		dialog.windowIcon = resourceManager.getFromCacheAsIcon('file-open');
		dialog.windowTitle = qsTr("Open Workflow");
		dialog.label.text = qsTr("Select workflow to open:");
		dialog.acceptPushButton.text = qsTr("&Open");
		dialog.acceptPushButton.enabled = false;
		dialog.workflowsTreeWidget["currentItemChanged(QTreeWidgetItem*, QTreeWidgetItem*)"].connect(function() { dialog.acceptPushButton.enabled = true; });

		var response = remoteCaller.call("get_workflow_list");

		print("response = " + response);

		if (remoteCaller.hasError()) {
			QMessageBox.warning(this, qsTr("Failed to get workflow list"), remoteCaller.errorString());
			return;
		}

		this.populateWorkflows(response, dialog);

		if (dialog.exec()) {
			var item = dialog.workflowsTreeWidget.currentItem();
			if (item)
				this.doOpen(item.data(0, Qt.UserRole));
		}
	}
}

WorkflowDesigner.prototype.populateWorkflows = function(xml, dialog)
{
	var stream = new QXmlStreamReader(xml);
	if (!stream.readNextStartElement() || stream.name() != Tag.success)
		return;
	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.workflow) {
			var name = stream.attributes().value(Tag.aname);
			var label = stream.attributes().value(Tag.label);
			var result = guidRegex.exec(stream.attributes().value(Tag.guid));
			if (result == undefined)
				continue;
			var guid = result[1];
			var item = new QTreeWidgetItem(dialog.workflowsTreeWidget);
			item.setText(0, "%1 [%2]".arg(label).arg(name));
			item.setIcon(0, resourceManager.getFromCacheAsIcon('eof'));
			item.setData(0, Qt.UserRole, guid);
			dialog.workflowsTreeWidget.addTopLevelItem(item);
			stream.skipCurrentElement();
		} else {
			stream.skipCurrentElement();
		}
	}
}

/* Open workflow from box */

WorkflowDesigner.prototype.doOpen = function(guid)
{
	var response = remoteCaller.call("get_workflow", "<input_data><guid>%1</guid></input_data>".arg(guid));

	if (remoteCaller.hasError()) {
		QMessageBox.warning(this, qsTr("Failed to get workflow %1").arg(guid), remoteCaller.errorString());
		return;
	}

	this.undoStack.clear();
	this.activeWorkflow = guid;

	//print("response="+response);

	var stream = new QXmlStreamReader(response);
	stream.readNextStartElement(); // Tag.success

	this.doLoad(stream);
}

/* Load workflow into workflow designer and fill scene */

WorkflowDesigner.prototype.doLoad = function(stream)
{
	print("doLoad()");

	QApplication.setOverrideCursor(new QCursor(Qt.WaitCursor));

	this.scene.read(stream);

	QApplication.restoreOverrideCursor();

	if (stream.hasError()) {
		QMessageBox.warning(this, qsTr("Workflow Designer"),
							qsTr("Error parsing workflow file at line %1: %2.").arg(stream.lineNumber()).arg(stream.errorString()));
		return;
	}

	this.windowModified = false;
	this.updateWindowTitle(this.scene.name());
	this.statusBar().showMessage(qsTr("Workflow loaded"), 2000);
}

/* Import workflow from file and install to box */

WorkflowDesigner.prototype.importWorkflow = function()
{
	if (this.maybeSave()) {
		if (!QFile.exists(config.lastWorkflowLocation))
			config.lastWorkflowLocation = QDir.homePath();

		var fileName = QFileDialog.getOpenFileName(this, qsTr("Import Workflow"),
												   config.lastWorkflowLocation, qsTr("Workflow Files (*.xml)"));

		if (fileName == "")
			return;

		var fileInfo = new QFileInfo(fileName);
		config.lastWorkflowLocation = fileInfo.absolutePath();

		if (this.doImportFromFile(fileName) && this.importedWorkflowString != undefined) {
			this.scene.getBoxFolders();
			var stream = new QXmlStreamReader(this.importedWorkflowString);
			this.doLoad(stream);
			this.importedWorkflowString = undefined;
		}
	}
}

/* Import helper */

WorkflowDesigner.prototype.doImportFromFile = function(fileName)
{
	var file = new QFile(fileName);
	if (!file.open(QIODevice.OpenMode(QIODevice.ReadOnly, QIODevice.Text))) {
		QMessageBox.warning(this, qsTr("Workflow Designer"),
							qsTr("Cannot read file %1:\n%2.").arg(fileName).arg(file.errorString()));
		return false;
	}

	var stream = new QTextStream(file);
	stream.setCodec("utf-8");
	var contents = stream.readAll();
	file.close();

	/*
	// workaround
	var result = /(<workflow\s[\s\S]*>[\s\S]*<\/workflow>)/.exec(contents);
	if (result == undefined) {
		return;
	}
	var workflowString = result[1];
	*/

	var rx = new QRegExp("<\\?xml.*>.*(<workflow\\s.*>.*<\\/workflow>)");
	while (rx.indexIn(contents) == -1) {
		QMessageBox.warning(this, qsTr("Failed to import workflow"), qsTr("Invalid xml: workflow tag not found"));
		return false;
	}

	var workflowString = rx.cap(1);

	var response = remoteCaller.call("create_workflow",
									 "<input_data><install>true</install>%1<image>null</image></input_data>"
									 .arg(workflowString));

	if (remoteCaller.hasError()) {
		QMessageBox.warning(this, qsTr("Failed to import workflow"), remoteCaller.errorString());
		return false;
	}

	var stream = new QXmlStreamReader(response);
	stream.readNextStartElement(); // Tag.success
	if (!stream.readNextStartElement() || stream.name() != Tag.guid) // Tag.guid
		return false;

	var result = guidRegex.exec(stream.readElementText());
	if (result == undefined)
		return false;

	this.activeWorkflow = result[1];

	this.importedWorkflowString = workflowString;
	//this.windowModified = false;
	//this.updateWindowTitle(this.scene.name());
	this.statusBar().showMessage(qsTr("Workflow imported"), 2000);
	return true;
}

/* Export workflow to file */

WorkflowDesigner.prototype.exportWorkflow = function()
{
	if (!this.canSave())
		return false;

	if (!QFile.exists(config.lastWorkflowLocation))
		config.lastWorkflowLocation = QDir.homePath();

	var fileName = QFileDialog.getSaveFileName(this, qsTr("Export Workflow"),
											   config.lastWorkflowLocation, qsTr("Workflow Files (*.xml)"));

	if (fileName == "")
		return false;

	if (fileName.indexOf(".xml", fileName.length - 4) == -1)
		fileName += ".xml";

	var fileInfo = new QFileInfo(fileName);
	config.lastWorkflowLocation = fileInfo.absolutePath();

	return this.doExportToFile(fileName);
}

/* Export helper */

WorkflowDesigner.prototype.doExportToFile = function(fileName)
{
	var file = new QFile(fileName);
	if (!file.open(QIODevice.OpenMode(QIODevice.WriteOnly, QIODevice.Text))) {
		QMessageBox.warning(this, qsTr("Workflow Designer"),
							qsTr("Cannot write file %1:\n%2.").arg(fileName).arg(file.errorString()));
		return false;
	}

	QApplication.setOverrideCursor(new QCursor(Qt.WaitCursor));

	var stream = new QXmlStreamWriter(file);
	stream.setAutoFormatting(true);
	stream.setAutoFormattingIndent(4);
	stream.writeStartDocument();
	stream.writeDTD("<!DOCTYPE workflow>");
	this.scene.write(stream);
	stream.writeEndDocument();
	file.close();

	QApplication.restoreOverrideCursor();

	this.windowModified = false;
	this.statusBar().showMessage(qsTr("Workflow exported"), 2000);
	return true;
}

/* Save action handler */

WorkflowDesigner.prototype.saveWorkflow = function()
{
	if (!this.canSave())
		return false;

	/*
	if (this.scene.hasUnfinishedItems()) {
		QMessageBox.warning(this, qsTr("Unfinished items in workflow"),
								  qsTr("Workflow will be saved, but can be non operational " +
									   "because of unfinished items marked with warning sign."));
		return false;
	}
	*/

	var success;
	if (this.activeWorkflow == undefined || this.activeWorkflow == "")
		success = this.doCreate();
	else
		success = this.doSave(this.activeWorkflow);
	if (success)
		this.undoStack.setClean();
	return success;
}

/* Create new workflow on box */

WorkflowDesigner.prototype.doCreate = function()
{
	var response = remoteCaller.call("create_workflow",
									 "<input_data><install>false</install><compilable>%1</compilable>%2<image>%3</image></input_data>"
									 .arg(this.scene.isCompilable().toString())
									 .arg(this.scene.toXml())
									 .arg(this.scene.toPixmap().toBase64()));

	print("wf xml = " + this.scene.toXml());
	print("response = " + response);

	if (remoteCaller.hasError()) {
		QMessageBox.warning(this, qsTr("Failed to create workflow"), remoteCaller.errorString());
		return false;
	}

	var stream = new QXmlStreamReader(response);
	stream.readNextStartElement(); // Tag.success
	if (!stream.readNextStartElement() || stream.name() != Tag.guid) // Tag.guid
		return false;

	var result = guidRegex.exec(stream.readElementText());
	if (result == undefined)
		return false;

	this.activeWorkflow = result[1];

	//this.windowModified = false;
	//this.updateWindowTitle(this.scene.name());
	this.statusBar().showMessage(qsTr("Workflow created"), 2000);
	return true;
}

/* Modify existing workflow on box */

WorkflowDesigner.prototype.doSave = function(workflowGuid)
{
	var response = remoteCaller.call("modify_workflow",
									 "<input_data><guid>%1</guid><compilable>%2</compilable>%3<image>%4</image></input_data>"
									 .arg(workflowGuid)
									 .arg(this.scene.isCompilable().toString())
									 .arg(this.scene.toXml())
									 .arg(this.scene.toPixmap().toBase64()));

	if (remoteCaller.hasError()) {
		QMessageBox.warning(this, qsTr("Failed to modify workflow"), remoteCaller.errorString());
		return false;
	}

	//this.windowModified = false;
	//this.updateWindowTitle(this.scene.name());
	this.statusBar().showMessage(qsTr("Workflow saved"), 2000);
	return true;
}

/* Save workflow graphics scene preview as png image */

WorkflowDesigner.prototype.saveWorkflowPreview = function()
{
	if (!QFile.exists(config.lastWorkflowLocation))
		config.lastWorkflowLocation = QDir.homePath();

	var fileName = QFileDialog.getSaveFileName(this, qsTr("Save Workflow Preview"),
											   config.lastWorkflowLocation, qsTr("Images (*.png)"));

	if (fileName == "")
		return;

	var fileInfo = new QFileInfo(fileName);
	config.lastWorkflowLocation = fileInfo.absolutePath();

	var pixmap = this.scene.toPixmap();
	var saved = pixmap.save(fileName);
	if (saved)
		this.statusBar().showMessage(qsTr("Workflow preview saved to %1".arg(fileName)), 2000);

	return saved;
}

WorkflowDesigner.prototype.closeEvent = function(event)
{
	if (this.maybeSave()) {
		config.write();
		event.accept();
	} else {
		event.ignore();
	}
}

WorkflowDesigner.prototype.maybeSave = function()
{
	if (this.windowModified) {
		var ret = QMessageBox.warning(this, qsTr("Workflow Designer"),
									  qsTr("The workflow has been modified.\nDo you want to save your changes?"),
									  QMessageBox.StandardButtons(QMessageBox.Save, QMessageBox.Discard, QMessageBox.Cancel));
		if (ret == QMessageBox.Save)
			return this.saveWorkflow();
		else if (ret == QMessageBox.Cancel)
			return false;
	}
	return true;
}

WorkflowDesigner.prototype.canSave = function()
{
	if (this.scene.hasInvalidItems()) {
		QMessageBox.critical(this, qsTr("Cannot save invalid workflow"),
							 qsTr("There are some invalid items in workflow marked with red cross. " +
								  "You must fix them first to save the workflow."));
		return false;
	}

	return true;
}

WorkflowDesigner.prototype.print = function()
{
	//var printer = new QPrinter(QPrinter.HighResolution);
	var printDialog = new QPrintDialog(/*printer*/);
	printDialog["accepted(QPrinter*)"].connect(this, this.printDialogAccepted);
	printDialog.exec();
}

// TODO: fix this
WorkflowDesigner.prototype.printDialogAccepted = function(printer)
{
	//printer.setPaperSize(QPrinter.A4);
	var painter = new QPainter();
	painter.begin(printer);
	painter.setRenderHint(QPainter.Antialiasing);
	this.scene.render(painter, 0, this.scene.itemsBoundingRect());
	painter.end();
}

