WorkflowDesigner.prototype.changeEvent = function(event)
{
	// hack
	// events delivered twice because of WorkflowDesigner.prototype = new QMainWindow();
	if (!this.visible)
		return;

	//print("WorkflowDesigner.prototype.changeEvent type = " + event.type());

	if (event.type() == QEvent.LanguageChange) {
		//print("retranslate()");
		this.retranslate();
		this.scene.retranslate();
	} else {
		var eventMethod = WorkflowDesigner.prototype.changeEvent;
		WorkflowDesigner.prototype.changeEvent = undefined;
		QMainWindow.prototype.changeEvent.call(this, event);
		WorkflowDesigner.prototype.changeEvent = eventMethod;
	}
}

