function WorkflowItemCommon()
{
}

augment(WorkflowItemCommon, LogWidgetHandler);

WorkflowItemCommon.prototype.nameValid = function()
{
	var name = this.generalWidget.nameLineEdit.text.trim();
	var empty = name == "";
	if (empty) {
		this.generalWidget.nameLineEdit.toolTip = "";
		return false;
	}
	var valid = nameRegex.test(name);
	if (!valid) {
		this.generalWidget.nameLineEdit.toolTip = qsTr("Should contain only alphanumeric and '_', and start with letter or '_'");
		return false;
	}
	var unique = !workflowDesigner.scene.workflowItemNameExists(name, this.data(WorkflowScene.ItemId));
	if (!unique) {
		this.generalWidget.nameLineEdit.toolTip = qsTr("Name is already in use");
		return false;
	}
	return true;
}

WorkflowItemCommon.prototype.isValid = function()
{
	return this.nameValid();
}

WorkflowItemCommon.prototype.isFinished = function()
{
	return this.allMandatoryDataFilled();
}

WorkflowItemCommon.prototype.isStep = function()
{
	return this.data(WorkflowScene.ItemType) == WorkflowScene.Step;
}

WorkflowItemCommon.prototype.isTransition = function()
{
	return this.data(WorkflowScene.ItemType) == WorkflowScene.Transition;
}

WorkflowItemCommon.prototype.id = function()
{
	return this.data(WorkflowScene.ItemId);
}

WorkflowItemCommon.prototype.type = function()
{
	return this.data(WorkflowScene.ItemType);
}

WorkflowItemCommon.prototype.subType = function()
{
	return this.data(WorkflowScene.ItemSubType);
}

WorkflowItemCommon.prototype.updateStatus = function()
{
	if (!this.nameValid())
		this.setStatus(WorkflowScene.Invalid);
	else if (this.allMandatoryDataFilled())
		this.setStatus(WorkflowScene.Valid);
	else
		this.setStatus(WorkflowScene.Unfinished);
}

WorkflowItemCommon.prototype.setStatus = function(status)
{
	switch (status) {
		case WorkflowScene.Invalid:
			this.setStatusPixmap(resourceManager.getFromCacheAsPixmap('delete').scaledToHeight(12, Qt.SmoothTransformation));
			if (!this.statusPixmapItem.isVisible())
				this.statusPixmapItem.show();
			this.adjustStatusPixmap();
			break;
		case WorkflowScene.Unfinished:
			this.setStatusPixmap(resourceManager.getFromCacheAsPixmap('warning').scaledToHeight(12, Qt.SmoothTransformation));
			if (!this.statusPixmapItem.isVisible())
				this.statusPixmapItem.show();
			this.adjustStatusPixmap();
			break;
		case WorkflowScene.Valid:
		default:
			this.setStatusPixmap(new QPixmap());
			if (this.statusPixmapItem.isVisible())
				this.statusPixmapItem.hide();
			break;
	}
}

WorkflowItemCommon.prototype.retranslate = function()
{
	for (var i = 0; i < this.widgets.length; ++i)
		this.widgets[i].retranslateUi();
}

function WorkflowScene(parent)
{
	QGraphicsScene.call(this);

	//this.acceptDrops = true;
	//this.dragEnabled = true;

	//this.itemIndexMethod = QGraphicsScene.NoIndex;

	this.setSceneRect(0, 0, 5000, 5000);

	//this.backgroundBrush = new QBrush(resourceManager.getFromCacheAsPixmap('grid').scaled(17, 17));

	//this.backgroundBrush = new QBrush(new QColor(255, 255, 255));

	// obsolete
	this.stepInserted = signal();
	// obsolete
	this.transitionInserted = signal();

	this.stepsMoved = signal();

	this.behaviorMode = WorkflowScene.SelectItemMode;
	this.previousBehaviorMode = this.behaviorMode;

	this.stepId = -1;
	this.transitionId = -1;

	//this.selectionChanged.connect(this, this.handleSelectionChanged);

	this.undoCommandsCreated = false;

	this.workflowItems = new Array();

	this.factory = new WorkflowItemFactory(this);

	this.widgets = new Array();

	this._currentId = -1;

	this.createWidgets();
	this.createActions();
	this.createConnections();
	this.createContextMenus();

	this.selectedSteps = new Array();
	this.oldPositions = new Array();

	this.selectedTransitions = new Array();

	this.retranslate();
	//this.clearSelection();
	//this.activateWidgets(this.widgets);
}

WorkflowScene.SelectItemMode = 0;
WorkflowScene.InsertTransitionMode = 1;

WorkflowScene.Step = 0;
WorkflowScene.Transition = 1;

WorkflowScene.StartStep = 0;
WorkflowScene.DocumentEventStep = 1;
WorkflowScene.UsersStep = 2;
WorkflowScene.MoveStep = 3;
WorkflowScene.NotificationStep = 4;
WorkflowScene.ArchiveStep = 5;
WorkflowScene.ScriptStep = 6;
WorkflowScene.GatewayStep = 7;
WorkflowScene.StopStep = 8;

WorkflowScene.AutomaticTransition = 10;
WorkflowScene.ConditionalTransition = 11;
WorkflowScene.TimeoutTransition = 12;
WorkflowScene.ErrorTransition = 13;

WorkflowScene.ItemId = 0;
WorkflowScene.ItemType = 1;
WorkflowScene.ItemSubType = 2;

WorkflowScene.Invalid = 0;
WorkflowScene.Unfinished = 1;
WorkflowScene.Valid = 2;

WorkflowScene.cellWidth = 17; // pixels

WorkflowScene.prototype = new QGraphicsScene();

augment(WorkflowScene, WorkflowCommon);
augment(WorkflowScene, LabelWidgetHandler);

WorkflowScene.prototype.handleSelectionChanged = function()
{
	//print("WorkflowScene.prototype.handleSelectionChanged()");

	var selectedItems = this.selectedItems();

	this.selectedSteps = new Array();
	this.oldPositions = new Array();

	this.selectedTransitions = new Array();

	//print("handleSelectionChanged() 0");

	if (selectedItems.length > 0) {
		// filter out steps
		for (var i = 0; i < selectedItems.length; ++i) {
			//print("i="+i);
			var item = this.workflowItem(selectedItems[i]);
			if (item != undefined) {
				//print("valid item");
				if (item.isStep()) {
					//print("step");
					this.selectedSteps.push(item);
					this.oldPositions.push(item.pos());
					//print("step ok");
				} else if (item.isTransition()) {
					//print("transition");
					this.selectedTransitions.push(item);
					//print("transition ok");
				}
			}
		}
	}

	//print("handleSelectionChanged() 1");

	if (selectedItems.length == 0) {
		this.activateWidgets(this.widgets);
		this.readWidgetValues();
		//this.lastActiveItem = undefined;
	} else {
		var item = selectedItems[0].parentItem() != null ? selectedItems[0].parentItem() : selectedItems[0];
		var workflowItem = this.workflowItem(item);
		// accepting only workflow items
		if (workflowItem != undefined/* && workflowItem.data(WorkflowScene.ItemId) != this.lastActiveItem*/) {
			this.activateWidgets(workflowItem.widgets);
			workflowItem.readWidgetValues();
			this.lastActiveItem = workflowItem.data(WorkflowScene.ItemId);
		}
	}

	//print("handleSelectionChanged() 2");

	workflowDesigner.updateEditActions();

	//print("handleSelectionChanged() 3");
}

WorkflowScene.prototype.contextMenuEvent = function(event)
{
	var scenePos = event.scenePos();
	var itemAtScenePos = this.itemAt(scenePos);
	if (itemAtScenePos != null) {
		// get parent if there is one
		if (itemAtScenePos.parentItem())
			itemAtScenePos = itemAtScenePos.parentItem();

		this.startItem = this.workflowItem(itemAtScenePos);

		var screenPos = event.screenPos();
		var itemType = itemAtScenePos.data(WorkflowScene.ItemType);
		if (itemType == WorkflowScene.Step) {
			var itemSubType = itemAtScenePos.data(WorkflowScene.ItemSubType);
			if (itemSubType == WorkflowScene.StartStep) {
				this.startContextMenu.popup(screenPos);
			} else if (itemSubType == WorkflowScene.StopStep) {
				// do nothing
			} else {
				this.contextMenu.popup(screenPos)
			}
		}
	}

	var eventMethod = WorkflowScene.prototype.contextMenuEvent;
    WorkflowScene.prototype.contextMenuEvent = undefined;
	QGraphicsScene.prototype.contextMenuEvent.call(this, event);
    WorkflowScene.prototype.contextMenuEvent = eventMethod;
}

WorkflowScene.prototype.mousePressEvent = function(event)
{
	//print("WorkflowScene.prototype.mousePressEvent() event = " + event);
	//print("WorkflowScene.prototype.mousePressEvent() this.selectedItems().length = " + this.selectedItems().length);

	if (event.button() == Qt.LeftButton) {
		var itemAtScenePos = this.itemAt(event.scenePos());
		if (itemAtScenePos != null) {

			var item = itemAtScenePos;
			if (itemAtScenePos.parentItem() != null)
				item = itemAtScenePos.parentItem();

			// TODO: ###
			if (!item.isSelected()) {
				if (!(event.modifiers() & Qt.ControlModifier))
					this.clearSelection();
				item.setSelected(true);
			} else {
				if (event.modifiers() & Qt.ControlModifier)
					item.setSelected(false);
			}

			if (this.behaviorMode == WorkflowScene.InsertTransitionMode) {
				var endItem = this.workflowItem(item);

				if (this.startItem.data(WorkflowScene.ItemId) != endItem.data(WorkflowScene.ItemId) // deny self transitions
					&& this.startItem.data(WorkflowScene.ItemSubType) != WorkflowScene.StopStep // deny transitions from stop
					&& endItem.data(WorkflowScene.ItemSubType) != WorkflowScene.StartStep) // deny transitions to start
				{
					var insertCommand = new InsertTransitionCommand(this.transitionId, this.startItem, endItem);
					workflowDesigner.undoStack.push(insertCommand);
					this.clearSelection();
					insertCommand.transition.setSelected(true);
				}
				this.transitionId = -1;
			}

			// roll back to previous behavior mode
			this.behaviorMode = WorkflowScene.SelectItemMode;
		}
	}

	var eventMethod = WorkflowScene.prototype.mousePressEvent;
    WorkflowScene.prototype.mousePressEvent = undefined;
	QGraphicsScene.prototype.mousePressEvent.call(this, event);
    WorkflowScene.prototype.mousePressEvent = eventMethod;
}

WorkflowScene.prototype.mouseMoveEvent = function(event)
{
	//print("WorkflowScene.prototype.mouseMoveEvent() event = " + event);
	//print("WorkflowScene.prototype.mouseMoveEvent() this.selectedSteps = " + this.selectedSteps);
	//print("WorkflowScene.prototype.mouseMoveEvent() this.selectedSteps.length = " + this.selectedSteps.length);

	if (this.selectedSteps.length > 0 && this.selectedStepsMoved()) {
		// update transitions
		for (var i = 0; i < this.selectedSteps.length; ++i)
			this.selectedSteps[i].updateTransitions();
		//this.update(); ### causes serious slow down
	}

	var eventMethod = WorkflowScene.prototype.mouseMoveEvent;
    WorkflowScene.prototype.mouseMoveEvent = undefined;
	QGraphicsScene.prototype.mouseMoveEvent.call(this, event);
    WorkflowScene.prototype.mouseMoveEvent = eventMethod;
}

WorkflowScene.prototype.mouseReleaseEvent = function(event)
{
	//print("WorkflowScene.prototype.mouseReleaseEvent() event = " + event);
	//print("WorkflowScene.prototype.mouseReleaseEvent() this.selectedSteps = " + this.selectedSteps);
	//print("WorkflowScene.prototype.mouseReleaseEvent() this.selectedSteps.length = " + this.selectedSteps.length);

	if (event.button() == Qt.LeftButton
		&& this.selectedSteps.length > 0
		&& this.selectedSteps.length == this.oldPositions.length) {

		// snap to grid
		if (config.snapToGrid) {
			for (var i = 0; i < this.selectedSteps.length; ++i)
				this.selectedSteps[i].adjustPosition();
			this.update();
		}

		if (this.selectedStepsMoved())
			this.stepsMoved.emit(this.selectedSteps, this.oldPositions);

		//this.selectedSteps = null;
		//this.oldPositions = null;
	}

	var eventMethod = WorkflowScene.prototype.mouseReleaseEvent;
    WorkflowScene.prototype.mouseReleaseEvent = undefined;
	QGraphicsScene.prototype.mouseReleaseEvent.call(this, event);
    WorkflowScene.prototype.mouseReleaseEvent = eventMethod;
}

WorkflowScene.prototype.selectedStepsMoved = function()
{
	if (this.selectedSteps.length == 0)
		return false;

	// check if at least one item has changed position
	for (var i = 0; i < this.selectedSteps.length; ++i)
		if (this.oldPositions[i] != this.selectedSteps[i].pos())
			return true;

	return false;
}

WorkflowScene.prototype.addWorkflowItem = function(workflowItem) {
	this.workflowItems.push(workflowItem);
	this.addItem(workflowItem);
}

WorkflowScene.prototype.removeWorkflowItem = function(workflowItem) {
	print("WorkflowScene.prototype.removeWorkflowItem()");

	var item = this.workflowItem(workflowItem);
	if (item != undefined) {
		// remove from WorkflowScene.workfrolItems
		var index = this.workflowItems.indexOf(item);
		this.workflowItems.splice(index, 1);
		// finish
		item.isTransition() ? this.removeTransition(item) : this.removeStep(item);
	}
}

WorkflowScene.prototype.removeTransition = function(transition) {
	// remove transition from scene
	this.removeItem(transition);
	// remove references in corresponding steps
	transition.startItem.removeTransition(transition);
	transition.endItem.removeTransition(transition);
}

WorkflowScene.prototype.removeStep = function(step) {
	// remove step from scene
	this.removeItem(step);
	// remove transitions references belonging to step
	while (step.transitions.length > 0)
		this.removeTransition(step.transitions[0]);
}

WorkflowScene.prototype.clearAll = function()
{
	this.clearSelection();
	this._currentId = -1;
	this.workflowItems = [];
	this.selectedSteps = [];
	this.oldPositions = [];
	this.selectedTransitions = [];
	this.clear(); // QGraphicsScene.clear();
}

// get workflow item by graphics item
WorkflowScene.prototype.workflowItem = function(item) {
	//print("WorkflowScene.prototype.workflowItem()");
	//print("item="+item);
	//print("ok 1");
	var _id = item.data(WorkflowScene.ItemId);
	//print("_id="+_id);
	return this.workflowItemById(_id);
}

// get workflow item by id
WorkflowScene.prototype.workflowItemById = function(id) {
	if (id == undefined)
		return undefined;
	//print("WorkflowScene.prototype.workflowItemById() id="+id);
	//print("this.workflowItems.length="+this.workflowItems.length);
	for (var i = 0; i < this.workflowItems.length; ++i) {
		//print("i="+i);
		//print("this.workflowItems[i].data(WorkflowScene.ItemId)="+this.workflowItems[i].data(WorkflowScene.ItemId));
		//print("this.workflowItems[i].name()="+this.workflowItems[i].name());
		if (this.workflowItems[i].data(WorkflowScene.ItemId) == id) {
			//print("matched, return");
			return this.workflowItems[i];
		}
		//print("i="+i+" done");
		//print("!this.workflowItems.length="+this.workflowItems.length);
	}
	//print("return undefined");
	return undefined;
}

WorkflowScene.prototype.workflowItemByName = function(name) {
	for (var i = 0; i < this.workflowItems.length; ++i)
		if (this.workflowItems[i].name() == name)
			return this.workflowItems[i];
	return undefined;
}

WorkflowScene.prototype.workflowItemIndexById = function(id) {
	for (var i = 0; i < this.workflowItems.length; ++i) {
		if (this.workflowItems[i].data(WorkflowScene.ItemId) == id)
			return i;
	}
	return undefined;
}

WorkflowScene.prototype.workflowItemNameExists = function(name, excludeId) {
	// check workflow name
	if (/* hack */ excludeId != undefined && this.name() == name)
		return true;

	for (var i = 0; i < this.workflowItems.length; ++i) {
		if (this.workflowItems[i].data(WorkflowScene.ItemId) == excludeId)
			continue;
		// check next workflow item name
		if (this.workflowItems[i].name() == name)
			return true;
	}
	return false;
}

// obsolete?
WorkflowScene.prototype.valid = function()
{
	// check workflow name alphanum
	if (!nameRegex.test(this.name())) {
		this.clearSelection();
		return false;
	}

	for (var i = 0; i < this.workflowItems.length; ++i) {
		var item = this.workflowItems[i];
		var name = item.name();
		var id = item.data(WorkflowScene.ItemId);

		// check workflow item name alphanum
		if (!nameRegex.test(name)) {
			this.clearSelection();
			item.setSelected(true);
			return false;
		}
		// check workflow item name unicity
		for (var j = 0; j < this.workflowItems.length; ++j) {
			if (this.workflowItems[j].data(WorkflowScene.ItemId) == id)
				continue;
			if (this.workflowItems[j].name() == name) {
				this.clearSelection();
				this.workflowItems[j].setSelected(true);
				return false;
			}
		}
	}
	return true;
}

WorkflowScene.prototype.hasInvalidItems = function()
{
	if (!this.nameValid())
		return true;

	for (var i = 0; i < this.workflowItems.length; ++i) {
		if (!this.workflowItems[i].isValid())
			return true;
	}

	return false;
}

WorkflowScene.prototype.hasUnfinishedItems = function()
{
	for (var i = 0; i < this.workflowItems.length; ++i) {
		if (!this.workflowItems[i].isFinished())
			return true;
	}
	return false;
}

WorkflowScene.prototype.hasStartStep = function()
{
	for (var i = 0; i < this.workflowItems.length; ++i)
		if (this.workflowItems[i].subType() == WorkflowScene.StartStep)
			return true;
	return false;
}

WorkflowScene.prototype.hasStartStepAndConnectedDocumentEvent = function()
{
	for (var i = 0; i < this.workflowItems.length; ++i) {
		var transition = this.workflowItems[i];
		if (transition.subType() == WorkflowScene.AutomaticTransition
			&& transition.startItem.subType() == WorkflowScene.StartStep
			&& transition.endItem.subType() == WorkflowScene.DocumentEventStep)
			return true;
	}
	return false;
}

WorkflowScene.prototype.isCompilable = function()
{
	return this.isFinished();
}

WorkflowScene.prototype.isFinished = function()
{
	return !this.hasUnfinishedItems() && this.hasStartStepAndConnectedDocumentEvent();
}

WorkflowScene.prototype.selectAll = function()
{
	this.clearSelection();
	workflowDesigner.scene.removeSelectionChangedConnection();
	for (var i = 0; i < this.workflowItems.length; ++i)
		this.workflowItems[i].setSelected(true);
	workflowDesigner.scene.createSelectionChangedConnection();
	this.handleSelectionChanged();
}

WorkflowScene.prototype.nextUnfinished = function(backwards)
{
	if (backwards == undefined)
		backwards = false;

	var index = undefined;
	if (this.lastActiveItem != undefined)
		index = this.workflowItemIndexById(this.lastActiveItem);

	var first = 0;
	var last = this.workflowItems.length - 1;

	if (index == undefined)
		index = backwards ? last : first;

	var limit, jump, delta;
	if (backwards) {
		limit = first;
		jump = last;
		delta = -1;
	} else {
		limit = last;
		jump = first;
		delta = 1;
	}

	var i = index != limit ? index + delta : jump;

	while (i != index) {
		if (!this.workflowItems[i].allMandatoryDataFilled()) {
			this.clearSelection();
			this.workflowItems[i].setSelected(true);
			return;
		}
		i == limit ? i = jump : i = i + delta;
	}

}

WorkflowScene.prototype.createWidgets = function()
{
	this.widgets.push(this.generalWidget = form.load("widgets.workflow.general", this));
	this.widgets.push(this.folderWidget = form.load("widgets.workflow.folder", this));

	// create model
	this.model = new QStandardItemModel(0, 1);
	this.model.setHeaderData(0, Qt.Horizontal, qsTr("Folder"), Qt.DisplayRole);

	// setup views
	this.folderWidget.topLevelComboBox.setModel(this.model);
	this.folderWidget.foldersListView.setModel(this.model);

	this.folderIcon = resourceManager.getFromCacheAsIcon('folder');
}

WorkflowScene.prototype.fillDefaultData = function()
{
	this.generalWidget.nameLineEdit.text = "NewWorkflow";
	this.checkName(this.generalWidget.nameLineEdit.text);
	this.generalWidget.labelLineEdit.text = qsTr("New Workflow");
	this.generalWidget.descriptionPlainTextEdit.plainText = "";

	this.generalWidget.timeoutCheckBox.checked = true;
	this.generalWidget.daysSpinBox.value = 0;
	this.generalWidget.hoursSpinBox.value = 0;
	this.generalWidget.minutesSpinBox.value = 0;
	this.generalWidget.secondsSpinBox.value = 0;

	this.folderWidget.createFolderCheckBox = true;
}

WorkflowScene.prototype.refreshBoxFolders = function()
{
	var topLevelFolder = this.folderWidget.topLevelComboBox.currentText;
	var checkedFolders = new Array();
	var rootIndex = this.folderWidget.foldersListView.rootIndex();
	var rowCount = this.model.rowCount(rootIndex);
	for (var i = 0; i < rowCount; ++i) {
		var index = this.model.index(i, 0, rootIndex);
		var item = this.model.itemFromIndex(index);
		if (item && item.checkState() == Qt.Checked)
			checkedFolders.push(item.text());
	}

	this.getBoxFolders();

	this.removeUndoCommands();
	var index = this.folderWidget.topLevelComboBox.findText(topLevelFolder);
	if (index != -1 && checkedFolders.length > 0) {
		this.folderWidget.currentIndex = index;
		var rootIndex = this.folderWidget.foldersListView.rootIndex();
		var rowCount = this.model.rowCount(rootIndex);
		for (var i = 0; i < rowCount; ++i) {
			var index = this.model.index(i, 0, rootIndex);
			var item = this.model.itemFromIndex(index);
			if (item) {
				for (var j = 0; j < checkedFolders.length; ++j) {
					if (item.text() == checkedFolders[j]) {
						item.setCheckState(Qt.Checked);
						checkedFolders.splice(j, 1);
						break;
					}
				}

			}
			if (checkedFolders.length == 0)
				break;
		}
	}
	this.createUndoCommands();
}

WorkflowScene.prototype.getBoxFolders = function()
{
	this.removeUndoCommands();

	// do not use clear() as it clears header
	this.model.removeRows(0, this.model.rowCount());

	var response = remoteCaller.call("get_folders");

	if (remoteCaller.hasError()) {
		QMessageBox.warning(this, qsTr("Workflow Designer"),
							qsTr("Failed to get file server's folder list: %1").arg(remoteCaller.errorString()));
		return;
	}

	var stream = new QXmlStreamReader(response);
	stream.readNextStartElement(); // Tag.success

	while (stream.readNextStartElement()) {
        if (stream.name() == Tag.folder)
            this.readBoxFolder(stream, this.model);
        else
            stream.skipCurrentElement();
    }

	this.createUndoCommands();
}

WorkflowScene.prototype.readBoxFolder = function(stream, item)
{
	// create item in tree

	var name = stream.attributes().value(Tag.aname);
	var guid = stream.attributes().value(Tag.guid);

	var childItem = new QStandardItem(name);
	childItem.setIcon(this.folderIcon);
	childItem.setEditable(false);
	if (item instanceof QStandardItem) {
		childItem.setCheckable(true);
		childItem.setCheckState(Qt.Unchecked);
	}
	childItem.setData(guid);
	item.appendRow(childItem);

	while (stream.readNextStartElement()) {
        if (stream.name() == Tag.folder)
            this.readBoxFolder(stream, childItem);
        else
            stream.skipCurrentElement();
	}

}

WorkflowScene.prototype.topLevelFolderChanged = function(index)
{
	if (index == -1)
		return;

	var modelIndex = this.model.index(index, 0);
	var item = this.model.itemFromIndex(modelIndex);
	if (item)
		this.folderWidget.foldersListView.setRootIndex(modelIndex);
}

WorkflowScene.prototype.createActions = function()
{
	this.automaticTransitionAct = new QAction(resourceManager.getTransitionIcon(WorkflowScene.AutomaticTransition), "", this);
	this.automaticTransitionAct.triggered.connect(this, function() { this.switchToInsertTransitionMode(WorkflowScene.AutomaticTransition); });

	this.conditionalTransitionAct = new QAction(resourceManager.getTransitionIcon(WorkflowScene.ConditionalTransition), "", this);
	this.conditionalTransitionAct.triggered.connect(this, function() { this.switchToInsertTransitionMode(WorkflowScene.ConditionalTransition); });

	this.timeoutTransitionAct = new QAction(resourceManager.getTransitionIcon(WorkflowScene.TimeoutTransition), "", this);
	this.timeoutTransitionAct.triggered.connect(this, function() { this.switchToInsertTransitionMode(WorkflowScene.TimeoutTransition); });

	this.errorTransitionAct = new QAction(resourceManager.getTransitionIcon(WorkflowScene.ErrorTransition), "", this);
	this.errorTransitionAct.triggered.connect(this, function() { this.switchToInsertTransitionMode(WorkflowScene.ErrorTransition); });

	this.refreshBoxFoldersAct = new QAction(qsTr("&Refresh"), this);
	this.refreshBoxFoldersAct.toolTip = qsTr("Refresh folders list");
	this.refreshBoxFoldersAct.icon = resourceManager.getFromCacheAsIcon('refresh');
	this.folderWidget.refreshToolButton.setDefaultAction(this.refreshBoxFoldersAct);
}

WorkflowScene.prototype.createConnections = function()
{
	this.createNameConnections();
	//this.createDescriptionConnections();

	this.folderWidget.topLevelComboBox["currentIndexChanged(int)"].connect(this, this.topLevelFolderChanged);

	this.refreshBoxFoldersAct.triggered.connect(this, this.refreshBoxFolders);

	this.createSelectionChangedConnection();
}

WorkflowScene.prototype.removeConnections = function()
{
	this.removeNameConnections();
	//this.removeDescriptionConnections();

	this.folderWidget.topLevelComboBox["currentIndexChanged(int)"].disconnect(this, this.topLevelFolderChanged);

	this.refreshBoxFoldersAct.triggered.disconnect(this, this.refreshBoxFolders);

	this.removeSelectionChangedConnection();
}

WorkflowScene.prototype.createSelectionChangedConnection = function()
{
	this.selectionChanged.connect(this, this.handleSelectionChanged);
}

WorkflowScene.prototype.removeSelectionChangedConnection = function()
{
	this.selectionChanged.disconnect(this, this.handleSelectionChanged);
}

WorkflowScene.prototype.createUndoCommands = function()
{
	if (this.undoCommandsCreated)
		return;

	var gw = this.generalWidget;
	var fw = this.folderWidget;
	var n = this.name();

	this.nameSlot = workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	this.createLabelUndoConnections();
	this.descriptionSlot = workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(gw.descriptionPlainTextEdit, gw.descriptionLabel, n);
	this.timeoutSlot = workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.timeoutCheckBox, n);
	this.daysSlot = workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(gw.daysSpinBox, gw.timeoutAfterLabel, n);
	this.hoursSlot = workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(gw.hoursSpinBox, gw.timeoutAfterLabel, n);
	this.minutesSlot = workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(gw.minutesSpinBox, gw.timeoutAfterLabel, n);
	this.secondsSlot = workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(gw.secondsSpinBox, gw.timeoutAfterLabel, n);

	this.topLevelSlot = workflowDesigner.undoCommandFactory.createChangeComboBoxCommand(fw.topLevelComboBox, fw.topLevelLabel, n);
	this.createFolderSlot = workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(fw.createFolderCheckBox, n);
	this.modelSlot = workflowDesigner.undoCommandFactory.createChangeAbstractItemViewCommand(fw.foldersListView, n);

	this.undoCommandsCreated = true;
}

WorkflowScene.prototype.removeUndoCommands = function()
{
	if (!this.undoCommandsCreated)
		return;

	var gw = this.generalWidget;
	var fw = this.folderWidget;

	if (this.nameSlot)
		gw.nameLineEdit.textChanged.disconnect(this.nameSlot);
	this.removeLabelUndoConnections();
	if (this.descriptionSlot)
		gw.descriptionPlainTextEdit.textChanged.disconnect(this.descriptionSlot);
	if (this.timeoutSlot)
		gw.timeoutCheckBox.stateChanged.disconnect(this.timeoutSlot);
	if (this.daysSlot)
		gw.daysSpinBox["valueChanged(int)"].disconnect(this.daysSlot);
	if (this.hoursSlot)
		gw.hoursSpinBox["valueChanged(int)"].disconnect(this.hoursSlot);
	if (this.minutesSlot)
		gw.minutesSpinBox["valueChanged(int)"].disconnect(this.minutesSlot);
	if (this.secondsSlot)
		gw.secondsSpinBox["valueChanged(int)"].disconnect(this.secondsSlot);

	if (this.topLevelSlot)
		fw.topLevelComboBox["currentIndexChanged(int)"].disconnect(this.topLevelSlot);
	if (this.createFolderSlot)
		fw.createFolderCheckBox.stateChanged.disconnect(this.createFolderSlot);
	if (this.modelSlot)
		this.model.dataChanged.disconnect(this.modelSlot);

	this.undoCommandsCreated = false;
}

WorkflowScene.prototype.readWidgetValues = function()
{
	this.generalWidget.nameLineEdit.oldText = this.generalWidget.nameLineEdit.text;
	this.generalWidget.labelLineEdit.oldText = this.generalWidget.labelLineEdit.text;
	this.generalWidget.descriptionPlainTextEdit.oldPlainText = this.generalWidget.descriptionPlainTextEdit.plainText;
	this.generalWidget.timeoutCheckBox.oldChecked = this.generalWidget.timeoutCheckBox.checked;
	this.generalWidget.daysSpinBox.oldValue = this.generalWidget.daysSpinBox.value;
	this.generalWidget.hoursSpinBox.oldValue = this.generalWidget.hoursSpinBox.value;
	this.generalWidget.minutesSpinBox.oldValue = this.generalWidget.minutesSpinBox.value;
	this.generalWidget.secondsSpinBox.oldValue = this.generalWidget.secondsSpinBox.value;

	this.folderWidget.topLevelComboBox.prevIndex = this.folderWidget.topLevelComboBox.currentIndex;
	this.folderWidget.createFolderCheckBox.oldChecked = this.folderWidget.createFolderCheckBox.checked;
}

WorkflowScene.prototype.createContextMenus = function()
{
	this.contextMenu = new QMenu("", this);
	this.contextMenu.addAction(this.automaticTransitionAct);
	this.contextMenu.addAction(this.conditionalTransitionAct);
	this.contextMenu.addAction(this.timeoutTransitionAct);
	this.contextMenu.addAction(this.errorTransitionAct);

	this.startContextMenu = new QMenu("", this);
	this.startContextMenu.addAction(this.automaticTransitionAct);
}

WorkflowScene.prototype.activateWidgets = function(widgetsArray)
{
	if (!Array.isArray(widgetsArray))
		return;

	workflowDesigner.tabWidget.clear();
	for (var i = 0; i < widgetsArray.length; ++i)
		workflowDesigner.tabWidget.addTab(widgetsArray[i], widgetsArray[i].windowTitle);
}

WorkflowScene.prototype.switchToInsertTransitionMode = function(transitionId) {
	this.previousBehaviorMode = this.behaviorMode;
	this.behaviorMode = WorkflowScene.InsertTransitionMode;
	this.transitionId = transitionId;
}

// mandatory

WorkflowScene.prototype.nameValid = function()
{
	var name = this.generalWidget.nameLineEdit.text.trim();
	var empty = name == "";
	if (empty) {
		this.generalWidget.nameLineEdit.toolTip = "";
		return false;
	}
	var valid = nameRegex.test(name);
	if (!valid) {
		this.generalWidget.nameLineEdit.toolTip = qsTr("Should contain only alphanumeric and '_', and start with letter");
		return false;
	}
	var unique = !this.workflowItemNameExists(name);
	if (!unique) {
		this.generalWidget.nameLineEdit.toolTip = qsTr("Name is already in use");
		return false;
	}
	return true;
}

WorkflowScene.prototype.updateStatus = function()
{
	if (!this.nameValid())
		workflowDesigner.windowIcon = resourceManager.getFromCacheAsIcon('delete');
	else
		workflowDesigner.windowIcon = resourceManager.getFromCacheAsIcon('eof');
}

WorkflowScene.prototype.allMandatoryDataFilled = function()
{
	return this.nameValid();
}

// serializing

WorkflowScene.prototype.write = function(stream)
{
	var gw = this.generalWidget;
	var fw = this.folderWidget;

	// workflow
	stream.writeStartElement(Tag.workflow);
	stream.writeAttribute(Tag.aname, gw.nameLineEdit.text);
	stream.writeAttribute(Tag.label, gw.labelLineEdit.text);
	var timeout = gw.timeoutCheckBox.checked;
	stream.writeAttribute(Tag.timeout, timeout);
	var timeoutDelay = timeout ? XmlWriter.formattedTime(gw.daysSpinBox, gw.hoursSpinBox, gw.minutesSpinBox, gw.secondsSpinBox) : "00:00:00:00";
	stream.writeAttribute(Tag.timeoutDelay, timeoutDelay);
	stream.writeAttribute(Tag.version, "1.0");

	// take writeDescription from common XmlWriter
	// description
	stream.writeStartElement(Tag.description);
	stream.writeCDATA(gw.descriptionPlainTextEdit.plainText);
	stream.writeEndElement(); // Tag.description

	this.writeFolders(stream);

	stream.writeStartElement(Tag.graph);

	this.writeSteps(stream);
	this.writeTransitions(stream);

	stream.writeEndElement(); // Tag.graph

	stream.writeEndElement(); // Tag.workflow
}

WorkflowScene.prototype.writeFolders = function(stream)
{
	stream.writeStartElement(Tag.folders);
	stream.writeAttribute(Tag.topLevel, this.folderWidget.topLevelComboBox.currentText);
	stream.writeAttribute(Tag.createFolder, this.folderWidget.createFolderCheckBox.checked);

	var rootIndex = this.folderWidget.foldersListView.rootIndex();
	var rowCount = this.model.rowCount(rootIndex);
	for (var i = 0; i < rowCount; ++i) {
		var index = this.model.index(i, 0, rootIndex);
		var item = this.model.itemFromIndex(index);
		if (item && item.checkState() == Qt.Checked) {
			stream.writeStartElement(Tag.folder);
			stream.writeAttribute(Tag.aname, item.text());
			stream.writeEndElement(); // Tag.folder
		}
	}

	stream.writeEndElement(); // Tag.folders
}

WorkflowScene.prototype.writeSteps = function(stream)
{
	stream.writeStartElement(Tag.steps);

	for (var i = 0; i < this.workflowItems.length; ++i) {
		if (this.workflowItems[i].isStep())
			this.workflowItems[i].write(stream);
	}

	stream.writeEndElement(); // Tag.steps
}

WorkflowScene.prototype.writeTransitions = function(stream)
{
	stream.writeStartElement(Tag.transitions);

	for (var i = 0; i < this.workflowItems.length; ++i) {
		if (this.workflowItems[i].isTransition())
			this.workflowItems[i].write(stream);
	}

	stream.writeEndElement(); // Tag.transitions
}

WorkflowScene.prototype.writeSelected = function(stream)
{
	if (this.selectedSteps.length == 0)
		return;

	stream.writeStartElement(Tag.graph);

	this.writeSelectedSteps(stream);
	this.writeSelectedTransitions(stream);

	stream.writeEndElement(); // Tag.graph
}

WorkflowScene.prototype.writeSelectedSteps = function(stream)
{
	stream.writeStartElement(Tag.steps);

	for (var i = 0; i < this.selectedSteps.length; ++i) {
		if (this.selectedSteps[i].isStep())
			this.selectedSteps[i].write(stream);
	}

	stream.writeEndElement(); // Tag.steps
}

WorkflowScene.prototype.writeSelectedTransitions = function(stream)
{
	stream.writeStartElement(Tag.transitions);

	for (var i = 0; i < this.workflowItems.length; ++i) {
		var item = this.workflowItems[i];
		if (item.isTransition()
			&& this.selectedSteps.indexOf(item.startItem) != -1
			&& this.selectedSteps.indexOf(item.endItem) != -1)
			item.write(stream);
	}

	stream.writeEndElement(); // Tag.transitions
}

WorkflowScene.prototype.read = function(stream)
{
	if (!stream.readNextStartElement() || stream.name() != Tag.workflow)
		return;

	this.removeConnections();

	this.removeUndoCommands();

	this.clearAll();

	var gw = this.generalWidget;
	var fw = this.folderWidget;

	gw.nameLineEdit.text = stream.attributes().value(Tag.aname);
	gw.labelLineEdit.text = stream.attributes().value(Tag.label);

	var timeout = stream.attributes().value(Tag.timeout);
	gw.timeoutCheckBox.checked = timeout;
	if (timeout)
		XmlReader.readTimeout(stream.attributes().value(Tag.timeoutDelay), gw.daysSpinBox, gw.hoursSpinBox, gw.minutesSpinBox, gw.secondsSpinBox);

	var version = stream.attributes().value(Tag.version);

	while (stream.readNextStartElement()) {
        if (stream.name() == Tag.description)
            this.readDescription(stream);
        else if (stream.name() == Tag.folders)
            this.readFolders(stream);
        else if (stream.name() == Tag.graph)
            this.readGraph(stream);
        else
            stream.skipCurrentElement();
    }

	this.createConnections();

	this.createUndoCommands();

	this.clearSelection();
}

WorkflowScene.prototype.readDescription = function(stream)
{
	this.generalWidget.descriptionPlainTextEdit.plainText = stream.readElementText();
}

WorkflowScene.prototype.readFolders = function(stream)
{
	var topLevel = stream.attributes().value(Tag.topLevel);
	if (topLevel != "") {
		var index = this.folderWidget.topLevelComboBox.findText(topLevel);
		if (index != -1) {
			this.folderWidget.topLevelComboBox.currentIndex = index;
			this.topLevelFolderChanged(index); // cuz signals disconnected
		}
	}

	this.folderWidget.createFolderCheckBox.checked = stream.attributes().value(Tag.createFolder);

	// uncheck all folders
	var rootIndex = this.folderWidget.foldersListView.rootIndex();
	var rowCount = this.model.rowCount(rootIndex);
	for (var row = 0; row < rowCount; ++row) {
		var index = this.model.index(row, 0, rootIndex);
		var item = this.model.itemFromIndex(index);
		if (item)
			item.setCheckState(Qt.Unchecked);
	}

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.folder) {
			this.readFolder(stream);
		} else {
            stream.skipCurrentElement();
		}
    }
}

WorkflowScene.prototype.readFolder = function(stream)
{
	var folderName = stream.attributes().value(Tag.aname);
	if (folderName == "") {
		stream.skipCurrentElement();
		return;
	}
	var rootIndex = this.folderWidget.foldersListView.rootIndex();
	var rowCount = this.model.rowCount(rootIndex);
	for (var row = 0; row < rowCount; ++row) {
		var index = this.model.index(row, 0, rootIndex);
		var item = this.model.itemFromIndex(index);
		if (item && item.text() == folderName) {
			item.setCheckState(Qt.Checked);
			break;
		}
	}
	stream.skipCurrentElement();
}

WorkflowScene.prototype.readGraph = function(stream)
{
	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.steps)
			this.readSteps(stream);
		else if (stream.name() == Tag.transitions)
			this.readTransitions(stream);
		else
            stream.skipCurrentElement();
    }
}

WorkflowScene.prototype.readSteps = function(stream)
{
	//print("WorkflowScene.prototype.readSteps()");
	while (stream.readNextStartElement()) {
		var step = undefined;
		var stepType = undefined;
		if (stream.name() == Tag.startStep) {
			step = new StartStep(this);
			stepType = WorkflowScene.StartStep;
		} else if (stream.name() == Tag.documentEventStep) {
			step = new DocumentEventStep(this);
			stepType = WorkflowScene.DocumentEventStep;
		} else if (stream.name() == Tag.usersStep) {
			step = new UsersStep(this);
			stepType = WorkflowScene.UsersStep;
		} else if (stream.name() == Tag.moveStep) {
			step = new MoveStep(this);
			stepType = WorkflowScene.MoveStep;
		} else if (stream.name() == Tag.notificationStep) {
			step = new NotificationStep(this);
			stepType = WorkflowScene.NotificationStep;
		} else if (stream.name() == Tag.archiveStep) {
			step = new ArchiveStep(this);
			stepType = WorkflowScene.ArchiveStep;
		} else if (stream.name() == Tag.scriptStep) {
			step = new ScriptStep(this);
			stepType = WorkflowScene.ScriptStep;
		} else if (stream.name() == Tag.gatewayStep) {
			step = new GatewayStep(this);
			stepType = WorkflowScene.GatewayStep;
		} else if (stream.name() == Tag.stopStep) {
			step = new StopStep(this);
			stepType = WorkflowScene.StopStep;
		} else {
            stream.skipCurrentElement();
		}
		if (step != undefined) {

			step.setData(WorkflowScene.ItemType, WorkflowScene.Step);
			step.setData(WorkflowScene.ItemSubType, stepType);

			// generate id
			step.setData(WorkflowScene.ItemId, ++this._currentId);

			//step.setPos(scenePos);
			//step.adjustPosition();

			step.read(stream);

			this.addWorkflowItem(step);

			step.createUndoCommands();
		}
    }
}

WorkflowScene.prototype.readTransitions = function(stream)
{
	//print("WorkflowScene.prototype.readTransitions()");
	while (stream.readNextStartElement()) {

		var transition = undefined;
		var transitionType = undefined;
		if (stream.name() == Tag.automaticTransition) {
			// source and target item are yet unknown and will be set later
			transition = new AutomaticTransition(undefined, undefined, this);
			transitionType = WorkflowScene.AutomaticTransition;
		} else if (stream.name() == Tag.conditionalTransition) {
			transition = new ConditionalTransition(undefined, undefined, this);
			transitionType = WorkflowScene.ConditionalTransition;
		} else if (stream.name() == Tag.timeoutTransition) {
			transition = new TimeoutTransition(undefined, undefined, this);
			transitionType = WorkflowScene.timeoutTransition;
		} else if (stream.name() == Tag.errorTransition) {
			transition = new ErrorTransition(undefined, undefined, this);
			transitionType = WorkflowScene.ErrorTransition;
		} else {
            stream.skipCurrentElement();
		}

		if (transition != undefined) {
			// set transition type id
			transition.setData(WorkflowScene.ItemType, WorkflowScene.Transition);
			transition.setData(WorkflowScene.ItemSubType, transitionType);
			// generate id
			transition.setData(WorkflowScene.ItemId, ++this._currentId);

			transition.read(stream);

			if (transition._startItemName == undefined || transition._startItemName == "" || transition._endItemName == undefined || transition._endItemName == "")
				continue;

			var startItem = this.workflowItemByName(transition._startItemName);
			var endItem = this.workflowItemByName(transition._endItemName);

			if (startItem == undefined || endItem == undefined)
				continue;

			// transition
			transition.setStartItem(startItem);
			transition.setEndItem(endItem);

			// add transition to steps
			startItem.addTransition(transition);
			endItem.addTransition(transition);

			// add to scene
			this.addWorkflowItem(transition);

			// refresh positioning
			transition.updatePosition();

			transition.createUndoCommands();
		}
    }
}

WorkflowScene.prototype.retranslate = function()
{
	// actions
	this.automaticTransitionAct.text = qsTr("&Automatic");
	this.conditionalTransitionAct.text = qsTr("&Conditional");
	this.timeoutTransitionAct.text = qsTr("&Timeout");
	this.errorTransitionAct.text = qsTr("&Error");

	// context menus
	this.contextMenu.text = qsTr("&Transition");
	this.startContextMenu.text = qsTr("&Transition");

	// retranslate tab titles when all changeEvents are proceeded
	var singleShot = new QTimer();
    singleShot.singleShot = true;
    singleShot.timeout.connect(this, this.retranslateTabText);
    singleShot.start();
}

WorkflowScene.prototype.retranslateTabText = function()
{
	for (var i = 0; i < workflowDesigner.tabWidget.count; ++i) {
		var windowTitle = workflowDesigner.tabWidget.widget(i).windowTitle;
		workflowDesigner.tabWidget.setTabText(i, windowTitle);
	}
}

WorkflowScene.prototype.toXml = function()
{
	var contents = new QByteArray();
	var stream = new QXmlStreamWriter(contents);
	stream.setAutoFormatting(true);
	stream.setAutoFormattingIndent(4);
	this.write(stream);
	var textStream = new QTextStream(contents);
	textStream.setCodec("utf-8");
	return textStream.readAll();
}

WorkflowScene.prototype.selectionToXml = function()
{
	var contents = new QByteArray();
	var stream = new QXmlStreamWriter(contents);
	stream.setAutoFormatting(true);
	stream.setAutoFormattingIndent(4);
	this.writeSelected(stream);
	var textStream = new QTextStream(contents);
	textStream.setCodec("utf-8");
	return textStream.readAll();
}

WorkflowScene.prototype.workflowBoundingRect = function()
{
	if (this.workflowItems.length == 0)
		return new QRect(0, 0, 0, 0);

	var r = this.workflowItems[0].mapRectToScene(this.workflowItems[0].boundingRect());
	var left = r.left(), right = r.right(), top = r.top(), bottom = r.bottom();

	for (var i = 1; i < this.workflowItems.length; ++i) {
		var r = this.workflowItems[i].mapRectToScene(this.workflowItems[i].boundingRect());
		if (r.left() < left)
			left = r.left();
		if (r.right() > right)
			right = r.right();
		if (r.top() < top)
			top = r.top();
		if (r.bottom() > bottom)
			bottom = r.bottom();
	}

	return new QRectF(left, top, right - left, bottom - top);
}

WorkflowScene.prototype.previewPixmap = function()
{
	// Sometimes itemsBoundingRect() returns QRectF() with NaN width and height
	// This, for example, occures when some big workflow is loaded using openAct
	// and can't be entirely displayed in viewport
	// So, using own function and iterating only workflow items
	var extra = WorkflowScene.cellWidth / 2;
	var rect = this.workflowBoundingRect().adjusted(-extra, -extra, extra, extra);

	/*
	var image = new QImage(rect.width(), rect.height(), QImage.Format_ARGB32_Premultiplied);
	//image.fill(Qt.transparent);
	var painter = new QPainter();
	var begin = painter.begin(image);
	painter.setRenderHint(QPainter.Antialiasing);
	this.workflow.scene.render(painter, null, rect);
	painter.end();
	*/

	var pixmap = new QPixmap(rect.width(), rect.height());
	//pixmap.fill(Qt.transparent);
	var painter = new QPainter();
	var begin = painter.begin(pixmap);
	painter.setRenderHint(QPainter.Antialiasing);
	this.render(painter, null, rect);
	painter.end();
	return pixmap;
}

WorkflowScene.prototype.toPixmap = function()
{
	return this.previewPixmap();
}

WorkflowScene.prototype.undoStackIndexChanged = function()
{
	for (var i = 0; i < this.workflowItems.length; ++i) {
		if (this.workflowItems[i].isTransition())
			this.workflowItems[i].updatePosition();
	}
	if (this.lastActiveItem != undefined) {
		var item = this.workflowItemById(this.lastActiveItem);
		if (item != undefined && (typeof item.mandatoryWidgetChanged == 'function'))
			item.mandatoryWidgetsChanged();
	} else {
		// workflow.mandatoryWidgetsChanged()
	}
}

function IntermediateStep()
{
}

augment(IntermediateStep, WorkflowStep);
augment(IntermediateStep, LabelWidgetHandler);

// common for all intermediate steps
IntermediateStep.prototype.writeCommon = function(stream)
{
	stream.writeAttribute(Tag.aname, this.generalWidget.nameLineEdit.text); // name
	stream.writeAttribute(Tag.label, this.generalWidget.labelLineEdit.text); // label
	this.writeDescription(stream); // description
	this.writeGeometry(stream); // geometry
	this.writeAttribute(stream, Tag.attributeLog, this.generalWidget.logCheckBox.checked); // attribute log
}

function TerminalStep()
{
}

