var WorkflowStep = function() {
	this.widgets = new Array();
};
*/


function WorkflowStep()
{
	//this.widgets = new Array();
}

augment(WorkflowStep, XmlWriter);
augment(WorkflowStep, XmlReader);
augment(WorkflowStep, WorkflowItemCommon);
augment(WorkflowStep, WorkflowCommon);

WorkflowStep.prototype.addTransition = function(transition)
{
	if (this.transitions == undefined)
		this.transitions = new Array();

	this.transitions.push(transition);
}

WorkflowStep.prototype.removeTransition = function(transition)
{
	var id = transition.data(WorkflowScene.ItemId);
	//QMessageBox.information(0, "WorkflowStep.prototype.removeTransition()", "idToDelete=" + _id);
	for (var i = 0; i < this.transitions.length; ++i) {
		//QMessageBox.information(0, "transition" + i, " id=" + this.transitions[i].data(WorkflowScene.ItemId));
		if (this.transitions[i].data(WorkflowScene.ItemId) == id) {
			this.transitions.splice(i, 1);
			//QMessageBox.information(0, "transition removed", "transitions remaining: " + this.transitions.length);
			return;
		}
		//this.messageBoxTransitions();
	}
}

// debug
WorkflowStep.prototype.messageBoxTransitions = function()
{
	var text = ("total transitions: " + this.transitions.length);
	for (var i = 0; i < this.transitions.length; ++i) {
		text += ("transition" + i + " id=" + this.transitions[i].data(WorkflowScene.ItemId) + "\n");
	}
	QMessageBox.information(0, "current transitions", text);
}

WorkflowStep.prototype.removeTransitions = function()
{
	this.transitions = [];
}

WorkflowStep.prototype.updateTransitions = function()
{
	for (var i = 0; i < this.transitions.length; ++i)
		this.transitions[i].updatePosition();
}

WorkflowStep.prototype.adjustPosition = function()
{
	var cell = WorkflowScene.cellWidth;
	var mapped = this.mapToScene(this.boundingRect().topLeft());

	// Compensate half a cell at top left corner.
	// NB: this should be subtracted back when calculations are finished.
	var x = mapped.x() + cell / 2;
	var y = mapped.y() + cell / 2;

	x = Math.round(x / cell) * cell + this.boundingRect().width() / 2 - cell / 2;
	y = Math.round(y / cell) * cell + this.boundingRect().height() / 2 - cell / 2;

	this.setPos(x, y);
}

function ArchiveStep(parent)
{
	RectItem.call(this, resourceManager.getStepPixmap(WorkflowScene.ArchiveStep), parent);
	this.transitions = new Array();

	this.label = qsTr("Archive");
	this.setToolTip(this.label);

	this.createWidgets();
	this.createActions();
	this.createConnections();

	this.updateLabelLineEdit(this.label);
	this.updateTextItemLabel(this.label);

	this.mandatoryWidgetsChanged();
}

ArchiveStep.prototype = new RectItem();

augment(ArchiveStep, IntermediateStep);

ArchiveStep.prototype.createWidgets = function()
{
	this.widgets = new Array();
	this.widgets.push(this.generalWidget = form.load("widgets.step.archive.general"));
	this.widgets.push(this.archiveWidget = form.load("widgets.step.archive.archive"));
	this.archiveWidget.loadToolButton.enable = false;
	this.archiveWidget.loadToolButton.visible = false;
}

ArchiveStep.prototype.createActions = function()
{
	this.addIndexAct = new QAction(qsTr("Add"), this);
	this.addIndexAct.toolTip = qsTr("Add index to the list");
	this.addIndexAct.icon = resourceManager.getFromCacheAsIcon('add');

	this.archiveWidget.addToolButton.setDefaultAction(this.addIndexAct);

	this.removeIndexAct = new QAction(qsTr("Remove"), this);
	this.removeIndexAct.toolTip = qsTr("Remove index from the list");
	this.removeIndexAct.icon = resourceManager.getFromCacheAsIcon('del');

	this.archiveWidget.removeToolButton.setDefaultAction(this.removeIndexAct);
	this.archiveWidget.removeToolButton.enabled = false;

	this.loadIndexAct = new QAction(qsTr("Load"), this);
	this.loadIndexAct.toolTop = qsTr("Load index");
	this.loadIndexAct.icon = resourceManager.getFromCacheAsIcon('file-open');

	this.archiveWidget.loadToolButton.setDefaultAction(this.loadIndexAct);
}

ArchiveStep.prototype.createConnections = function()
{
	this.createNameConnections();
	this.createLabelConnections();

	// mandatory
	this.archiveWidget.toLineEdit.textChanged.connect(this, this.toLineEditChanged);

	// widgets logic
	this.archiveWidget.indexesTableWidget.itemSelectionChanged.connect(this, this.indexSelectionChanged);

	this.addIndexAct.triggered.connect(this, this.addIndex);
	this.removeIndexAct.triggered.connect(this, this.removeIndex);
	this.loadIndexAct.triggered.connect(this, this.loadIndex);
}

ArchiveStep.prototype.createUndoCommands = function()
{
	var gw = this.generalWidget;
	var aw = this.archiveWidget;
	var n = this.name();

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	this.createLabelUndoConnections();
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(gw.descriptionPlainTextEdit, gw.descriptionLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.logCheckBox, n);

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(aw.toLineEdit, aw.toLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(aw.createArchiveCheckBox, n);
	workflowDesigner.undoCommandFactory.createChangeAbstractItemViewCommand(aw.indexesTableWidget, n);
}

ArchiveStep.prototype.readWidgetValues = function()
{
	this.generalWidget.nameLineEdit.oldText = this.generalWidget.nameLineEdit.text;
	this.generalWidget.labelLineEdit.oldText = this.generalWidget.labelLineEdit.text;
	this.generalWidget.descriptionPlainTextEdit.oldPlainText = this.generalWidget.descriptionPlainTextEdit.plainText;
	this.generalWidget.logCheckBox.oldChecked = this.generalWidget.logCheckBox.checked;
	this.archiveWidget.toLineEdit.oldText = this.archiveWidget.toLineEdit.text;
	this.archiveWidget.createArchiveCheckBox.oldChecked = this.archiveWidget.createArchiveCheckBox.checked;
}

// logic

ArchiveStep.prototype.addIndex = function()
{
	var insertCommand = new InsertRowCommand(this.archiveWidget.indexesTableWidget, qsTr("Indexes"), this.name());
	workflowDesigner.undoStack.push(insertCommand);
}

ArchiveStep.prototype.removeIndex = function()
{
	var removeCommand = new RemoveRowsCommand(this.archiveWidget.indexesTableWidget, qsTr("Indexes"), this.name());
	workflowDesigner.undoStack.push(removeCommand);
}

ArchiveStep.prototype.loadIndex = function()
{
	notImplementedStub();
}

ArchiveStep.prototype.indexSelectionChanged = function()
{
	var rowIndexes = this.archiveWidget.indexesTableWidget.selectionModel().selectedRows();
	this.archiveWidget.removeToolButton.enabled = rowIndexes.length > 0 ? true : false;
}

// mandatory

ArchiveStep.prototype.toLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.archiveWidget.toLineEdit);
}

ArchiveStep.prototype.mandatoryWidgetsChanged = function()
{
	this.toLineEditChanged();
}

ArchiveStep.prototype.allMandatoryDataFilled = function()
{
	return this.nameValid() && this.archiveWidget.toLineEdit.text.trim() != "";
}

// serializing

ArchiveStep.prototype.write = function(stream)
{
	var gw = this.generalWidget;
	var aw = this.archiveWidget;

	stream.writeStartElement(Tag.archiveStep);

	// name, label, description, geometry, log
	this.writeCommon(stream);

	// attribute to
	this.writeAttribute(stream, Tag.attributeTo, aw.toLineEdit.text, /* cdata */ true);

	// attribute create archive
	this.writeAttribute(stream, Tag.attributeCreateArchive, aw.createArchiveCheckBox.checked);

	// attribute index
	this.writeTableWidgetContents(stream, Tag.attributeIndex, Tag.indexes, Tag.index, Tag.aname, aw.indexesTableWidget);

	stream.writeEndElement(); // Tag.archiveStep
}

ArchiveStep.prototype.read = function(stream)
{
	//print("ArchiveStep.prototype.deserialize()");

	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);
	this.generalWidget.labelLineEdit.text = stream.attributes().value(Tag.label);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.description)
			this.readDescription(stream);
		else if (stream.name() == Tag.geometry)
			this.readGeometry(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

ArchiveStep.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeTo)
		this.readText(stream, this.archiveWidget.toLineEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeCreateArchive)
		this.readText(stream, this.archiveWidget.createArchiveCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeIndex)
		this.readTableWidgetContents(stream, Tag.indexes, Tag.index, Tag.aname, this.archiveWidget.indexesTableWidget);
}

function DocumentEventStep(parent)
{
	RectItem.call(this, resourceManager.getStepPixmap(WorkflowScene.DocumentEventStep), parent);
	this.transitions = new Array();

	this.label = qsTr("Document Event");
	this.setToolTip(this.label);

	this.createWidgets();
	this.createConnections();

	this.updateLabelLineEdit(this.label);
	this.updateTextItemLabel(this.label);

	this.mandatoryWidgetsChanged();
}

DocumentEventStep.prototype = new RectItem();

augment(DocumentEventStep, IntermediateStep);

DocumentEventStep.Insert = 0;
DocumentEventStep.Modify = 1;
DocumentEventStep.Delete = 2;

DocumentEventStep.EventTypeStrings = new Array("insert", "modify", "delete");

DocumentEventStep.prototype.createWidgets = function()
{
	this.widgets = new Array();
	this.widgets.push(this.generalWidget = form.load("widgets.step.documentevent.general"));
	this.widgets.push(this.eventWidget = form.load("widgets.step.documentevent.event"));
}

DocumentEventStep.prototype.createConnections = function()
{
	this.createNameConnections();
	this.createLabelConnections();

	// mandatory
	this.eventWidget.folderLineEdit.textChanged.connect(this, this.folderLineEditChanged);
}

DocumentEventStep.prototype.createUndoCommands = function()
{
	var gw = this.generalWidget;
	var ew = this.eventWidget;
	var n = this.name();

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	this.createLabelUndoConnections();
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(gw.descriptionPlainTextEdit, gw.descriptionLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.logCheckBox, n);

	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(ew.extractContentCheckBox, n);
	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(ew.folderLineEdit, ew.folderLabel, n);
	workflowDesigner.undoCommandFactory.createChangeComboBoxCommand(ew.eventTypeComboBox, ew.eventTypeLabel, n);
}

//
DocumentEventStep.prototype.readWidgetValues = function()
{
	this.generalWidget.nameLineEdit.oldText = this.generalWidget.nameLineEdit.text;
	this.generalWidget.labelLineEdit.oldText = this.generalWidget.labelLineEdit.text;
	this.generalWidget.descriptionPlainTextEdit.oldPlainText = this.generalWidget.descriptionPlainTextEdit.plainText;
	this.generalWidget.logCheckBox.oldChecked = this.generalWidget.logCheckBox.checked;
	this.eventWidget.extractContentCheckBox.oldChecked = this.eventWidget.extractContentCheckBox.checked;
	this.eventWidget.folderLineEdit.oldText = this.eventWidget.folderLineEdit.text;
	this.eventWidget.eventTypeComboBox.prevIndex = this.eventWidget.eventTypeComboBox.currentIndex;
}

// mandatory

DocumentEventStep.prototype.folderLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.eventWidget.folderLineEdit);
}

DocumentEventStep.prototype.mandatoryWidgetsChanged = function()
{
	this.folderLineEditChanged();
}

DocumentEventStep.prototype.allMandatoryDataFilled = function()
{
	return this.nameValid() && this.eventWidget.folderLineEdit.text.trim() != "";
}

// serializing

DocumentEventStep.prototype.write = function(stream)
{
	var gw = this.generalWidget;
	var ew = this.eventWidget;

	stream.writeStartElement(Tag.documentEventStep);

	// name, label, description, geometry, log
	this.writeCommon(stream);

	// attribute extract content
	this.writeAttribute(stream, Tag.attributeExtractContent, ew.extractContentCheckBox.checked);

	// attribute folder
	this.writeAttribute(stream, Tag.attributeFolder, ew.folderLineEdit.text, /* cdata */ true);

	// attribute event type
	this.writeAttribute(stream, Tag.attributeEventType, DocumentEventStep.EventTypeStrings[ew.eventTypeComboBox.currentIndex]);

	stream.writeEndElement(); // Tag.documentEventStep
}

DocumentEventStep.prototype.read = function(stream)
{
	//print("DocumentEventStep.prototype.deserialize()");

	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);
	this.generalWidget.labelLineEdit.text = stream.attributes().value(Tag.label);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.description)
			this.readDescription(stream);
		else if (stream.name() == Tag.geometry)
			this.readGeometry(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

DocumentEventStep.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeExtractContent)
		this.readCheckBox(stream, this.eventWidget.extractContentCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeFolder)
		this.readText(stream, this.eventWidget.folderLineEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeEventType)
		this.readAttributeEventType(stream);
}

DocumentEventStep.prototype.readAttributeEventType = function(stream)
{
	var index = DocumentEventStep.EventTypeStrings.indexOf(stream.readElementText());
	if (index != -1)
		this.eventWidget.eventTypeComboBox.currentIndex = index;
}

