MoveStep.prototype = new RectItem();

augment(MoveStep, IntermediateStep);

MoveStep.prototype.createWidgets = function()
{
	this.widgets = new Array();
	this.widgets.push(this.generalWidget = form.load("widgets.step.move.general"));
	this.widgets.push(this.moveWidget = form.load("widgets.step.move.move"));
}

MoveStep.prototype.createConnections = function()
{
	this.createNameConnections();
	this.createLabelConnections();

	// mandatory
	this.moveWidget.documentLineEdit.textChanged.connect(this, this.documentLineEditChanged);
	this.moveWidget.fromLineEdit.textChanged.connect(this, this.fromLineEditChanged);
	this.moveWidget.toLineEdit.textChanged.connect(this, this.toLineEditChanged);
	this.moveWidget.usersLineEdit.textChanged.connect(this, this.usersLineEditChanged);
	this.moveWidget.setRightsCheckBox.stateChanged.connect(this, this.setRightsCheckBoxStateChanged);
}

MoveStep.prototype.createUndoCommands = function()
{
	var gw = this.generalWidget;
	var mw = this.moveWidget;
	var n = this.name();

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	this.createLabelUndoConnections();
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(gw.descriptionPlainTextEdit, gw.descriptionLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.logCheckBox, n);

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(mw.documentLineEdit, mw.documentLabel, n);
	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(mw.fromLineEdit, mw.fromLabel, n);
	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(mw.toLineEdit, mw.toLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(mw.setRightsCheckBox, n);
	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(mw.usersLineEdit, mw.usersLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(mw.keepCopyOfFileCheckBox, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(mw.keepVersionSourceCheckBox, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(mw.keepVersionTargetCheckBox, n);
}

MoveStep.prototype.readWidgetInitialValues = function()
{
	this.generalWidget.nameLineEdit.oldText = this.generalWidget.nameLineEdit.text;
	this.generalWidget.labelLineEdit.oldText = this.generalWidget.labelLineEdit.text;
	this.generalWidget.descriptionPlainTextEdit.oldPlainText = this.generalWidget.descriptionPlainTextEdit.plainText;
	this.generalWidget.logCheckBox.oldChecked = this.generalWidget.logCheckBox.checked;
	this.moveWidget.documentLineEdit.oldText = this.moveWidget.documentLineEdit.text;
	this.moveWidget.fromLineEdit.oldText = this.moveWidget.fromLineEdit.text;
	this.moveWidget.toLineEdit.oldText = this.moveWidget.toLineEdit.text;
	this.moveWidget.setRightsCheckBox.oldChecked = this.moveWidget.setRightsCheckBox.checked;
	this.moveWidget.usersLineEdit.oldText = this.moveWidget.usersLineEdit.text;
	this.moveWidget.keepCopyOfFileCheckBox.oldChecked = this.moveWidget.keepCopyOfFileCheckBox.checked;
	this.moveWidget.keepVersionSourceCheckBox.oldChecked = this.moveWidget.keepVersionSourceCheckBox.checked;
	this.moveWidget.keepVersionTargetCheckBox.oldChecked = this.moveWidget.keepVersionTargetCheckBox.checked;
}

// mandatory

MoveStep.prototype.documentLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.moveWidget.documentLineEdit);
}

MoveStep.prototype.fromLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.moveWidget.fromLineEdit);
}

MoveStep.prototype.toLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.moveWidget.toLineEdit);
}

MoveStep.prototype.usersLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.moveWidget.usersLineEdit);
}

MoveStep.prototype.setRightsCheckBoxStateChanged = function(state)
{
	if (state == Qt.Unchecked)
		this.clearHighlight(this.moveWidget.usersLineEdit);
	else
		this.usersLineEditChanged();
}

MoveStep.prototype.mandatoryWidgetsChanged = function()
{
	this.documentLineEditChanged();
	this.fromLineEditChanged();
	this.toLineEditChanged();
	this.setRightsCheckBoxStateChanged(this.moveWidget.setRightsCheckBox.checkState());
}

MoveStep.prototype.allMandatoryDataFilled = function()
{
	var filled = this.nameValid()
		&& this.moveWidget.documentLineEdit.text.trim() != ""
		&& this.moveWidget.fromLineEdit.text.trim() != ""
		&& this.moveWidget.toLineEdit.text.trim() != "";
	if (this.moveWidget.setRightsCheckBox.checked)
		filled = filled && this.moveWidget.usersLineEdit.text.trim() != "";
	return filled;
}

// serializing

MoveStep.prototype.write = function(stream)
{
	var gw = this.generalWidget;
	var mw = this.moveWidget;

	stream.writeStartElement(Tag.moveStep);

	// name, label, description, geometry, log
	this.writeCommon(stream);

	// attribute document
	this.writeAttribute(stream, Tag.attributeDocument, mw.documentLineEdit.text, /* cdata */ true);

	// attribute from
	this.writeAttribute(stream, Tag.attributeFrom, mw.fromLineEdit.text, /* cdata */ true);

	// attribute to
	this.writeAttribute(stream, Tag.attributeTo, mw.toLineEdit.text, /* cdata */ true);

	// attribute set rights to file
	this.writeAttribute(stream, Tag.attributeSetRightsToFile, mw.setRightsCheckBox.checked);

	// attribute users
	this.writeAttribute(stream, Tag.attributeUsers, mw.usersLineEdit.text, /* cdata */ true);

	// attribute keep copy of file
	this.writeAttribute(stream, Tag.attributeKeepCopyOfFile, mw.keepCopyOfFileCheckBox.checked);

	// attribute keep version source
	this.writeAttribute(stream, Tag.attributeKeepVersionSource, mw.keepVersionSourceCheckBox.checked);

	// attribute keep version target
	this.writeAttribute(stream, Tag.attributeKeepVersionTarget, mw.keepVersionTargetCheckBox.checked);

	stream.writeEndElement(); // Tag.moveStep
}

MoveStep.prototype.read = function(stream)
{
	//print("MoveStep.prototype.deserialize()");

	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);
	this.generalWidget.labelLineEdit.text = stream.attributes().value(Tag.label);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.description)
			this.readDescription(stream);
		else if (stream.name() == Tag.geometry)
			this.readGeometry(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

MoveStep.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeDocument)
		this.readText(stream, this.moveWidget.documentLineEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeFrom)
		this.readText(stream, this.moveWidget.fromLineEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeTo)
		this.readText(stream, this.moveWidget.toLineEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeSetRightsToFile)
		this.readCheckBox(stream, this.moveWidget.setRightsCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeUsers)
		this.readTextIfCheckBoxChecked(stream, this.moveWidget.usersLineEdit, this.moveWidget.setRightsCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeKeepCopyOfFile)
		this.readCheckBox(stream, this.moveWidget.keepCopyOfFileCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeKeepVersionSource)
		this.readCheckBox(stream, this.moveWidget.keepVersionSourceCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeKeepVersionTarget)
		this.readCheckBox(stream, this.moveWidget.keepVersionTargetCheckBox);
}

