	this.emailWidget.objectLineEdit.textChanged.connect(this, this.emailObjectLineEditChanged);
	this.emailWidget.contentPlainTextEdit.textChanged.connect(this, this.emailContentPlainTextEditChanged);
	this.emailWidget.documentLineEdit.textChanged.connect(this, this.emailDocumentLineEditChanged);

	this.popupWidget.toLineEdit.textChanged.connect(this, this.popupToLineEditChanged);
	this.popupWidget.contentPlainTextEdit.textChanged.connect(this, this.popupContentPlainTextEditChanged);

	this.generalWidget.notifyByEmailCheckBox.stateChanged.connect(this, this.notifyByEmailCheckBoxStateChanged);
	this.generalWidget.notifyByPopupCheckBox.stateChanged.connect(this, this.notifyByPopupCheckBoxStateChanged);
	this.emailWidget.attachDocumentCheckBox.stateChanged.connect(this, this.attachDocumentCheckBoxStateChanged);

	// widget logic
	//this.emailWidget.advancedEditorToolButton.clicked.connect(notImplementedStub);
}

NotificationStep.prototype.createUndoCommands = function()
{
	var gw = this.generalWidget;
	var ew = this.emailWidget;
	var pw = this.popupWidget;
	var n = this.name();

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	this.createLabelUndoConnections();
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(gw.descriptionPlainTextEdit, gw.descriptionLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.logCheckBox, n);

	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.notifyByEmailCheckBox, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.notifyByPopupCheckBox, n);

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(ew.toLineEdit, ew.toLabel, n);
	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(ew.objectLineEdit, ew.objectLabel, n);
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(ew.contentPlainTextEdit, ew.contentLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(ew.attachDocumentCheckBox, n);
	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(ew.documentLineEdit, ew.documentLabel, n);

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(pw.toLineEdit, pw.toLabel, n);
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(pw.contentPlainTextEdit, pw.contentLabel, n);
}

NotificationStep.prototype.readWidgetValues = function()
{
	this.generalWidget.nameLineEdit.oldText = this.generalWidget.nameLineEdit.text;
	this.generalWidget.labelLineEdit.oldText = this.generalWidget.labelLineEdit.text;
	this.generalWidget.descriptionPlainTextEdit.oldPlainText = this.generalWidget.descriptionPlainTextEdit.plainText;
	this.generalWidget.notifyByEmailCheckBox.oldChecked = this.generalWidget.notifyByEmailCheckBox.checked;
	this.generalWidget.notifyByPopupCheckBox.oldChecked = this.generalWidget.notifyByPopupCheckBox.checked;
	this.generalWidget.logCheckBox.oldChecked = this.generalWidget.logCheckBox.checked;
	this.emailWidget.toLineEdit.oldText = this.emailWidget.toLineEdit.text;
	this.emailWidget.objectLineEdit.oldText = this.emailWidget.objectLineEdit.text;
	this.emailWidget.contentPlainTextEdit.oldPlainText = this.emailWidget.contentPlainTextEdit.plainText;
	this.emailWidget.attachDocumentCheckBox.oldChecked = this.emailWidget.attachDocumentCheckBox.checked;
	this.emailWidget.documentLineEdit.oldText = this.emailWidget.documentLineEdit.text;
	this.popupWidget.toLineEdit.oldText = this.popupWidget.toLineEdit.text;
	this.popupWidget.contentPlainTextEdit.oldPlainText = this.popupWidget.contentPlainTextEdit.plainText;
}

// mandatory

NotificationStep.prototype.emailToLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.emailWidget.toLineEdit);
}

NotificationStep.prototype.emailObjectLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.emailWidget.objectLineEdit);
}

NotificationStep.prototype.emailContentPlainTextEditChanged = function()
{
	this.mandatoryWidgetChanged(this.emailWidget.contentPlainTextEdit);
}

NotificationStep.prototype.emailDocumentLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.emailWidget.documentLineEdit);
}

NotificationStep.prototype.popupToLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.popupWidget.toLineEdit);
}

NotificationStep.prototype.popupContentPlainTextEditChanged = function()
{
	this.mandatoryWidgetChanged(this.popupWidget.contentPlainTextEdit);
}

NotificationStep.prototype.notifyByEmailCheckBoxStateChanged = function(state)
{
	if (state == Qt.Unchecked) {
		this.clearHighlight(this.emailWidget.toLineEdit);
		this.clearHighlight(this.emailWidget.objectLineEdit);
		this.clearHighlight(this.emailWidget.contentPlainTextEdit);
	} else {
		this.emailToLineEditChanged();
		this.emailObjectLineEditChanged();
		this.emailContentPlainTextEditChanged();
	}
	this.attachDocumentCheckBoxStateChanged();
}

NotificationStep.prototype.notifyByPopupCheckBoxStateChanged = function(state)
{
	if (state == Qt.Unchecked) {
		this.clearHighlight(this.popupWidget.toLineEdit);
		this.clearHighlight(this.popupWidget.contentPlainTextEdit);
	} else {
		this.popupToLineEditChanged();
		this.popupContentPlainTextEditChanged();
	}
}

NotificationStep.prototype.attachDocumentCheckBoxStateChanged = function(state)
{
	if (state == Qt.Unchecked)
		this.clearHighlight(this.emailWidget.documentLineEdit);
	else
		this.emailDocumentLineEditChanged();
}

NotificationStep.prototype.mandatoryWidgetsChanged = function()
{
	this.notifyByEmailCheckBoxStateChanged(this.generalWidget.notifyByEmailCheckBox.checkState());
	this.notifyByPopupCheckBoxStateChanged(this.generalWidget.notifyByPopupCheckBox.checkState());
}

NotificationStep.prototype.allMandatoryDataFilled = function()
{
	var filled = this.nameValid();
	if (this.generalWidget.notifyByEmailCheckBox.checked) {
		filled = filled
			&& this.emailWidget.toLineEdit.text.trim() != ""
			&& this.emailWidget.objectLineEdit.text.trim() != ""
			&& this.emailWidget.contentPlainTextEdit.plainText.trim() != "";
		if (this.emailWidget.attachDocumentCheckBox.checked)
			filled = filled && this.emailWidget.documentLineEdit.text.trim() != "";
	}
	if (this.generalWidget.notifyByPopupCheckBox.checked) {
		filled = filled
			&& this.popupWidget.toLineEdit.text.trim() != ""
			&& this.popupWidget.contentPlainTextEdit.plainText.trim() != "";
	}
	return filled;
}

// Serializing

NotificationStep.prototype.write = function(stream)
{
	var gw = this.generalWidget;
	var ew = this.emailWidget;
	var pw = this.popupWidget;

	stream.writeStartElement(Tag.notificationStep);

	// name, label, description, geometry, log
	this.writeCommon(stream);

	// attribute notify be email
	this.writeAttribute(stream, Tag.attributeNotifyByEmail, gw.notifyByEmailCheckBox.checked);

	// attribute notify be popup
	this.writeAttribute(stream, Tag.attributeNotifyByPopup, gw.notifyByPopupCheckBox.checked);

	// attribute email to
	this.writeAttribute(stream, Tag.attributeEmailTo, ew.toLineEdit.text, /* cdata */ true);

	// attribute email object
	this.writeAttribute(stream, Tag.attributeEmailObject, ew.objectLineEdit.text, /* cdata */ true);

	// attribute email content
	this.writeAttribute(stream, Tag.attributeEmailContent, ew.contentPlainTextEdit.plainText, /* cdata */ true);

	// attribute email attach document
	this.writeAttribute(stream, Tag.attributeEmailAttachDocument, ew.attachDocumentCheckBox.checked);

	// attribute email document
	this.writeAttribute(stream, Tag.attributeEmailDocument, ew.documentLineEdit.text, /* cdata */ true);

	// attribute popup to
	this.writeAttribute(stream, Tag.attributePopupTo, pw.toLineEdit.text, /* cdata */ true);

	// attribute popup content
	this.writeAttribute(stream, Tag.attributePopupContent, pw.contentPlainTextEdit.plainText, /* cdata */ true);

	stream.writeEndElement(); // Tag.notificationStep
}

NotificationStep.prototype.read = function(stream)
{
	//print("MoveStep.prototype.deserialize()");

	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);
	this.generalWidget.labelLineEdit.text = stream.attributes().value(Tag.label);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.description)
			this.readDescription(stream);
		else if (stream.name() == Tag.geometry)
			this.readGeometry(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

NotificationStep.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeNotifyByEmail)
		this.readCheckBox(stream, this.generalWidget.notifyByEmailCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeNotifyByPopup)
		this.readCheckBox(stream, this.generalWidget.notifyByPopupCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeEmailTo)
		this.readText(stream, this.emailWidget.toLineEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeEmailObject)
		this.readText(stream, this.emailWidget.objectLineEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeEmailContent)
		this.readText(stream, this.emailWidget.contentPlainTextEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeEmailAttachDocument)
		this.readCheckBox(stream, this.emailWidget.attachDocumentCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeEmailDocument)
		this.readTextIfCheckBoxChecked(stream, this.emailWidget.documentLineEdit, this.emailWidget.attachDocumentCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributePopupTo)
		this.readText(stream, this.popupWidget.toLineEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributePopupContent)
		this.readText(stream, this.popupWidget.contentPlainTextEdit);
}

