}

function StartStep(parent)
{
	PixmapCircleItem.call(this, resourceManager.getStepPixmap(WorkflowScene.StartStep), parent);
	this.transitions = new Array();

	this.label = qsTr("Start");
	this.setToolTip(this.label);

	this.createWidgets();
	this.createActions();
	this.createConnections();
}

StartStep.prototype = new PixmapCircleItem();

augment(StartStep, TerminalStep);

StartStep.prototype.createWidgets = function()
{
	this.widgets = new Array();
	this.widgets.push(this.generalWidget = form.load("widgets.step.start.general"));
	this.widgets.push(this.variablesWidget = form.load("widgets.step.start.variables"));
}

StartStep.prototype.createActions = function()
{
	this.addVariableAct = new QAction(qsTr("Add"), this);
	this.addVariableAct.toolTip = qsTr("Add variable to the list");
	this.addVariableAct.icon = resourceManager.getFromCacheAsIcon('add');

	this.variablesWidget.addToolButton.setDefaultAction(this.addVariableAct);

	this.removeVariableAct = new QAction(qsTr("Remove"), this);
	this.removeVariableAct.toolTip = qsTr("Remove variable from the list");
	this.removeVariableAct.icon = resourceManager.getFromCacheAsIcon('del');

	this.variablesWidget.removeToolButton.setDefaultAction(this.removeVariableAct);
	this.variablesWidget.removeToolButton.enabled = false;
}

StartStep.prototype.createConnections = function()
{
	this.createNameConnections();

	this.variablesWidget.variablesTableWidget.itemSelectionChanged.connect(this, this.variableSelectionChanged);

	this.addVariableAct.triggered.connect(this, this.addVariable);
	this.removeVariableAct.triggered.connect(this, this.removeVariable);
}

StartStep.prototype.createUndoCommands = function()
{
	var gw = this.generalWidget;
	var vw = this.variablesWidget;
	var n = this.name();

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.logCheckBox, n);
	workflowDesigner.undoCommandFactory.createChangeAbstractItemViewCommand(vw.variablesTableWidget, n);
}

StartStep.prototype.readWidgetValues = function()
{
	this.generalWidget.nameLineEdit.oldText = this.generalWidget.nameLineEdit.text;
	this.generalWidget.logCheckBox.oldChecked = this.generalWidget.logCheckBox.checked;
}

// logic

StartStep.prototype.addVariable = function()
{
	var insertCommand = new InsertRowCommand(
		this.variablesWidget.variablesTableWidget,
		this.variablesWidget.windowTitle,
		this.name()
	);
	workflowDesigner.undoStack.push(insertCommand);
}

StartStep.prototype.removeVariable = function()
{
	var removeCommand = new RemoveRowsCommand(
		this.variablesWidget.variablesTableWidget,
		this.variablesWidget.windowTitle,
		this.name()
	);
	workflowDesigner.undoStack.push(removeCommand);
}

StartStep.prototype.variableSelectionChanged = function()
{
	var rowIndexes = this.variablesWidget.variablesTableWidget.selectionModel().selectedRows();
	this.variablesWidget.removeToolButton.enabled = rowIndexes.length > 0 ? true : false;
}

// mandatory

StartStep.prototype.mandatoryWidgetsChanged = function()
{
}

StartStep.prototype.allMandatoryDataFilled = function()
{
	return this.nameValid();
}

// serializing

StartStep.prototype.write = function(stream)
{
	var gw = this.generalWidget;
	var vw = this.variablesWidget;

	stream.writeStartElement(Tag.startStep);

	// name, geomatry, log
	this.writeCommon(stream);

	// attribute variables
	this.writeTableWidgetContents(stream, Tag.attributeVariable, Tag.variables, Tag.variable, Tag.aname, vw.variablesTableWidget);

	stream.writeEndElement(); // Tag.startStep
}

StartStep.prototype.read = function(stream)
{
	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.geometry)
			this.readGeometry(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
	//stream.skipCurrentElement();
}

StartStep.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeVariable)
		this.readAttributeVariable(stream);
}

StartStep.prototype.readAttributeVariable = function(stream)
{
	this.readTableWidgetContents(stream, Tag.variables, Tag.variable, Tag.aname, this.variablesWidget.variablesTableWidget);
}

