{
	var filled = this.nameValid();
	if (this.generalWidget.logCheckBox.checked)
		filled = filled && this.generalWidget.logDataPlainTextEdit.plainText.trim() != "";
	return filled;
}

// serializing

StopStep.prototype.write = function(stream)
{
	var gw = this.generalWidget;

	stream.writeStartElement(Tag.stopStep);

	// name, geomatry, log
	this.writeCommon(stream);

	// attribute log data
	this.writeAttribute(stream, Tag.attributeLogData, gw.logDataPlainTextEdit.plainText, /* cdata */ true);

	stream.writeEndElement(); // Tag.stopStep
}

StopStep.prototype.read = function(stream)
{
	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.geometry)
			this.readGeometry(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

StopStep.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeLogData)
		this.readTextIfCheckBoxChecked(stream, this.generalWidget.logDataPlainTextEdit, this.generalWidget.logCheckBox);
}

function UsersStep(parent)
{
	RectItem.call(this, resourceManager.getStepPixmap(WorkflowScene.UsersStep), parent);
	this.transitions = new Array();

	this.label = qsTr("Users");
	this.setToolTip(this.label);

	this.createWidgets();
	this.createActions();
	this.createConnections();

	this.updateLabelLineEdit(this.label);
	this.updateTextItemLabel(this.label);

	this.mandatoryWidgetsChanged();
}

UsersStep.prototype = new RectItem();

augment(UsersStep, IntermediateStep);

UsersStep.MultipleChoiceAnswer = 0;
UsersStep.FreeTextAnswer = 1;

UsersStep.AnswerTypeStrings = new Array("multipleChoice", "freeText");

UsersStep.prototype.createWidgets = function()
{
	this.widgets = new Array();
	this.widgets.push(this.generalWidget = form.load("widgets.step.users.general"));
	this.widgets.push(this.questionWidget = form.load("widgets.step.users.question"));

	// convinience
	this.choicePage = this.questionWidget.answerStackedWidget.multipleChoicePage;
	this.textPage = this.questionWidget.answerStackedWidget.freeTextPage;
}

UsersStep.prototype.createActions = function()
{
	this.addAnswerAct = new QAction(qsTr("Add"), this);
	this.addAnswerAct.toolTip = qsTr("Add answer to the list");
	this.addAnswerAct.icon = resourceManager.getFromCacheAsIcon('add');

	this.choicePage.addToolButton.setDefaultAction(this.addAnswerAct);

	this.removeAnswerAct = new QAction(qsTr("Remove"), this);
	this.removeAnswerAct.toolTip = qsTr("Remove answer from the list");
	this.removeAnswerAct.icon = resourceManager.getFromCacheAsIcon('del');

	this.choicePage.removeToolButton.setDefaultAction(this.removeAnswerAct);
	this.choicePage.removeToolButton.enabled = false;

	//this.questionWidget.answerTypeComboBox.addItem(qsTr("Multiple Choice"), UsersStep.MultipleChoiceAnswer);
	//this.questionWidget.answerTypeComboBox.addItem(qsTr("Free Text"), UsersStep.FreeTextAnswer);

}

UsersStep.prototype.createConnections = function()
{
	this.createNameConnections();
	this.createLabelConnections();

	//this.choicePage.answersTableWidget.model().dataChanged.connect(this, this.createChangeDataCommand);

	// mandatory
	this.questionWidget.toLineEdit.textChanged.connect(this, this.toLineEditChanged);
	this.questionWidget.questionPlainTextEdit.textChanged.connect(this, this.questionPlainTextEditChanged);
	this.choicePage.answersTableWidget.model().dataChanged.connect(this, this.anwsersTableWidgetChanged);
	this.choicePage.answersTableWidget.model().rowsInserted.connect(this, this.anwsersTableWidgetChanged);
	this.choicePage.answersTableWidget.model().rowsRemoved.connect(this, this.anwsersTableWidgetChanged);
	this.textPage.answerPlainTextEdit.textChanged.connect(this, this.answerPlainTextEditChanged);
	this.questionWidget.answerTypeComboBox["currentIndexChanged(int)"].connect(this, this.answerTypeComboBoxChanged);

	// widget logic
	this.questionWidget.answerTypeComboBox['currentIndexChanged(int)'].connect(this, this.answerTypeChanged);

	this.choicePage.answersTableWidget.itemSelectionChanged.connect(this, this.answerSelectionChanged);

	this.addAnswerAct.triggered.connect(this, this.addAnswer);
	this.removeAnswerAct.triggered.connect(this, this.removeAnswer);
}

UsersStep.prototype.createUndoCommands = function()
{
	var gw = this.generalWidget;
	var qw = this.questionWidget;
	var n = this.name();

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	this.createLabelUndoConnections();
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(gw.descriptionPlainTextEdit, gw.descriptionLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.logCheckBox, n);

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(qw.toLineEdit, qw.toLabel, n);
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(qw.questionPlainTextEdit, qw.questionLabel, n);
	workflowDesigner.undoCommandFactory.createChangeComboBoxCommand(qw.answerTypeComboBox, qw.answerTypeLabel, n);
	workflowDesigner.undoCommandFactory.createChangeAbstractItemViewCommand(qw.answerStackedWidget.multipleChoicePage.answersTableWidget, n);
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(qw.answerStackedWidget.freeTextPage.answerPlainTextEdit, qw.answerLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(qw.allowIDontKnowCheckBox, n);
	workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(qw.daysSpinBox, qw.renewAfterLabel, n);
	workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(qw.hoursSpinBox, qw.renewAfterLabel, n);
	workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(qw.minutesSpinBox, qw.renewAfterLabel, n);
	workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(qw.secondsSpinBox, qw.renewAfterLabel, n);
}

UsersStep.prototype.readWidgetValues = function()
{
	this.generalWidget.nameLineEdit.oldText = this.generalWidget.nameLineEdit.text;
	this.generalWidget.labelLineEdit.oldText = this.generalWidget.labelLineEdit.text;
	this.generalWidget.descriptionPlainTextEdit.oldPlainText = this.generalWidget.descriptionPlainTextEdit.plainText;
	this.generalWidget.logCheckBox.oldChecked = this.generalWidget.logCheckBox.checked;
	this.questionWidget.toLineEdit.oldText = this.questionWidget.toLineEdit.text;
	this.questionWidget.questionPlainTextEdit.oldPlainText = this.questionWidget.questionPlainTextEdit.plainText;
	this.questionWidget.answerTypeComboBox.prevIndex = this.questionWidget.answerTypeComboBox.currentIndex;
	this.textPage.answerPlainTextEdit.oldPlainText = this.textPage.answerPlainTextEdit.plainText;
	this.questionWidget.allowIDontKnowCheckBox.oldChecked = this.questionWidget.allowIDontKnowCheckBox.checked;
	this.questionWidget.daysSpinBox.oldValue = this.questionWidget.daysSpinBox.value;
	this.questionWidget.hoursSpinBox.oldValue = this.questionWidget.hoursSpinBox.value;
	this.questionWidget.minutesSpinBox.oldValue = this.questionWidget.minutesSpinBox.value;
	this.questionWidget.secondsSpinBox.oldValue = this.questionWidget.secondsSpinBox.value;
}

// logic

UsersStep.prototype.answerTypeChanged = function(index)
{
	this.questionWidget.answerLabel.text = index == UsersStep.MultipleChoiceAnswer ? qsTr("&List of answers") : qsTr("&Answer");

	if (this.questionWidget.answerStackedWidget.currentWidget() != null)
		this.questionWidget.answerStackedWidget.currentWidget().setSizePolicy(QSizePolicy.Ignored, QSizePolicy.Ignored);

	this.questionWidget.answerStackedWidget.currentIndex = index;
	this.questionWidget.answerStackedWidget.currentWidget().setSizePolicy(QSizePolicy.Expanding, QSizePolicy.Expanding);
	this.questionWidget.answerStackedWidget.currentWidget().adjustSize();
}

UsersStep.prototype.addAnswer = function()
{
	var insertCommand = new InsertRowCommand(
		this.choicePage.answersTableWidget,
		this.questionWidget.answerLabel.text.replace(/&/g, ""),
		this.name()
	);
	workflowDesigner.undoStack.push(insertCommand);
}

UsersStep.prototype.removeAnswer = function()
{
	var removeCommand = new RemoveRowsCommand(
		this.choicePage.answersTableWidget,
		this.questionWidget.answerLabel.text.replace(/&/g, ""),
		this.name()
	);
	workflowDesigner.undoStack.push(removeCommand);
}

UsersStep.prototype.answerSelectionChanged = function()
{
	var rowIndexes = this.choicePage.answersTableWidget.selectionModel().selectedRows();
	this.choicePage.removeToolButton.enabled = rowIndexes.length > 0 ? true : false;
}

// mandatory

UsersStep.prototype.toLineEditChanged = function()
{
	this.mandatoryWidgetChanged(this.questionWidget.toLineEdit);
}

UsersStep.prototype.questionPlainTextEditChanged = function()
{
	this.mandatoryWidgetChanged(this.questionWidget.questionPlainTextEdit);
}

UsersStep.prototype.anwsersTableWidgetChanged = function()
{
	this.setHighlighted(this.choicePage.answersTableWidget, !this.answersTableWidgetFilled());
}

UsersStep.prototype.answerPlainTextEditChanged = function()
{
	this.mandatoryWidgetChanged(this.textPage.answerPlainTextEdit);
}

UsersStep.prototype.answerTypeComboBoxChanged = function(index)
{
	if (index == UsersStep.MultipleChoiceAnswer) {
		this.clearHighlight(this.textPage.answerPlainTextEdit);
		this.anwsersTableWidgetChanged();
	} else if (index == UsersStep.FreeTextAnswer) {
		this.clearHighlight(this.choicePage.answersTableWidget);
		this.answerPlainTextEditChanged();
	}
}

UsersStep.prototype.mandatoryWidgetsChanged = function()
{
	this.toLineEditChanged();
	this.questionPlainTextEditChanged();
	this.anwsersTableWidgetChanged();
	this.answerPlainTextEditChanged();
}

UsersStep.prototype.allMandatoryDataFilled = function()
{
	var filled = this.nameValid()
		&& this.questionWidget.toLineEdit.text.trim() != ""
		&& this.questionWidget.questionPlainTextEdit.plainText.trim() != "";
	var index = this.questionWidget.answerTypeComboBox.currentIndex;
	if (index == UsersStep.MultipleChoiceAnswer)
		filled = filled && this.answersTableWidgetFilled();
	else if (index == UsersStep.FreeTextAnswer)
		filled = filled && this.textPage.answerPlainTextEdit.plainText.trim() != "";
	return filled;
}

UsersStep.prototype.answersTableWidgetFilled = function()
{
	var tw = this.choicePage.answersTableWidget;
	return tw.rowCount > 0 && tw.item(0, 0) && tw.item(0, 0).text().trim() != "";
}

// serializing

UsersStep.prototype.write = function(stream)
{
	var gw = this.generalWidget;
	var qw = this.questionWidget;

	stream.writeStartElement(Tag.usersStep);

	// name, label, description, geometry, log
	this.writeCommon(stream);

	// attribute to
	this.writeAttribute(stream, Tag.attributeTo, qw.toLineEdit.text, /* cdata */ true);

	// attribute question
	this.writeAttribute(stream, Tag.attributeQuestion, qw.questionPlainTextEdit.plainText, /* cdata */ true);

	// attribute answer type
	this.writeAttribute(stream, Tag.attributeAnswerType, UsersStep.AnswerTypeStrings[qw.answerTypeComboBox.currentIndex]);

	// attribute answer
	var index = qw.answerTypeComboBox.currentIndex;
	if (index == UsersStep.MultipleChoiceAnswer) {
		this.writeTableWidgetContents(stream, Tag.attributeAnswer, Tag.answers, Tag.answer, Tag.value,
									  this.choicePage.answersTableWidget);
	} else if (index == UsersStep.FreeTextAnswer) {
		this.writeAttribute(stream, Tag.attributeAnswer, this.textPage.answerPlainTextEdit.plainText, /* cdata */ true);
	}

	// attribute variable
	var allowIDontKnow = qw.allowIDontKnowCheckBox.checked;
	this.writeAttribute(stream, Tag.attributeIDontKnow, allowIDontKnow);

	// attribute renew delay
	var renewDelay = allowIDontKnow ? XmlWriter.formattedTime(qw.daysSpinBox, qw.hoursSpinBox, qw.minutesSpinBox, qw.secondsSpinBox) : "00:00:00:00";
	this.writeAttribute(stream, Tag.attributeRenewDelay, renewDelay);

	stream.writeEndElement(); // Tag.usersStep
}

UsersStep.prototype.read = function(stream)
{
	//print("DocumentEventStep.prototype.deserialize()");

	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);
	this.generalWidget.labelLineEdit.text = stream.attributes().value(Tag.label);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.description)
			this.readDescription(stream);
		else if (stream.name() == Tag.geometry)
			this.readGeometry(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

UsersStep.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeTo)
		this.readText(stream, this.questionWidget.toLineEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeQuestion)
		this.readText(stream, this.questionWidget.questionPlainTextEdit);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeAnswerType)
		this.readAttributeAnswerType(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeAnswer)
		this.readAttributeAnswer(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeIDontKnow)
		this.readCheckBox(stream, this.questionWidget.allowIDontKnowCheckBox);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeRenewDelay)
		this.readAttributeRenewDelay(stream);
}

UsersStep.prototype.readAttributeAnswerType = function(stream)
{
	var aa = stream.readElementText();
	print("usersStep.readAttributeAnswerType() " + aa);
	var index = UsersStep.AnswerTypeStrings.indexOf(aa);
	print("index = " + index);
	if (index != -1)
		this.questionWidget.answerTypeComboBox.currentIndex = index;
}

UsersStep.prototype.readAttributeAnswer = function(stream)
{
	if (this.questionWidget.answerTypeComboBox.currentIndex == UsersStep.MultipleChoiceAnswer)
		this.readTableWidgetContents(stream, Tag.answers, Tag.answer, Tag.value, this.questionWidget.answerStackedWidget.multipleChoicePage.answersTableWidget)
	else
		this.questionWidget.answerStackedWidget.freeTextPage.answerPlainTextEdit.plainText = stream.readElementText();
}

UsersStep.prototype.readAttributeRenewDelay = function(stream)
{
	if (this.questionWidget.allowIDontKnowCheckBox.checked)
		XmlReader.readTimeout(stream.readElementText(), this.questionWidget.daysSpinBox, this.questionWidget.hoursSpinBox, this.questionWidget.minutesSpinBox, this.questionWidget.secondsSpinBox);
}

function GatewayStep(parent)
