	stream.writeEndElement(); // Tag.gatewayStep
}

GatewayStep.prototype.read = function(stream)
{
	//print("GatewayStep.prototype.deserialize()");

	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);
	this.generalWidget.labelLineEdit.text = stream.attributes().value(Tag.label);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.description)
			this.readDescription(stream);
		else if (stream.name() == Tag.geometry)
			this.readGeometry(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

GatewayStep.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
