ScriptStep.prototype.createWidgets = function()
{
	this.widgets = new Array();
	this.widgets.push(this.generalWidget = form.load("widgets.step.documentevent.general"));
	this.scriptWidget = new CodeEditor;
	this.scriptWidget.windowTitle = qsTr("Script");
	this.widgets.push(this.scriptWidget);
}

ScriptStep.prototype.createConnections = function()
