	var n = this.name();

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	this.createLabelUndoConnections();
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(gw.descriptionPlainTextEdit, gw.descriptionLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.logCheckBox, n);

	workflowDesigner.undoCommandFactory.createChangeScintillaCommand(sw, qsTr("Source"), n);
}

//
ScriptStep.prototype.readWidgetValues = function()
{
	this.generalWidget.nameLineEdit.oldText = this.generalWidget.nameLineEdit.text;
	this.generalWidget.labelLineEdit.oldText = this.generalWidget.labelLineEdit.text;
	this.generalWidget.descriptionPlainTextEdit.oldPlainText = this.generalWidget.descriptionPlainTextEdit.plainText;
	this.generalWidget.logCheckBox.oldChecked = this.generalWidget.logCheckBox.checked;
	this.scriptWidget.oldText = this.scriptWidget.text();
}

// mandatory

ScriptStep.prototype.mandatoryWidgetsChanged = function()
{
}

ScriptStep.prototype.allMandatoryDataFilled = function()
{
	return this.nameValid();
}

// serializing

ScriptStep.prototype.write = function(stream)
{
	var gw = this.generalWidget;
	var sw = this.scriptWidget;

	stream.writeStartElement(Tag.scriptStep);

	// name, label, description, geometry, log
	this.writeCommon(stream);

	// script
	this.writeAttribute(stream, Tag.attributeSource, sw.text(), true);

	stream.writeEndElement(); // Tag.scriptStep
}

ScriptStep.prototype.read = function(stream)
{
	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);
	this.generalWidget.labelLineEdit.text = stream.attributes().value(Tag.label);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.description)
			this.readDescription(stream);
		else if (stream.name() == Tag.geometry)
			this.readGeometry(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

ScriptStep.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeSource)
		this.scriptWidget.setText(stream.readElementText());
}

function WorkflowTransition(startItem, endItem, pixmap, parent)
{
	ArrowItem.call(this, startItem, endItem, pixmap, parent);
}

WorkflowTransition.prototype = new ArrowItem();

augment(WorkflowTransition, XmlWriter);
augment(WorkflowTransition, XmlReader);
augment(WorkflowTransition, WorkflowItemCommon);
augment(WorkflowTransition, WorkflowCommon);

// common for all non-automatic transitions
WorkflowTransition.prototype.writeCommon = function(stream)
{
	stream.writeAttribute(Tag.aname, this.generalWidget.nameLineEdit.text); // name
	stream.writeAttribute(Tag.label, this.generalWidget.labelLineEdit.text); // label
	stream.writeAttribute(Tag.source, this.startItem.generalWidget.nameLineEdit.text); // source
	stream.writeAttribute(Tag.target, this.endItem.generalWidget.nameLineEdit.text); // target
	this.writeDescription(stream); // description
	this.writeAttribute(stream, Tag.attributeLog, this.generalWidget.logCheckBox.checked); // attribute log
}

function AutomaticTransition(startItem, endItem, parent)
{
	WorkflowTransition.call(this, startItem, endItem, resourceManager.getTransitionPixmap(WorkflowScene.AutomaticTransition), parent);
	this.label = qsTr("Automatic");
	this.setToolTip(this.label);

	// temp fix
	this.textItem.__QGraphicsItem__.setVisible.call(this.textItem, false);
	this.textItem.__QGraphicsItem__.setParentItem.call(this.textItem, 0);

	this.createWidgets();
	this.createConnections();
}

AutomaticTransition.prototype = new WorkflowTransition();

AutomaticTransition.prototype.createWidgets = function()
{
	this.widgets = new Array();
	this.widgets.push(this.generalWidget = form.load("widgets.transition.automatic.general"));
}

AutomaticTransition.prototype.createConnections = function()
{
	this.createNameConnections();
}

AutomaticTransition.prototype.createUndoCommands = function()
{
	var gw = this.generalWidget;
	var n = this.name();

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.logCheckBox, n);
}

// mandatory

AutomaticTransition.prototype.mandatoryWidgetsChanged = function()
{
}

AutomaticTransition.prototype.allMandatoryDataFilled = function()
{
	return this.nameValid();
}

// serializing

AutomaticTransition.prototype.write = function(stream)
{
	var gw = this.generalWidget;

	stream.writeStartElement(Tag.automaticTransition);

	// name
	stream.writeAttribute(Tag.aname, gw.nameLineEdit.text);

	// source
	stream.writeAttribute(Tag.source, this.startItem.generalWidget.nameLineEdit.text);

	// target
	stream.writeAttribute(Tag.target, this.endItem.generalWidget.nameLineEdit.text);

	// attribute log
	this.writeAttribute(stream, Tag.attributeLog, gw.logCheckBox.checked);

	stream.writeEndElement(); // Tag.automaticTransition
}

AutomaticTransition.prototype.read = function(stream)
{
	//print("AutomaticTransition.prototype.deserialize()");

	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);

	this._startItemName = stream.attributes().value(Tag.source);
	this._endItemName = stream.attributes().value(Tag.target);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

AutomaticTransition.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
}

function ConditionalTransition(startItem, endItem, parent)
{
	WorkflowTransition.call(this, startItem, endItem, resourceManager.getTransitionPixmap(WorkflowScene.ConditionalTransition), parent);
	this.label = qsTr("Conditional");
	this.setToolTip(this.label);

	this.createWidgets();
	this.createConnections();

	this.updateLabelLineEdit(this.label);
	this.updateTextItemLabel(this.label);

	this.mandatoryWidgetsChanged();
}

ConditionalTransition.prototype = new WorkflowTransition();

augment(ConditionalTransition, LabelWidgetHandler);

ConditionalTransition.prototype.rotate = function()
{
	var angle = this.line().angleTo(new QLineF(0, 0, 0, 1));
	this.pixmapItem.setRotation(90 + angle);
}

ConditionalTransition.prototype.updatePosition = function()
{
	var paintMethod = ConditionalTransition.prototype.updatePosition;
    ConditionalTransition.prototype.updatePosition = undefined;
	ArrowItem.prototype.updatePosition.call(this);
    ConditionalTransition.prototype.updatePosition = paintMethod;

	// this causes slow performance of big scenes
	// might want to draw polygon instead of pixmap
	if (this.pixmapItem != undefined)
		this.rotate();
}

ConditionalTransition.prototype.createWidgets = function()
{
	this.widgets = new Array();
	this.widgets.push(this.generalWidget = form.load("widgets.transition.conditional.general"));
	this.widgets.push(this.conditionalWidget = form.load("widgets.transition.conditional.condition"));
}

ConditionalTransition.prototype.createConnections = function()
{
	this.createNameConnections();
	this.createLabelConnections();

	// mandatory
	this.conditionalWidget.conditionPlainTextEdit.textChanged.connect(this, this.conditionPlainTextEditChanged);
}

ConditionalTransition.prototype.createUndoCommands = function()
{
	var gw = this.generalWidget;
	var cw = this.conditionalWidget;
	var n = this.name();

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	this.createLabelUndoConnections();
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(gw.descriptionPlainTextEdit, gw.descriptionLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.logCheckBox, n);

	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(cw.conditionPlainTextEdit, cw.conditionLabel, n);
}

// mandatory

ConditionalTransition.prototype.conditionPlainTextEditChanged = function()
{
	this.mandatoryWidgetChanged(this.conditionalWidget.conditionPlainTextEdit);
}

ConditionalTransition.prototype.mandatoryWidgetsChanged = function()
{
	this.conditionPlainTextEditChanged();
}

ConditionalTransition.prototype.allMandatoryDataFilled = function()
{
	return this.nameValid() && this.conditionalWidget.conditionPlainTextEdit.plainText.trim() != "";
}

// serializing

ConditionalTransition.prototype.write = function(stream)
{
	var gw = this.generalWidget;
	var cw = this.conditionalWidget;

	stream.writeStartElement(Tag.conditionalTransition);

	// name, label, source, target, description, log
	this.writeCommon(stream);

	// attribute condition
	this.writeAttribute(stream, Tag.attributeCondition, cw.conditionPlainTextEdit.plainText, /* cdata */ true);

	stream.writeEndElement(); // Tag.conditionalTransition
}

ConditionalTransition.prototype.read = function(stream)
{
	//print("ConditionalTransition.prototype.deserialize()");

	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);
	this.generalWidget.labelLineEdit.text = stream.attributes().value(Tag.label);

	this._startItemName = stream.attributes().value(Tag.source);
	this._endItemName = stream.attributes().value(Tag.target);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.description)
			this.readDescription(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

ConditionalTransition.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeCondition)
		this.readText(stream, this.conditionalWidget.conditionPlainTextEdit);
}

