TimeoutTransition.prototype.createUndoCommands = function()
{
	var gw = this.generalWidget;
	var tw = this.timeoutWidget;
	var n = this.name();

	workflowDesigner.undoCommandFactory.createChangeLineEditCommand(gw.nameLineEdit, gw.nameLabel, n);
	this.createLabelUndoConnections();
	workflowDesigner.undoCommandFactory.createChangePlainTextEditCommand(gw.descriptionPlainTextEdit, gw.descriptionLabel, n);
	workflowDesigner.undoCommandFactory.createChangeCheckBoxCommand(gw.logCheckBox, n);

	workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(tw.daysSpinBox, tw.timeoutLabel, n);
	workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(tw.hoursSpinBox, tw.timeoutLabel, n);
	workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(tw.minutesSpinBox, tw.timeoutLabel, n);
	workflowDesigner.undoCommandFactory.createChangeSpinBoxCommand(tw.secondsSpinBox, tw.timeoutLabel, n);
}

// mandatory

TimeoutTransition.prototype.mandatoryWidgetsChanged = function()
{
}

TimeoutTransition.prototype.allMandatoryDataFilled = function()
{
	return this.nameValid();
}

// serializing

TimeoutTransition.prototype.write = function(stream)
{
	var gw = this.generalWidget;
	var tw = this.timeoutWidget;

	stream.writeStartElement(Tag.timeoutTransition);

	// name, label, source, target, description, log
	this.writeCommon(stream);

	// attribute timeout after
	var timeoutAfter = XmlWriter.formattedTime(tw.daysSpinBox, tw.hoursSpinBox, tw.minutesSpinBox, tw.secondsSpinBox);
	this.writeAttribute(stream, Tag.attributeTimeoutAfter, timeoutAfter);

	stream.writeEndElement(); // Tag.timeoutTransition
}

TimeoutTransition.prototype.read = function(stream)
{
	//print("TimeoutTransition.prototype.deserialize()");

	this.generalWidget.nameLineEdit.text = stream.attributes().value(Tag.aname);
	this.generalWidget.labelLineEdit.text = stream.attributes().value(Tag.label);

	this._startItemName = stream.attributes().value(Tag.source);
	this._endItemName = stream.attributes().value(Tag.target);

	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.description)
			this.readDescription(stream);
		else if (stream.name() == Tag.attribute)
			this.readAttribute(stream);
		else
            stream.skipCurrentElement();
    }
}

TimeoutTransition.prototype.readAttribute = function(stream)
{
	if (stream.attributes().value(Tag.aname) == Tag.attributeLog)
		this.readAttributeLog(stream);
	else if (stream.attributes().value(Tag.aname) == Tag.attributeTimeoutAfter)
		//this.readText(stream, this.conditionalWidget.conditionPlainTextEdit);
		XmlReader.readTimeout(stream.readElementText(), this.timeoutWidget.daysSpinBox, this.timeoutWidget.hoursSpinBox, this.timeoutWidget.minutesSpinBox, this.timeoutWidget.secondsSpinBox);
}

function WorkflowView(parent)
{
	QGraphicsView.call(this);
	//this.setRenderHint(QPainter.Antialiasing);
	this.setRenderHint(QPainter.SmoothPixmapTransform);
	this.setRenderHint(QPainter.TextAntialiasing, false);
	//this.acceptDrops = true; // true by default
	this.dragMode = QGraphicsView.RubberBandDrag;
	//this.cacheMode = QGraphicsView.CacheBackground;
}

WorkflowView.prototype = new QGraphicsView();

WorkflowView.prototype.dragEnterEvent = function(event)
{
	if (event.mimeData().hasFormat("text/x-vdom-workflow-step"))
		event.acceptProposedAction();
	else
		event.ignore();
}

WorkflowView.prototype.dragMoveEvent = function(event)
{
	if (event.mimeData().hasFormat("text/x-vdom-workflow-step"))
		event.acceptProposedAction();
	else
		event.ignore();
}

WorkflowView.prototype.dragLeaveEvent = function(event)
{
	event.accept();
}

WorkflowView.prototype.dropEvent = function(event)
{
	if (event.mimeData().hasFormat("text/x-vdom-workflow-step")) {
		var data = event.mimeData().data("text/x-vdom-workflow-step");
		var dataStream = new QDataStream(data, QIODevice.ReadOnly);
		var stepId = dataStream.readInt();
		// do not allow multiple start steps
		if (stepId == WorkflowScene.StartStep && workflowDesigner.scene.hasStartStep()) {
			event.ignore();
			return;
		}
		var insertCommand = new InsertStepCommand(stepId, this.mapToScene(event.pos()));
		workflowDesigner.undoStack.push(insertCommand);
		workflowDesigner.scene.clearSelection();
		insertCommand.step.setSelected(true);
		event.acceptProposedAction();
	} else {
		event.ignore();
	}
}

// Common xml write routines
function XmlReader()
{
}

XmlReader.prototype.deserialize = function(stream)
{
	// blank implementation
}

XmlReader.prototype.readGeometry = function(stream)
{
	var x = stream.attributes().value(Tag.x);
	var y = stream.attributes().value(Tag.y);
	this.setPos(x, y);
	stream.skipCurrentElement();
}

XmlReader.prototype.readAttributeLog = function(stream)
{
	this.generalWidget.logCheckBox.checked = stream.readElementText() == "true" ? true : false;
}

XmlReader.prototype.readDescription = function(stream)
{
	this.generalWidget.descriptionPlainTextEdit.plainText = stream.readElementText();
}

XmlReader.prototype.readText = function(stream, widget)
{
	if (widget instanceof QLineEdit)
		widget.text = stream.readElementText();
	else if (widget instanceof QPlainTextEdit)
		widget.plainText = stream.readElementText();
	else
		stream.skipCurrentElement();
}

XmlReader.prototype.readTextIfCheckBoxChecked = function(stream, widget, checkBox)
{
	if (checkBox instanceof QCheckBox && checkBox.checked)
		this.readText(stream, widget);
	else
		stream.skipCurrentElement();
}

XmlReader.prototype.readCheckBox = function(stream, checkBox)
{
	if (checkBox instanceof QCheckBox)
		checkBox.checked = stream.readElementText() == "true" ? true : false;
	else
		stream.skipCurrentElement();
}

XmlReader.prototype.readTableWidgetContents = function(stream, tag, itemTag, itemAttributeTag, tableWidget)
{
	while (stream.readNextStartElement()) {
		if (stream.name() == tag) {
			while (stream.readNextStartElement()) {
				if (stream.name() == itemTag)
					this._readItemTagContents(stream, itemAttributeTag, tableWidget);
				else
					stream.skipCurrentElement();
			}
		} else
			stream.skipCurrentElement();
	}
}

XmlReader.prototype._readItemTagContents = function(stream, itemAttributeTag, tableWidget)
{
	var key = stream.attributes().value(itemAttributeTag);
	if (key == "") {
		stream.skipCurrentElement();
		return;
	}
	var value = stream.readElementText();

	var rowCount = tableWidget.rowCount;
	tableWidget.insertRow(rowCount);

	tableWidget.setItem(rowCount, 0, new QTableWidgetItem(key));
	tableWidget.setItem(rowCount, 1, new QTableWidgetItem(value));
}

XmlReader.readTimeout = function(timeoutString, daysSpinBox, hoursSpinBox, minutesSpinBox, secondsSpinBox)
{
	var timeoutSplit = timeoutString.split(':');
	for (var i = 0; i < timeoutSplit.length; ++i) {
		var number = parseInt(timeoutSplit[i]);
		switch (i) {
			case 0: daysSpinBox.value = number; break;
			case 1: hoursSpinBox.value = number; break;
			case 2: minutesSpinBox.value = number; break;
			case 3: secondsSpinBox.value = number; break;
			default: break;
		}
	}
}

