// Common xml write routines
function XmlWriter()
{
}

XmlWriter.prototype.serialize = function(stream)
{
	// blank implementation
}

XmlWriter.prototype.writeGeometry = function(stream)
{
	stream.writeStartElement(Tag.geometry);
	stream.writeAttribute(Tag.x, this.x());
	stream.writeAttribute(Tag.y, this.y());
	stream.writeAttribute(Tag.width, this.boundingRect().width());
	stream.writeAttribute(Tag.height, this.boundingRect().height());
	stream.writeEndElement(); // Tag.geometry
}

XmlWriter.prototype.writeAttribute = function(stream, name, value, cdata)
{
	stream.writeStartElement(Tag.attribute);
	stream.writeAttribute(Tag.aname, name);
	cdata == true ? stream.writeCDATA(value) : stream.writeCharacters(value);
	stream.writeEndElement(); // Tag.attribute
}

XmlWriter.prototype.writeTableWidgetContents = function(stream, attributeName, tag, itemTag,
														itemAttributeTag, tableWidget)
{
	stream.writeStartElement(Tag.attribute);
	stream.writeAttribute(Tag.aname, attributeName);
	stream.writeStartElement(tag);
	for (var row = 0; row < tableWidget.rowCount; ++row) {

		var keyItem = tableWidget.item(row, 0);
		if (keyItem == undefined)
			continue;
		var keyItemText = keyItem.text();
		if (keyItemText.trim() == "")
			continue;

		var valueItem = tableWidget.item(row, 1);
		if (valueItem == undefined)
			continue;
		var valueItemText = valueItem.text();
		if (valueItemText.trim() == "")
			continue;

		stream.writeStartElement(itemTag);
		stream.writeAttribute(itemAttributeTag, keyItemText);
		//stream.writeCharacters(valueItemText);
		stream.writeCDATA(valueItemText);
		stream.writeEndElement(); // itemTag
	}
	stream.writeEndElement(); // tag
	stream.writeEndElement(); // Tag.attribute
}

XmlWriter.prototype.writeDescription = function(stream)
{
	stream.writeStartElement(Tag.description);
	stream.writeCDATA(this.generalWidget.descriptionPlainTextEdit.plainText);
	stream.writeEndElement(); // Tag.description
}

XmlWriter.formattedTime = function(daysSpinBox, hoursSpinBox, minutesSpinBox, secondsSpinBox)
{
	return daysSpinBox.value + ':' + hoursSpinBox.value + ':' + minutesSpinBox.value + ':' + secondsSpinBox.value;
}

function RemoteCaller()
{
	var apiObject = vdom.application.ObjectByGuid("17794a39-3a07-4408-9bee-3f6321fe3652");

	var error = false;
	var errorString = undefined;
	var response = undefined;

	this.call = function(action, value)
	{
		if (value == undefined)
			value = "";

		QApplication.setOverrideCursor(new QCursor(Qt.WaitCursor));

		response = apiObject.CallAction(action, value);

		print("response="+response);

		var stream = new QXmlStreamReader(response);
		if (stream.readNextStartElement()) {
			print("stream.name() = " + stream.name());

			switch (stream.name()) {
				case Tag.success:
					print("Tag.success");
					error = false;
					errorString = "success";
					break;
				case Tag.error:
					print("Tag.error");
					error = true;
					errorString = "";
					if (stream.readNextStartElement() && stream.name() == Tag.code) {
						print("Tag.code");
						errorString = "Error " + stream.readElementText() + "\n";
					}
					if (stream.readNextStartElement() && stream.name() == Tag.description) {
						print("Tag.description");
						errorString += stream.readElementText();
					}
					if (errorString == "")
						errorString = stream.readElementText();
					break;
				default:
					print("default");
					error = true;
					errorString = qsTr("Invalid response xml");
					break;
			}
		}

		if (stream.hasError()) {
			print("error parsing response");
			error = true;
			errorString = qsTr("error parsing response xml at line %1: %2").arg(stream.lineNumber()).arg(stream.errorString())
		}

		QApplication.restoreOverrideCursor();

		return response;
	}

	this.hasError = function() {
		return error;
	}

	this.errorString = function() {
		return errorString;
	}

	this.response = function() {
		return response;
	}
}

