// IconButton - helper class to represent a clickable icon
function IconButton(parent)
{
	QAbstractButton.call(this, parent);

	//m_autoHide(false)
	this.cursor = Qt.ArrowCursor;
	this.focusPolicy = Qt.NoFocus;
}

IconButton.prototype = new QAbstractButton();

IconButton.prototype.paintEvent = function(event)
{
	var painter = new QPainter();
	painter.begin(this);
	var pixmapRect = new QRect(0, 0, this.pixmap.width(), this.pixmap.height());
	pixmapRect.moveCenter(this.rect.center());
	//if (m_autoHide)
	//	painter.setOpacity(m_iconOpacity);
	painter.drawPixmap(pixmapRect, this.pixmap);
	painter.end();
	// Note isDown should really use the active state but in most styles
	// this has no proper feedback
	/*
	QIcon::Mode state = QIcon::Disabled;
	if (isEnabled())
		state = isDown() ? QIcon::Selected : QIcon::Normal;
	QRect pixmapRect = QRect(0, 0, m_pixmap.width(), m_pixmap.height());
	pixmapRect.moveCenter(rect().center());
	if (m_autoHide)
		painter.setOpacity(m_iconOpacity);
	painter.drawPixmap(pixmapRect, m_pixmap);
	*/
}

