QPixmap.prototype.toBase64 = function()
{
	var byteArray = new QByteArray();
	var buffer = new QBuffer(byteArray);
	this.save(buffer, "PNG");
	var stream = new QTextStream(byteArray.toBase64());
	//textStream.setCodec("utf-8");
	return stream.readAll();
}

function WorkflowCommon()
{
}

augment(WorkflowCommon, NameWidgetHandler);

WorkflowCommon.prototype.updateStatus = function()
{
	if (!this.nameValid())
		this.setStatus(WorkflowScene.Invalid);
	else if (this.allMandatoryDataFilled())
		this.setStatus(WorkflowScene.Valid);
	else
		this.setStatus(WorkflowScene.Unfinished);
}

WorkflowCommon.prototype.mandatoryWidgetChanged = function(widget)
{
	var text;
	if (widget instanceof QLineEdit)
		text = widget.text.trim();
	else if (widget instanceof QPlainTextEdit)
		text = widget.plainText.trim();
	else
		return;

	this.setHighlighted(widget, text == "");
}

WorkflowCommon.prototype.setHighlighted = function(widget, highlighted, color)
{
	if (!(widget instanceof QWidget))
		return;

	if (highlighted && color == undefined)
		color = "#808000";

	var styleSheet = highlighted ? "QWidget { border: 2px solid %1; }".arg(color) : "";
	if (widget.styleSheet != styleSheet)
		widget.styleSheet = styleSheet;
	this.updateStatus();
}

WorkflowCommon.prototype.highlight = function(widget)
{
	this.setHighlighted(widget, true);
}

WorkflowCommon.prototype.clearHighlight = function(widget)
{
	this.setHighlighted(widget, false);
}

WorkflowCommon.prototype.readWidgetValues = function()
{
	this.generalWidget.nameLineEdit.oldText = this.generalWidget.nameLineEdit.text;
}

WorkflowCommon.prototype.setTextSilently = function(widget, text)
{
	if (!(widget instanceof QLineEdit) && !(widget instanceof QPlainTextEdit) && !(widget instanceof QTextDocument))
		return;

	widget.blockSignals(true);
	if (widget instanceof QLineEdit && widget.text != text)
		widget.text = text;
	else if (widget instanceof QPlainTextEdit && widget.plainText != text)
		widget.plainText = text;
	else if (widget instanceof QTextDocument && widget.toPlainText() != text)
		widget.setPlainText(text);
	widget.blockSignals(false);
}

function InsertStepCommand(stepType, pos)
{
	QUndoCommand.call(this);

	this.pos = pos;
	this.step = undefined;
	switch(stepType) {
		case WorkflowScene.StartStep:
			this.step = new StartStep(workflowDesigner.scene);
			break;
		case WorkflowScene.DocumentEventStep:
			this.step = new DocumentEventStep(workflowDesigner.scene);
			break;
		case WorkflowScene.UsersStep:
			this.step = new UsersStep(workflowDesigner.scene);
			break;
		case WorkflowScene.MoveStep:
			this.step = new MoveStep(workflowDesigner.scene);
			break;
		case WorkflowScene.NotificationStep:
			this.step = new NotificationStep(workflowDesigner.scene);
			break;
		case WorkflowScene.ArchiveStep:
			this.step = new ArchiveStep(workflowDesigner.scene);
			break;
		case WorkflowScene.ScriptStep:
			this.step = new ScriptStep(workflowDesigner.scene);
			break;
		case WorkflowScene.GatewayStep:
			this.step = new GatewayStep(workflowDesigner.scene);
			break;
		case WorkflowScene.StopStep:
			this.step = new StopStep(workflowDesigner.scene);
			break;
		default:
			return;
	}

	if (this.step != undefined) {
		// set type type and subtype
		this.step.setData(WorkflowScene.ItemType, WorkflowScene.Step);
		this.step.setData(WorkflowScene.ItemSubType, stepType);
		// generate id
		this.step.setData(WorkflowScene.ItemId, ++workflowDesigner.scene._currentId);
		// generate name
		this.step.setName("step_" + QUuid.createUuid().toString().substring(1, 37).replace(/-/g, '_'));
		// undo commands for widgets
		this.step.createUndoCommands();
		// command text
		this.setText(qsTr("Add %1 step at (%2, %3)").arg(this.step.label).arg(pos.x()).arg(pos.y()));
	}
}

InsertStepCommand.prototype = new QUndoCommand();

InsertStepCommand.prototype.undo = function()
{
	workflowDesigner.scene.removeWorkflowItem(this.step);
	//workflowDesigner.scene.update();
}

InsertStepCommand.prototype.redo = function()
{
	// add to scene
	workflowDesigner.scene.addWorkflowItem(this.step);

	// refresh positioning
	this.step.setPos(this.pos);
	this.step.adjustPosition();

	//workflowDesigner.scene.clearSelection();
	//workflowDesigner.scene.update();

	//this.step.changed.connect(this, this.changed);

	//this.stepInserted.emit(step.data(WorkflowScene.ItemId));
	//this.changed.emit();
}

function InsertTransitionCommand(transitionType, startItem, endItem)
{
	QUndoCommand.call(this);

	this.startItem = startItem;
	this.endItem = endItem;

	this.transition = undefined;
	switch (transitionType) {
		case WorkflowScene.AutomaticTransition:
			this.transition = new AutomaticTransition(startItem, endItem, workflowDesigner.scene);
			break;
		case WorkflowScene.ConditionalTransition:
			this.transition = new ConditionalTransition(startItem, endItem, workflowDesigner.scene);
			break;
		case WorkflowScene.TimeoutTransition:
			this.transition = new TimeoutTransition(startItem, endItem, workflowDesigner.scene);
			break;
		case WorkflowScene.ErrorTransition:
			this.transition = new ErrorTransition(startItem, endItem, workflowDesigner.scene);
			break;
		default:
			return;
	}

	if (this.transition != undefined) {
		// set transition type and subtype
		this.transition.setData(WorkflowScene.ItemType, WorkflowScene.Transition);
		this.transition.setData(WorkflowScene.ItemSubType, transitionType);
		// generate id
		this.transition.setData(WorkflowScene.ItemId, ++workflowDesigner.scene._currentId);
		// generate name
		this.transition.setName("transition_" + QUuid.createUuid().toString().substring(1, 37).replace(/-/g, '_'));
		// undo commands for widgets
		this.transition.createUndoCommands();
		// command text
		this.setText(qsTr("Add %1 transition from '%2' to '%3'").arg(this.transition.label).arg(startItem.name()).arg(endItem.name()));
	}
}

InsertTransitionCommand.prototype = new QUndoCommand();

InsertTransitionCommand.prototype.undo = function()
{
	workflowDesigner.scene.removeWorkflowItem(this.transition);
	//workflowDesigner.scene.update();
}

InsertTransitionCommand.prototype.redo = function()
{
	// add transition to steps
	this.startItem.addTransition(this.transition);
	this.endItem.addTransition(this.transition);

	// add to scene
	workflowDesigner.scene.addWorkflowItem(this.transition);

	// refresh positioning
	this.transition.updatePosition();

	//workflowDesigner.scene.update();

	//this.clearSelection();
	//transition.setSelected(true);

	//this.transition.changed.connect(this, this.changed);
	//this.transitionInserted.emit(transition.data(WorkflowScene.ItemId));
	//this.changed.emit();
}

function DeleteCommand()
{
	QUndoCommand.call(this);

	this.items = new Array();

	var selectedItems = workflowDesigner.scene.selectedItems();

	for (var i = 0; i < selectedItems.length; ++i) {
		var item = workflowDesigner.scene.workflowItem(selectedItems[i]);
		if (item != undefined) {
			// Check if there is no such item added yet as far as
			// transition can already be added when processing step's transitions
			if (this.items.indexOf(item) == -1)
				this.items.push(item);
			// process step's transitions
			if (item.isStep()) {
				for (var j = 0; j < item.transitions.length; ++j) {
					var transition = item.transitions[j];
					if (this.items.indexOf(transition) == -1)
						this.items.push(transition);
				}
			}
		}
	}

	if (this.items.length == 0)
		return;

	this.setText(this.items.length == 1 ? qsTr("Delete '%1'").arg(this.items[0].name())
										: qsTr("Delete %1 workflow items").arg(this.items.length));
}

DeleteCommand.prototype = new QUndoCommand();

DeleteCommand.prototype.undo = function()
{
	workflowDesigner.scene.removeSelectionChangedConnection();
	for (var i = 0; i < this.items.length; ++i) {
		var item = this.items[i];
		if (item.scene() != null)
			continue;
		workflowDesigner.scene.addWorkflowItem(item);
		if (item.isTransition()) {
			item.startItem.addTransition(item);
			item.endItem.addTransition(item);
		}
	}
	// Restore previous selection
	workflowDesigner.scene.clearSelection();
	for (var i = 0; i < this.items.length; ++i)
		this.items[i].setSelected(true);
	workflowDesigner.scene.createSelectionChangedConnection();
	workflowDesigner.scene.handleSelectionChanged();
	workflowDesigner.scene.update();
}

DeleteCommand.prototype.redo = function()
{
	workflowDesigner.scene.removeSelectionChangedConnection();
	for (var i = 0; i < this.items.length; ++i)
		// Item can be already deleted (for example transition deleted with step)
		// So, checking for non-null scene before deleting
		if (this.items[i].scene() != null)
			workflowDesigner.scene.removeWorkflowItem(this.items[i]);
	workflowDesigner.scene.clearSelection();
	workflowDesigner.scene.createSelectionChangedConnection();
	workflowDesigner.scene.handleSelectionChanged();
	workflowDesigner.scene.update();
}

function MoveCommand(movingSteps, oldPositions)
{
	QUndoCommand.call(this);

	this.movingSteps = movingSteps;
	this.oldPositions = oldPositions;
	this.newPositions = new Array();
	for (var i = 0; i < movingSteps.length; ++i)
		this.newPositions.push(movingSteps[i].pos());

	if (this.movingSteps.length > 1)
		this.setText(qsTr("Move %1 steps").arg(this.movingSteps.length));
}

MoveCommand.prototype = new QUndoCommand();

MoveCommand.prototype.undo = function()
{
	for (var i = 0; i < this.movingSteps.length; ++i)
		this.movingSteps[i].setPos(this.oldPositions[i]);
	workflowDesigner.scene.update();

	if (this.movingSteps.length == 1)
		this.setText(qsTr("Move '%1' at (%2, %3)").arg(this.item.name()).arg(this.newPos.x()).arg(this.newPos.y()));
}

MoveCommand.prototype.redo = function()
{
	for (var i = 0; i < this.movingSteps.length; ++i)
		this.movingSteps[i].setPos(this.newPositions[i]);

	if (this.movingSteps.length == 1)
		this.setText(qsTr("Move '%1' at (%2, %3)").arg(this.movingSteps[0].name()).arg(this.newPositions[0].x()).arg(this.newPositions[0].y()));
}

MoveCommand.prototype.id = function()
{
	return 1349;
}

MoveCommand.prototype.mergeWith = function(command)
{
	// workflowDesigner.lastUndoCommand == command

	if (workflowDesigner.lastUndoCommand == undefined || !(workflowDesigner.lastUndoCommand instanceof MoveCommand))
		return false;

	if (this.movingSteps.length != workflowDesigner.lastUndoCommand.movingSteps.length)
		return false;

	for (var i = 0; i < this.movingSteps.length; ++i) {
		if (this.movingSteps[i] != workflowDesigner.lastUndoCommand.movingSteps[i])
			return false;
	}

	this.newPositions = new Array();
	for (var i = 0; i < workflowDesigner.lastUndoCommand.movingSteps.length; ++i)
		this.newPositions.push(workflowDesigner.lastUndoCommand.movingSteps[i].pos());

	if (this.movingSteps.length == 1)
		this.setText(qsTr("Move '%1' at (%2, %3)").arg(this.movingSteps[0].name()).arg(this.newPositions[0].x()).arg(this.newPositions[0].y()));

	return true;
}

function ChangeLineEditCommand(lineEdit, whatString, ownerString)
{
	QUndoCommand.call(this);

	this.lineEdit = lineEdit;

	this.oldText = lineEdit.oldText != undefined ? lineEdit.oldText : "";
	this.newText = lineEdit.text;

	this.textChanged = signal();

	this.setText(qsTr("Changed '%1' of '%2'").arg(whatString).arg(ownerString));
}

ChangeLineEditCommand.prototype = new QUndoCommand();

ChangeLineEditCommand.prototype.undo = function()
{
	this.setLineEditText(this.oldText);
}

ChangeLineEditCommand.prototype.redo = function()
{
	this.setLineEditText(this.newText);
	this.lineEdit.oldText = this.newText;
}

ChangeLineEditCommand.prototype.id = function()
{
	return 1350;
}

ChangeLineEditCommand.prototype.mergeWith = function(command)
{
	if (workflowDesigner.lastUndoCommand == undefined || !(workflowDesigner.lastUndoCommand instanceof ChangeLineEditCommand))
		return false;

	if (this.lineEdit != workflowDesigner.lastUndoCommand.lineEdit)
		return false;

	this.newText = workflowDesigner.lastUndoCommand.lineEdit.text;

	return true;
}

ChangeLineEditCommand.prototype.setLineEditText = function(text)
{
	if (this.lineEdit.text != text) {
		this.lineEdit.blockSignals(true);
		this.lineEdit.text = text;
		this.lineEdit.blockSignals(false);
	}
	this.textChanged.emit(text);
}

