function InsertRowCommand(view, whatString, ownerString)
{
	QUndoCommand.call(this);

	this.view = view;

	this.setText(qsTr("Insert row into '%1' of '%2'").arg(whatString).arg(ownerString));
}

InsertRowCommand.prototype = new QUndoCommand();

InsertRowCommand.prototype.undo = function()
{
	var lastRow = this.view.model().rowCount() - 1;
	this.view.model().removeRow(lastRow);
}

InsertRowCommand.prototype.redo = function()
{
	var rowCount = this.view.model().rowCount();
	this.view.model().insertRow(rowCount);
}

function RemoveRowsCommand(view, whatString, ownerString)
{
	QUndoCommand.call(this);

	this.view = view;

	var selectionModel = view.selectionModel();

	this.rowIndexes = selectionModel.selectedRows();
	if (this.rowIndexes.length == 0)
		return;

	this.rows = new Array();
	for (var i = 0; i < this.rowIndexes.length; ++i) {
		var row = this.rowIndexes[i].row();
		var parent = this.rowIndexes[i].parent();
		this.rows.push([row, parent]);
	}

	this.rows.sort();

	this.selectedIndexes = selectionModel.selectedIndexes();

	this.items = new Array();
	for (var i = 0; i < this.selectedIndexes.length; ++i) {
		var row = this.selectedIndexes[i].row();
		var column = this.selectedIndexes[i].column();
		var data = this.selectedIndexes[i].data();
		var parent = this.selectedIndexes[i].parent();
		this.items.push([row, column, data, parent]);
	}

	this.items.sort();

	this.setText(qsTr("Remove %1 rows from '%2' of '%3'").arg(this.rowIndexes.length).arg(whatString).arg(ownerString));
}

RemoveRowsCommand.prototype = new QUndoCommand();

RemoveRowsCommand.prototype.undo = function()
{
	// create rows
	var model = this.view.model();
	for (var i = 0; i < this.rows.length; ++i)
		model.insertRow(/* row */ this.rows[i][0], /* parent */ this.rows[i][3])

	// fill data
	model.blockSignals(true);
	for (var i = 0; i < this.items.length; ++i) {
		var item = this.items[i];
		var index = model.index(/* row */ item[0], /* column */ item[1], /* parent */ item[3]);
		model.setData(index, /* data */ item[2], Qt.DisplayRole);
	}
	model.blockSignals(false);
}

RemoveRowsCommand.prototype.redo = function()
{
	var model = this.view.model();
	for (var i = this.rows.length - 1; i >= 0; --i)
		model.removeRow(/* row */ this.rows[i][0], /* parent */ this.rows[i][3])
}

function ChangeAbstractItemViewCommand(view, topLeft, bottomRight, ownerString)
{
	QUndoCommand.call(this);

	this.view = view;
	this.model = view.model();
	this.row = topLeft.row();
	this.column = topLeft.column();
	this.parent = topLeft.parent();

	this.oldData = this.model.data(topLeft, ChangeAbstractItemViewCommand.OldDisplayRole);
	if (this.oldData == undefined)
		this.oldData = "";
	this.newData = this.model.data(topLeft);

	var whatString = this.model.headerData(this.column, Qt.Horizontal);

	this.setText(qsTr("Changed '%1' at (%2, %3) of '%4'").arg(whatString).arg(this.row + 1).arg(this.column + 1).arg(ownerString));
}

ChangeAbstractItemViewCommand.prototype = new QUndoCommand();

ChangeAbstractItemViewCommand.OldDisplayRole = Qt.UserRole;

ChangeAbstractItemViewCommand.prototype.undo = function()
{
	this.model.blockSignals(true);
	var index = this.model.index(this.row, this.column, this.parent);
	this.model.setData(index, this.oldData, Qt.DisplayRole);
	this.view.dataChanged(index, index);
	this.model.blockSignals(false);
}

ChangeAbstractItemViewCommand.prototype.redo = function()
{
	this.model.blockSignals(true);
	var index = this.model.index(this.row, this.column, this.parent);
	this.model.setData(index, this.newData, Qt.DisplayRole);
	this.model.setData(index, this.newData, ChangeAbstractItemViewCommand.OldDisplayRole);
	this.view.dataChanged(index, index);
	this.model.blockSignals(false);
}

ChangeAbstractItemViewCommand.prototype.id = function()
{
	return 1360;
}

ChangeAbstractItemViewCommand.prototype.mergeWith = function(command)
{
	if (workflowDesigner.lastUndoCommand == undefined || !(workflowDesigner.lastUndoCommand instanceof ChangeAbstractItemViewCommand))
		return false;

	if (this.model != workflowDesigner.lastUndoCommand.model)
		return false;

	if (this.row != workflowDesigner.lastUndoCommand.row || this.column != workflowDesigner.lastUndoCommand.column)
		return false;

	return true;
}

