function PasteCommand(contents)
{
	QUndoCommand.call(this);

	this.items = new Array();
	this.steps = {};
	this.selectedItems = workflowDesigner.scene.selectedItems();

	var stream = new QXmlStreamReader(contents);
	if (!stream.readNextStartElement() || stream.name() != Tag.graph)
		return;
	while (stream.readNextStartElement()) {
		if (stream.name() == Tag.steps) {
			while (stream.readNextStartElement()) {
				var step = workflowDesigner.scene.factory.createStep(stream.name());
				if (step != undefined) {
					//step.setData(WorkflowScene.ItemType, WorkflowScene.Step);
					//step.setData(WorkflowScene.ItemSubType, stepType);

					// generate id
					step.setData(WorkflowScene.ItemId, ++workflowDesigner.scene._currentId);
					//step.checkName();


					//step.setPos(scenePos);
					//step.adjustPosition();

					step.read(stream);

					var pos = step.pos();
					var offset = WorkflowScene.cellWidth * 3;
					step.setPos(pos.x() + offset, pos.y() + offset);

					var originalName = step.name();

					// generate name
					var copyName = "step_copy_" + QUuid.createUuid().toString().substring(1, 37).replace(/-/g, '_');
					step.setName(copyName);

					this.steps[originalName] = step;

					//workflowDesigner.scene.addWorkflowItem(step);

					//step.createUndoCommands();

					this.items.push(step);

				} else {
					stream.skipCurrentElement();
				}
			}
		} else if (stream.name() == Tag.transitions) {
			while (stream.readNextStartElement()) {
				var transition = workflowDesigner.scene.factory.createTransition(stream.name());
				if (transition != undefined) {

					// set transition type id
					//transition.setData(WorkflowScene.ItemType, WorkflowScene.Transition);
					//transition.setData(WorkflowScene.ItemSubType, transitionType);

					// generate id
					transition.setData(WorkflowScene.ItemId, ++workflowDesigner.scene._currentId);

					transition.read(stream);

					var copyName = "transition_copy_" + QUuid.createUuid().toString().substring(1, 37).replace(/-/g, '_');
					transition.setName(copyName);

					if (transition._startItemName == undefined || transition._startItemName == "" || transition._endItemName == undefined || transition._endItemName == "")
						continue;

					var startItem = this.steps[transition._startItemName];
					var endItem = this.steps[transition._endItemName];

					if (startItem == undefined || endItem == undefined)
						continue;

					// transition
					transition.setStartItem(startItem);
					transition.setEndItem(endItem);

					// add transition to steps
					//startItem.addTransition(transition);
					//endItem.addTransition(transition);

					// add to scene
					//this.addWorkflowItem(transition);

					// refresh positioning
					//transition.updatePosition();

					transition.createUndoCommands();

					this.items.push(transition);

				} else {
					stream.readNextStartElement();
				}
			}
		} else {
			stream.skipCurrentElement();
		}
	}

	if (this.items.length == 0)
		return;

	this.setText(qsTr("Paste %1 workflow item(s)").arg(this.items.length));
}

PasteCommand.prototype = new QUndoCommand();

PasteCommand.prototype.undo = function()
{
	workflowDesigner.scene.removeSelectionChangedConnection();
	for (var i = 0; i < this.items.length; ++i)
		// Item can be already deleted (for example transition deleted with step)
		// So, checking for non-null scene before deleting
		if (this.items[i].scene() != null)
			workflowDesigner.scene.removeWorkflowItem(this.items[i]);
	// Restore previous selection
	workflowDesigner.scene.clearSelection();
	for (var i = 0; i < this.selectedItems.length; ++i)
		this.selectedItems[i].setSelected(true);
	workflowDesigner.scene.createSelectionChangedConnection();
	workflowDesigner.scene.handleSelectionChanged();
	//workflowDesigner.scene.update();
}

PasteCommand.prototype.redo = function()
{
	//print("PasteCommand.prototype.redo()");
	//print("this.items.length="+this.items.length);

	workflowDesigner.scene.removeSelectionChangedConnection();
	workflowDesigner.scene.clearSelection();
	for (var i = 0; i < this.items.length; ++i) {
		var item = this.items[i];
		//print("!!!item"+i+"="+item);
		//print("!!!item.name()="+item.name());
		//print("!!!item.scene()="+item.scene());
		if (item.scene() != null)
			continue;
		workflowDesigner.scene.addWorkflowItem(item);
		if (item.isTransition()) {
			item.startItem.addTransition(item);
			item.endItem.addTransition(item);
		}
		item.setSelected(true);
		//print("ok");
	}
	workflowDesigner.scene.createSelectionChangedConnection();
	workflowDesigner.scene.handleSelectionChanged();
	//workflowDesigner.scene.update();
}

