function DescriptionWidgetHandler()
{
}

DescriptionWidgetHandler.prototype.createDescriptionUndoConnections = function()
{
	this.generalWidget.descriptionPlainTextEdit.textChanged.connect(this, this.createDescriptionChangePlainTextEditCommand);
}

DescriptionWidgetHandler.prototype.removeDescriptionUndoConnections = function()
{
	this.generalWidget.descriptionPlainTextEdit.textChanged.disconnect(this, this.createDescriptionChangePlainTextEditCommand);
}

DescriptionWidgetHandler.prototype.createDescriptionChangePlainTextEditCommand = function()
{
	workflowDesigner.undoStack.push(
		workflowDesigner.lastUndoCommand = new ChangePlainTextEditCommand(
			this.generalWidget.descriptionPlainTextEdit,
			this.generalWidget.descriptionLabel.text.replace(/&/g, ""),
			this.name()
		)
	);
}

