function LabelWidgetHandler()
{
}

LabelWidgetHandler.prototype.createLabelConnections = function()
{
	this.textItem.document().contentsChanged.connect(this, this.updateLabelLineEdit);
	this.generalWidget.labelLineEdit.textChanged.connect(this, this.updateTextItemLabel);
}

LabelWidgetHandler.prototype.removeLabelConnections = function()
{
	this.textItem.document().contentsChanged.disconnect(this, this.updateLabelLineEdit);
	this.generalWidget.labelLineEdit.textChanged.disconnect(this, this.updateTextItemLabel);
}

LabelWidgetHandler.prototype.updateLabelLineEdit = function()
{
	if (this.generalWidget.labelLineEdit.text != this.textItem.document().toPlainText())
		this.generalWidget.labelLineEdit.text = this.textItem.document().toPlainText();
}

LabelWidgetHandler.prototype.updateTextItemLabel = function(text)
{
	if (text != this.textItem.document().toPlainText())
		this.textItem.document().setPlainText(text);
}

// undo handler
LabelWidgetHandler.prototype.createLabelUndoConnections = function()
{
	this.generalWidget.labelLineEdit.textChanged.connect(this, this.createLabelChangeLineEditCommand);
}

LabelWidgetHandler.prototype.removeLabelUndoConnections = function()
{
	this.generalWidget.labelLineEdit.textChanged.disconnect(this, this.createLabelChangeLineEditCommand);
}

LabelWidgetHandler.prototype.createLabelChangeLineEditCommand = function(text)
{
	workflowDesigner.lastUndoCommand = new ChangeLineEditCommand(
		this.generalWidget.labelLineEdit,
		this.generalWidget.labelLabel.text.replace(/&/g, ""),
		this.name()
	);
	workflowDesigner.lastUndoCommand.textChanged.connect(
		this,
		function(text) {
			this.setTextSilently(this.textItem.document(), text);
			this.adjust();
		}
	);
	workflowDesigner.undoStack.push(workflowDesigner.lastUndoCommand);
}

function UndoCommandFactory()
{
	// QCheckBox
	this.createChangeCheckBoxCommand = function(checkBox, ownerString) {
		if (!(checkBox instanceof QCheckBox))
			return;

		var slot;
		checkBox.stateChanged.connect(
			slot = function() {
				workflowDesigner.undoStack.push(
					workflowDesigner.lastUndoCommand = new ChangeCheckBoxCommand(checkBox, ownerString)
				);
			}
		);
		return slot;
	}

	// QComboBox
	this.createChangeComboBoxCommand = function(comboBox, label, ownerString) {
		if (!(comboBox instanceof QComboBox) || !(label instanceof QLabel))
			return;

		var slot;
		comboBox["currentIndexChanged(int)"].connect(
			slot = function() {
				workflowDesigner.undoStack.push(
					workflowDesigner.lastUndoCommand = new ChangeComboBoxCommand(
						comboBox, label.text.replace(/&/g, ""), ownerString)
				);
			}
		);
		return slot;
	}

	// QAbstractItemView
	this.createChangeAbstractItemViewCommand = function(view, ownerString) {
		if (!(view instanceof QAbstractItemView))
			return;

		var slot;
		view.model().dataChanged.connect(
			slot = function(topLeft, bottomRight) {
				if (topLeft.row() != bottomRight.row() || topLeft.column() != bottomRight.column())
					return;

				workflowDesigner.undoStack.push(
					workflowDesigner.lastUndoCommand = new ChangeAbstractItemViewCommand(
						view, topLeft, bottomRight, ownerString
					)
				);
			}
		);
		return slot;
	}

	// QLineEdit
	this.createChangeLineEditCommand = function(lineEdit, label, ownerString)
	{
		if (!(lineEdit instanceof QLineEdit) || !(label instanceof QLabel))
			return;

		var slot;
		lineEdit.textChanged.connect(
			slot = function() {
				print("textChanged()");

				workflowDesigner.undoStack.push(
					workflowDesigner.lastUndoCommand = new ChangeLineEditCommand(
						lineEdit, label.text.replace(/&/g, ""), ownerString)
				);
			}
		);
		return slot;
	}

	// QPlainTextEdit
	this.createChangePlainTextEditCommand = function(plainTextEdit, label, ownerString)
	{
		if (!(plainTextEdit instanceof QPlainTextEdit) || !(label instanceof QLabel))
			return;

		var slot;
		plainTextEdit.textChanged.connect(
			slot = function() {
				workflowDesigner.undoStack.push(
					workflowDesigner.lastUndoCommand = new ChangePlainTextEditCommand(
						plainTextEdit, label.text.replace(/&/g, ""), ownerString)
				);
			}
		);
		return slot;
	}

	// QsciScintilla
	this.createChangeScintillaCommand = function(scintilla, label, ownerString)
	{
		if (!(scintilla instanceof QsciScintilla)/* || !(label instanceof QLabel)*/)
			return;

		var slot;
		scintilla.textChanged.connect(
			slot = function() {
				workflowDesigner.undoStack.push(
					workflowDesigner.lastUndoCommand = new ChangeScintillaCommand(
						scintilla, label instanceof QLabel ? label.text.replace(/&/g, "") : label, ownerString)
				);
			}
		);
		return slot;
	}

	// QSpinBox
	this.createChangeSpinBoxCommand = function(spinBox, label, ownerString)
	{
		if (!(spinBox instanceof QSpinBox) || !(label instanceof QLabel))
			return;

		var slot;
		spinBox["valueChanged(int)"].connect(
			slot = function() {
				workflowDesigner.undoStack.push(
					workflowDesigner.lastUndoCommand = new ChangeSpinBoxCommand(
						spinBox, label.text.replace(/&/g, "") + " " + spinBox.suffix, ownerString)
				);
			}
		);
		return slot;
	}
}

function ConfigDialog()
{
	this.dialog = form.load("widgets.configdialog", this);
	if (!this.dialog)
		return;
	this.dialog.windowIcon = resourceManager.getFromCacheAsIcon('tools-settings');

	this.dialog.buttonBox.clicked.connect(this, this.buttonBoxClicked);

	this.init();

	this.configChanged = signal();
}

ConfigDialog.prototype.buttonBoxClicked = function(button)
{
	switch (this.dialog.buttonBox.standardButton(button)) {
		case QDialogButtonBox.Discard:
			this.discard();
			break;
		case QDialogButtonBox.Ok:
		case QDialogButtonBox.Apply:
			this.apply();
			break;
		default:
	}
}

