	}
	*/
}

ConfigDialog.prototype.exec = function()
{
	this.loadConfig();
	this.dialog.exec();
}

ConfigDialog.prototype.loadConfig = function()
{
	var generalTab = this.dialog.tabWidget.widget(0);
	generalTab.openLastWorkflowCheckBox.checked = config.openLastWorkflow;
	generalTab.openLastWorkflowCheckBox.setChecked(config.openLastWorkflow);

//	var index = generalTab.languageComboBox.findData(config.language);
//	generalTab.languageComboBox.setCurrentIndex(index);

	var sceneTab = this.dialog.tabWidget.widget(1);
	sceneTab.gridCheckBox.checked = config.grid;
	sceneTab.snapToGridCheckBox.checked = config.snapToGrid;
	sceneTab.antialiasingCheckBox.checked = config.antialiasing;
}

ConfigDialog.prototype.discard = function()
{
	this.loadConfig();
}

ConfigDialog.prototype.apply = function()
{
	var generalTab = this.dialog.tabWidget.widget(0);
	config.openLastWorkflow = generalTab.openLastWorkflowCheckBox.checked;

//	var index = generalTab.languageComboBox.currentIndex;
//	config.language = generalTab.languageComboBox.itemData(index);

	var sceneTab = this.dialog.tabWidget.widget(1);
	config.grid = sceneTab.gridCheckBox.checked;
	config.snapToGrid = sceneTab.snapToGridCheckBox.checked;
	config.antialiasing = sceneTab.antialiasingCheckBox.checked;

	this.configChanged.emit();
}

function WorkflowItemFactory(scene)
{
	var scene = scene;

	this.createStep = function(id) {

		var step = undefined;
		var stepType = undefined;

		switch (id) {
			case Tag.startStep:
			case WorkflowScene.StartStep:
				step = new StartStep(scene);
				stepType = WorkflowScene.StartStep;
				break;
			case Tag.documentEventStep:
			case WorkflowScene.DocumentEventStep:
				step = new DocumentEventStep(scene);
				stepType = WorkflowScene.DocumentEventStep;
				break;
			case Tag.usersStep:
			case WorkflowScene.UsersStep:
				step = new UsersStep(scene);
				stepType = WorkflowScene.UsersStep;
				break;
			case Tag.moveStep:
			case WorkflowScene.MoveStep:
				step = new MoveStep(scene);
				stepType = WorkflowScene.MoveStep;
				break;
			case Tag.notificationStep:
			case WorkflowScene.NotificationStep:
				step = new NotificationStep(scene);
				stepType = WorkflowScene.NotificationStep;
				break;
			case Tag.archiveStep:
			case WorkflowScene.ArchiveStep:
				step = new ArchiveStep(scene);
				stepType = WorkflowScene.ArchiveStep;
				break;
			case Tag.scriptStep:
			case WorkflowScene.ScriptStep:
				step = new ScriptStep(scene);
				stepType = WorkflowScene.ScriptStep;
				break;
//			case Tag.genericEventStep:
//			case WorkflowScene.GenericEventStep:
//				step = new GenericEventStep(scene);
//				stepType = WorkflowScene.GenericEventStep;
				break
			case Tag.gatewayStep:
			case WorkflowScene.GatewayStep:
				step = new GatewayStep(scene);
				stepType = WorkflowScene.GatewayStep;
				break;
			case Tag.stopStep:
			case WorkflowScene.StopStep:
				step = new StopStep(scene);
				stepType = WorkflowScene.StopStep;
				break;
			default:
		}

		if (step != undefined) {
			step.setData(WorkflowScene.ItemType, WorkflowScene.Step);
			step.setData(WorkflowScene.ItemSubType, stepType);

			// generate id
			//step.setData(WorkflowScene.ItemId, ++this._currentId);

			//step.read(stream);

			//this.addWorkflowItem(step);

			//step.createUndoCommands();
		}

		return step;
	}

	this.createTransition = function(id, startItem, endItem) {

		var transition = undefined;
		var transitionType = undefined;

		switch (id) {
			case Tag.automaticTransition:
			case WorkflowScene.AutomaticTransition:
				transition = new AutomaticTransition(startItem, endItem, scene);
				transitionType = WorkflowScene.AutomaticTransition;
				break;
			case Tag.conditionalTransition:
			case WorkflowScene.ConditionalTransition:
				transition = new ConditionalTransition(startItem, endItem, scene);
				transitionType = WorkflowScene.ConditionalTransition;
				break;
			case Tag.timeoutTransition:
			case WorkflowScene.timeoutTransition:
				transition = new TimeoutTransition(startItem, endItem, scene);
				transitionType = WorkflowScene.timeoutTransition;
				break;
			case Tag.errorTransition:
			case WorkflowScene.ErrorTransition:
				transition = new ErrorTransition(undefined, undefined, this);
			transitionType = WorkflowScene.ErrorTransition;
				break;
			default:
		}

		if (transition != undefined) {
			// set transition type id
			transition.setData(WorkflowScene.ItemType, WorkflowScene.Transition);
			transition.setData(WorkflowScene.ItemSubType, transitionType);

			// generate id
			//transition.setData(WorkflowScene.ItemId, ++this._currentId);
/*
			transition.read(stream);

			if (transition._startItemName == undefined || transition._startItemName == "" || transition._endItemName == undefined || transition._endItemName == "")
				continue;

			var startItem = this.workflowItemByName(transition._startItemName);
			var endItem = this.workflowItemByName(transition._endItemName);

			if (startItem == undefined || endItem == undefined)
				continue;

			// transition
			transition.setStartItem(startItem);
			transition.setEndItem(endItem);

			// add transition to steps
			startItem.addTransition(transition);
			endItem.addTransition(transition);

			// add to scene
			this.addWorkflowItem(transition);

			// refresh positioning
			transition.updatePosition();

			transition.createUndoCommands();*/
		}

		return transition;
	}
}

function CodeEditor(parent)
{
	QsciScintilla.call(this, parent);

	this.setMarginType(0, QsciScintilla.NumberMargin);
	this.setMarginLineNumbers(0, true);
	this.setMarginType(1, QsciScintilla.SymbolMargin);
	this.setMarginSensitivity(1, true);
	this.setMarginWidth(1, 24);

	this.setCaretLineVisible(true);

	this.setIndentationGuides(true);

	this.setAutoCompletionSource(QsciScintilla.AcsAll);
	this.setAutoCompletionReplaceWord(true);
	this.setAutoCompletionThreshold(2);
	this.setAutoCompletionCaseSensitivity(false);

	this.setFolding(QsciScintilla.BoxedTreeFoldStyle, 2);

	this.setWrapVisualFlags(QsciScintilla.WrapFlagByText);

	this.setBraceMatching(QsciScintilla.SloppyBraceMatch);

	this.setCallTipsVisible(true);
	this.setCallTipsStyle(QsciScintilla.CallTipsContext);

	//this.setWhitespaceVisibility(QsciScintilla.WsInvisible);

	this.setUtf8(true);

	this.linesChanged.connect(this, this.handleLinesChanged);
	this.handleLinesChanged();

	var lexer = new QsciLexerPascal();

	var font = new QFont();
	if (system.os == "windows")
		font.setFamily("courier");
	else
		font.setFamily("monospace");
	font.setPointSize(10);

	lexer.setFont(font, -1);

	this.setLexer(lexer);
}

CodeEditor.prototype = new QsciScintilla();

CodeEditor.prototype.handleLinesChanged = function()
{
	this.setMarginWidth(0, ("_" + this.lines()));
}

CodeEditor.prototype.event = function(event)
{
	// According to https://bugreports.qt.nokia.com/browse/QTBUG-12495
	// this might not work on mac
	if (event.type() == QEvent.ShortcutOverride) {
		if (!this.isReadOnly()) {
			var ke = event.cast("QKeyEvent");
			// Override code taken from qtextcontrol.cpp
			if (ke.modifiers() == Qt.NoModifier
				|| ke.modifiers() == Qt.ShiftModifier
				|| ke.modifiers() == Qt.KeypadModifier) {
				if (ke.key() < Qt.Key_Escape) {
					ke.accept();
				} else {
					switch (ke.key()) {
						case Qt.Key_Return:
						case Qt.Key_Enter:
						case Qt.Key_Delete:
						case Qt.Key_Home:
						case Qt.Key_End:
						case Qt.Key_Backspace:
						case Qt.Key_Left:
						case Qt.Key_Right:
						case Qt.Key_Up:
						case Qt.Key_Down:
						case Qt.Key_Tab:
						ke.accept();
					default:
						break;
					}
				}
			} else if (ke.matches(QKeySequence.Copy)
						|| ke.matches(QKeySequence.Paste)
						|| ke.matches(QKeySequence.Cut)
						|| ke.matches(QKeySequence.Redo)
						|| ke.matches(QKeySequence.Undo)
						|| ke.matches(QKeySequence.MoveToNextWord)
						|| ke.matches(QKeySequence.MoveToPreviousWord)
						|| ke.matches(QKeySequence.MoveToStartOfDocument)
						|| ke.matches(QKeySequence.MoveToEndOfDocument)
						|| ke.matches(QKeySequence.SelectNextWord)
						|| ke.matches(QKeySequence.SelectPreviousWord)
						|| ke.matches(QKeySequence.SelectStartOfLine)
						|| ke.matches(QKeySequence.SelectEndOfLine)
						|| ke.matches(QKeySequence.SelectStartOfBlock)
						|| ke.matches(QKeySequence.SelectEndOfBlock)
						|| ke.matches(QKeySequence.SelectStartOfDocument)
						|| ke.matches(QKeySequence.SelectEndOfDocument)
						|| ke.matches(QKeySequence.SelectAll)
						|| ke.matches(QKeySequence.Delete) // added
						) {
				ke.accept();
			}
		}
	}
	return QsciScintilla.prototype.event.call(this, event);
}

