rules = ACL_proxy.rules
"""
Class User manages all about users
"""
class User:

	__thread_local = threading.local()
	rules = rules
	big_value = 500000000L

	def __init__(self, row=None):
		if row:
			self.id 		= row[0]
			self.guid 		= row[1]
			self.password 	= row[2]
			self.email 		= row[3]
			self.picture 	= row[4]
			self.first_name = row[5]
			self.last_name 	= row[6]
			self.keywords 	= row[7]
			self.acl		= ACL_proxy()
			self.acl.is_admin = True if self.acl.has_access(
				self.guid, application.id, ACL_proxy.rules["ADMIN"]
			) else False
		else:
			self.id 		= -1
			self.guid 		= ""
			self.password 	= ""
			self.email 		= ""
			self.picture 	= ""
			self.first_name = ""
			self.last_name 	= ""
			self.keywords 	= ""
			self.acl		= ACL_proxy()

	def __str__(self):
		return self.email

	def __eq__(self, other):
		return self.id == other.id and self.email == other.email

	def __hash__(self):
		return self.id

	@property
	def max_documents(self):
		from utils.card_connect import send_to_card_and_wait
		result = send_to_card_and_wait(
			"getlicense %s %s" % (application.id, "104"),
			"%s/%s" % (application.id, "104")
		)
		if result == "None":
			import localization_en
			from indexers import normalize
			response.write(normalize(localization_en.localization_dict["NO_LICENSE_ON_SMART_CARD"]))
		if isinstance(result, str) and int(result) == 0: result = self.big_value
		return int(result) if result not in [None, "None"] else 0 #int(str(5)+str(0))

	@property
	def max_users(self):
		from utils.card_connect import send_to_card_and_wait
		result = send_to_card_and_wait(
			"getlicense %s %s" % (application.id, "103"),
			"%s/%s" % (application.id, "103")
		)
		if result == "None":
			import localization_en
			from indexers import normalize
			#loc = localization.get_lang()
			response.write(normalize(localization_en.localization_dict["NO_LICENSE_ON_SMART_CARD"]))
		if isinstance(result, str) and int(result) == 0: result = self.big_value
		return int(result) if result not in [None, "None"] else 0 #int(str(5)+str(0))

	@property
	def current_users(self):
		return len(User.get_all())

	@classmethod
	def get_by_id(self, id):
		query = """SELECT id, guid,  password, email, picture, first_name, last_name, keywords
					FROM `user`
					WHERE `id` = ?"""
		user_row = Database.maindb().fetch_one(query, (str(id),))
		return user_row and User(user_row)

	@classmethod
	def get_by_guid(self, guid):
		query = """SELECT id, guid,  password, email, picture, first_name, last_name, keywords
					FROM `user`
					WHERE `guid` = ?"""
		user_row = Database.maindb().fetch_one(query, (str(guid),))
		return user_row and User(user_row)

	@classmethod
	def get_by_email(self, email):
		email = str(email).lower()
		query = """SELECT id, guid,  password, email, picture, first_name, last_name, keywords
					FROM `user`
					WHERE email = ?"""
		user_row = Database.maindb().fetch_one(query, (str(email),))
		return user_row and User(user_row)

	@classmethod
	def logoff(self):
		from helper import ssn_scalar
		user = ssn_scalar("user")#session.get("user")
		if user: del session["user"]


	@classmethod
	def login(self, email, password):
		email = email.lower()
		try:
			query = """SELECT id, guid,  password, email, picture, first_name, last_name, keywords
						FROM `user`
						WHERE email= ?  and password= ?"""
			row = Database.maindb().fetch_one( query, (email, password) )
			if row is None:
				return None
			user = User(row)

			session["user"] = user
			return session["user"]
		except:
			raise
			return None


	@classmethod
	def get_all(self):
		#user  = User.current()
#		if user:
		query = """SELECT id, guid,  password, email, picture, first_name, last_name, keywords
					FROM `user`
					ORDER BY email"""
		rows = Database.maindb().fetch_all( query, )

		return [User(row) for row in rows]
#		return []

	@classmethod
	def current(self):
		s_user = session.get("user")
		if s_user:
			return User.get_by_guid(s_user.guid)

#			if getattr( self.__thread_local, "current_user", None ) is None:
#				self.__thread_local.current_user = self.get_by_guid(s_user.guid)
#			return self.__thread_local.current_user
		else:
			return None

	@classmethod
	def admin_exists(self):
		query = """
					SELECT user.guid, user.email
					FROM `user`
					WHERE user.email IN
					(SELECT config.value FROM `config`, `acl`
					WHERE config.item=acl.rule)"""
		row = Database.maindb().fetch_one( query, )
		return row# and User(row)

	@classmethod
	def current_admin(self):
		query = """SELECT value
					FROM `config`
					WHERE item='admin'"""
		admin_row = Database.maindb().fetch_one(query,)

		if admin_row:
			admin_usr = User.get_by_email(admin_row["value"])

		return admin_usr or None

	def save(self,):

		from utils.uuid import uuid4
		self.__validate_email()
		if self.id == -1:
			if self.max_users < self.current_users + 1:
				raise Exception( "MAXIMUM_USERS_LIMIT" )
			query = """SELECT *
						FROM `user`
						WHERE email= ? """
			row = Database.maindb().fetch_one( query, (self.email,) )
			if row :
				raise Exception("USER_EXISTS")
			self.guid =  str(uuid4())

			query = """INSERT INTO user(email, password, picture, first_name, last_name, keywords, guid)
						VALUES(?,?,?,?,?,?,?)"""
		else:
			query = """SELECT *
						FROM `user`
						WHERE email= ? """
			row = Database.maindb().fetch_one( query, (self.email,) )
			if row and row['id'] != self.id:
				raise Exception("USER_EXISTS")

			query = """UPDATE user
						SET email=?, password=?, picture=?, first_name=?, last_name=?, keywords=?
						 WHERE guid=?	"""

		row = Database.maindb().commit( query,(self.email, self.password, self.picture,
				self.first_name, self.last_name, self.keywords, self.guid))

		if not self.is_admin(): return
		query = """UPDATE config
					SET `value`=?
				 	WHERE item='admin'	"""
		row = Database.maindb().commit( query,( str(self.email),))

	def update_email(self):
		query = """SELECT *
						FROM `user`
						WHERE email= ? """
		row = Database.maindb().fetch_one( query, (self.email,) )
		if row and row['guid']!=self.guid :
			raise Exception("USER_EXISTS")

		query = """UPDATE user
					SET email=?
				 	WHERE guid=?	"""
		row = Database.maindb().commit( query,( self.email, self.guid))

		if not self.is_admin(): return

		query = """UPDATE config
					SET `value`=?
				 	WHERE item='admin'	"""
		row = Database.maindb().commit( query,( str(self.email),))


	def save_new_password(self):
		query = """UPDATE user
					SET password=?
				 	WHERE guid=?	"""
		row = Database.maindb().commit( query,( self.password, self.guid))


	def __validate_email(self):
		self.email =  self.email.lower()

		match = re.search("[a-z0-9._%+-]+@(?:[a-z0-9-]+\.)+[a-z]{2,4}", self.email)
		if not match:
			raise Exception("ERROR_EMAIL_INVALID")

		query = """SELECT *
					FROM `user`
					WHERE email= ? """
		row = Database.maindb().fetch_one( query, (self.email,) )

		if row :
			pass
			#raise Exception("USER_EXISTS")



	def delete(self,):
		# old school
		#for acl in ACL.get_by_subject(self.guid):
		#	acl.delete()

		ACL_record.delete( subject=self.guid )

		query = """DELETE FROM user_in_group
					WHERE user_guid=? """
		Database.maindb().commit(query, (str(self.guid),))

		query = """DELETE FROM user
					WHERE guid=? """
		Database.maindb().commit(query, (self.guid,))


	def get_groups(self):
		from class_group import Group
		return Group.get_user_groups(self.guid)


	def add_to_group(self, guid):
		from class_group import Group
		group = Group.get_by_guid(guid)
		if group:
			group.add_user(self.guid)


	def delete_from_group(self, guid):
		from class_group import Group
		group = Group.get_by_guid(guid)
		if group:
			group.delete_user(self.guid)

	def __delete_all_groups(self):
		query = """DELETE FROM user_in_group
						WHERE user_id=? """
		rows = Database.maindb().commit( query,(str(self.id)))


	def is_admin(self):
		return True if self.acl.is_admin else False

	#"checks if user has access to the object_guid with rule input"#
	def can(self, rule, object_guid):
		# admin check (if user is admin, nothing else need to check)
		if self.is_admin():
			return True

		# normal acl check
		subj_guids = [self.guid.encode("utf8")]
		groups = self.get_groups()
		for g in groups:
			subj_guids.append(g.guid.encode("utf8"))

		return self.acl.has_access(subj_guids, object_guid, rule)


	def get_documents_shares(self, folder, page=-1):
		if folder and self.can(ACL_proxy.rules["READ"], folder.guid):
			return folder.get_document_shares(self, page)
		return []

	def get_subfolders(self, folder, page=-1):
		if folder:
			if self.is_admin():
				return folder.get_children(page)
			elif self.can(ACL_proxy.rules["READ"], folder.guid):
				return folder.get_children_for_user(self, page)
		return []

	def get_top_folders(self,page=-1):
		return Folder.get_top_for_user(self, page)

	def get_file_shares(self):
		return [top_dir for top_dir in ShareDir.top_folders() if self.can(ACL_proxy.rules["READ"], top_dir.guid)]

	def get_smart_folders(self):
		from class_smart_folder import SmartFolder
		smart_folders = SmartFolder.all()
		if self.is_admin():
			return smart_folders
		else:
			return [folder for folder in smart_folders if self.can(ACL_proxy.rules["READ"], folder.guid)]

	def can_edit_file_share(self, file_share):
		return self.can(rules["USER_ADMIN"], file_share.guid)

	def can_delete_file_share(self, file_share):
		return self.can(rules["USER_ADMIN"], file_share.guid)

	def can_delete_document_share(self, doc_share):
		return self.can(rules["USER_ADMIN"], doc_share.guid)

	def can_edit_document_share(self, doc_share):
		return self.can(rules["USER_ADMIN"], doc_share.guid)

	def can_read_share(self, share):
		return self.can(rules["READ"], share.guid)

	def has_admin_access_into(self, share):
		return self.can(rules["USER_ADMIN"], share.guid)

	def has_write_access_into(self, share):
		return share and self.can(rules["WRITE"], share.guid)

	def can_delete_folder(self, folder):
		return self.can(rules["USER_ADMIN"], share.guid)

	def add_access_to(self, share, acl_type):
		if share:
			acl = ACL_record()
			acl.subject = self.guid
			acl.object = share.guid
			acl.rule = acl_type
			acl.create()

	def delete_access_to(self, share, acl_type):
		if share:
			ACL_record.delete(subject=self.guid, object=share.guid,  rule=acl_type)

	def get_searches(self):
		from class_search import ISearch
		return [ search for search in ISearch.get_all() if self.can_read_share(search)]

	def generate_hashcode(self):
		from hashlib import sha224
		return sha224((self.guid + str(self.id) + self.password)).hexdigest()

	def get_name_from_email(self):
		return str(self.email).split("@")[0]

	def reset_password(self):
		import localization

		loc = localization.get_lang()
		domain = request.server.host.encode( "utf-8" )
		from_address = "eyeonfiles@" + domain

		to_address = self.email

		mail_theme = loc["mail_pass_0"]
		mail_text = """%(mail_pass_1)s, %(username)s! <br>
		%(mail_pass_2)s:<br><br>
		<a href="%(restore_link)s">%(restore_link)s</a><br><br>
		%(mail_pass_3)s!<br><br>
		%(mail_pass_4)s"""

		hash = self.generate_hashcode()
		username = self.get_name_from_email()
		reset_link =  request.server.host.encode("utf-8")
		guid = self.guid

		mail_text = mail_text % {
		"username" : username,
		"restore_link" : 	"http://" + reset_link +
							"/reset_password?resetid=" + hash +
							"&guid=" + guid,
		"mail_pass_1" : loc["mail_pass_1"],
		"mail_pass_2" : loc["mail_pass_2"],
		"mail_pass_3" : loc["mail_pass_3"],
		"mail_pass_4" : loc["mail_pass_4"]
		}

		server.mailer.send(from_address, to_address, mail_theme, mail_text)

		return None

	def set_as_admin(self):
		query = """SELECT value
					FROM `config`
					WHERE item='admin'"""
		admin_row = Database.maindb().fetch_one(query,)
		#raise Exception(str(admin_row))
		if admin_row:
			pass # update
			query = """UPDATE config
					SET `value`=?
				 	WHERE item='admin'	"""
			row = Database.maindb().commit( query,( str(self.email),))
		else:
			pass # insert
			query = """INSERT INTO config(item, value)
						VALUES('admin',?)"""
			Database.maindb().commit(query, (self.email,))

		#"put insert or update if exists"#
		self.save()

		acl = ACL_record()
		acl.subject = self.guid
		acl.object = application.id
		acl.rule = User.rules["ADMIN"]
		acl.save()

	def get_admined_searches(self):
		from class_search import ISearch
		return [ search for search in ISearch.get_all() if self.has_admin_access_into(search)]

