def save(self,): query = """SELECT * FROM acl WHERE acl.subject=? and acl.object=? and acl.rule=? """ rows = Database.maindb().fetch_all(query, (self.subject, self.object, self.rule,)) if rows: return
query = """INSERT INTO acl(subject, object, rule) VALUES(?,?,?)""" row = Database.maindb().commit( query,( self.subject, self.object, self.rule ))
def delete(self): query = """DELETE FROM acl WHERE id=? """ #Database.maindb().commit(query, (guid,)) row = Database.maindb().commit( query,( int(self.id),))
def delete_acl(self, subj_guid, obj_guid, acl_type): query = """DELETE FROM acl WHERE acl.subject=? and acl.object=? and acl.rule=? """ rows = Database.maindb().commit( query, (subj_guid, obj_guid, acl_type,))

