def put_document(self, filename, binary_data, meta={}, text=None): """ Creates a new document IN DS filename: unicode string binary_data: contents of the file (TODO: to be rewrited to file handler) meta: additional metafields provided by user text: indexed text of the document """ from class_user import User user = User.current() if user.max_documents < self.__search.get_doccount() + 1: raise Exception("MAXIMUM_DOCUMENTS_LIMIT") if ACL.current_user_can_write_object(self): self.validate_xapian_meta() from utils_old import filename_escape filename = normalize( filename_escape(filename) ) doc = Document(self.guid, filename) #category, 1,2,3 doc.save_file(binary_data) #db_id if text: doc.set_fulltext(text) #text else: doc.generate_fulltext() doc.set_meta( self.filter_meta(meta) ) with index_lock: self.__get_connector().add_file_with_metadata(doc.xapian_description())
return True
def meta_list(self): """List of metafields id's in xapian DB for this DS""" return [ str(metafield.id) for metafield in self.get_additional_metafields() ]
def meta_objects(self): """List of metafields id's in xapian DB for this DS""" return dict( [str(metafield.id), metafield] for metafield in self.get_additional_metafields() )
def filter_meta(self, meta): """filters meta dictionary, returns fields that match meta_list of this DS""" from utils_old import date2base meta_objects = self.meta_objects() return dict( [ (id, normalize( date2base(meta[id]) if meta_objects[id].type_id==2 else meta[id] )) for id in meta if id in meta_objects ] )
def validate_xapian_meta(self): """Check and create fileds of this DS in xapian DB""" with index_lock: Metafield.validate_autometa(self.__get_connector()) for metafield in self.get_additional_metafields(): metafield.validate()
