metafield.name = meta["name"] metafield.constraint = meta["constraint"] metafield.default = meta["default"] metafield.required = meta["required"] metafield.user_can_modify = meta["user_can_modify"] metafield.visible = meta["visible"] metafield.type_id = meta["type_id"] metafield.document_share_guid = doc_share.guid
if metafield: metafield.set_connector(connector) metafield.save() doc_share.add_metafield(metafield.id)
for doc in d_content[1]: pass
return ""
def set_content(content, cur_folder=""): if content: if content[0] == obj_folder: cur_folder = set_folder(content[1], cur_folder) for item in content[2]: set_content( item, cur_folder ) else: set_document_share(content[1], content[2], cur_folder)
return ""
### "init" ### from class_metafield import Metafield copy_temp = [] obj_folder, obj_share = "folder", "share"
### "making COPY template" ### for guid in from_guid: DS_object = Folder.get_by_guid(guid) # "if share is copied..." if not DS_object: # should be share DS_object = Document_share.get_by_guid(guid) copy_temp.append([ obj_share, get_attr(obj_share, DS_object), get_share_content(DS_object) ]) else: copy_temp.append( get_content(DS_object) )
### "making PASTE into to_guid folder " ### st = ""
for times in range(count): for top_object in copy_temp: set_content(top_object, Folder.get_by_guid(to_guid))
return

import html

from class_ACL import ACL
from class_ACL_proxy import ACL_proxy
from class_user import User, rules
from class_group import Group
