# L
# this would be localization mark - means "localizationed" :)
# even loc-independent functions will be marked with "# L"

class Html_wrapper:

	@classmethod # L
	def get_grops_form(self, share, page, parametrs="", share_type='F'):
		loc = localization.get_lang()
		user = User.current()
		user_isadmin = user.has_admin_access_into(share)
		cur_users	= avalible_groups= ""
		user_groups = []
		for acl in ACL.get_by_object(share.guid):
			group = Group.get_by_guid(acl.subject)
			if group:
				user_groups.append((group.name, group.guid, acl.id, acl.rule ))

		for a_group in  sorted(set(user_groups)):
			acl_type = a_group[3].lower()
			acl_type = loc[acl_type] if acl_type in loc else acl_type
			cur_users += html.get_grops_form_cur_users % { "group_name": a_group[0], "ACL_type":acl_type }

			if user_isadmin:
				cur_users += html.get_grops_form_delete_button % {"guid":share.guid, "group_guid":a_group[1],
				"page":page, "ACL_id":a_group[2], "parametrs":parametrs }

		user_groups =  [(group[0], group[1] ) for group in user_groups]
		all_group = [(group.name, group.guid ) for group in Group.get_all()]
		add_users = set(all_group) - set(user_groups)

		for a_group in sorted(set(add_users)):
			avalible_groups += html.get_grops_form_avalible_groups % {
				"guid":a_group[1], "email":a_group[0] }

		checkbox = ""
		if isinstance(share, Folder) or isinstance(share, ShareDir):
			checkbox = html.get_grops_form_checkbox % { "html_acl_setacl"	:loc["html_acl_setacl"] }

		rules = ""
		for rule in ACL_proxy.access_types[share_type.upper()]:
			rules += "<option value='%(rule)s'>%(title)s</option>" % {
				"rule" : rule.lower(), "title" : loc[rule]
			}
		add_button = "" if not user_isadmin else html.get_grops_form_button % {
			"html_acl_addng"	:loc["html_acl_addng"],
			"avalible_groups"	:avalible_groups,
			"rules"				:rules,
			"checkbox"			:checkbox,
			"add"				:loc["add"]
		}
		ret_data = html.get_grops_form_data % {
			"html_acl_groups"	:loc["html_acl_groups"],
			"cur_users"			:cur_users,
			"get_grops_form_button" : add_button
		}


		return ret_data


	@classmethod # L
	def get_users_form(self, share, page, parametrs="", share_type='F'):

		loc = localization.get_lang()
		user_temp = User.current()
		user_isadmin = user_temp.has_admin_access_into(share)
		cur_users	= avalible_users = ""
		share_users = []

		for acl in ACL.get_by_object(share.guid):
			user_ = User.get_by_guid(acl.subject)
			if user_:
				share_users.append((user_.email, user_.guid, acl.id, acl.rule ))

		for a_user in  sorted(set(share_users)):
			acl_type = a_user[3].lower()
			acl_type = loc[acl_type]if  acl_type  in loc else acl_type
			cur_users += html.get_users_form_cur_users % {
							"user_email": a_user[0], "ACL_type":acl_type
						}

			if user_isadmin and not user_temp.guid == a_user[1]:
				cur_users += html.get_users_form_delete_button % {
					"guid":share.guid,
					"user_guid":a_user[1],
					"ACL_id":a_user[2],
					"page":page, "parametrs":parametrs,
					"delete" : loc["delete"]
				}

		share_users =  [(a_user[0], a_user[1] ) for a_user in share_users]
		cur_admin = User.current_admin()
		all_users = [(usr.email, usr.guid) for usr in User.get_all() if not usr.guid == cur_admin.guid]
		add_users = set(all_users) - set(share_users)

		for a_user in sorted(set(add_users)):
			avalible_users += html.get_users_form_avalible_users % {"guid":a_user[1], "email":a_user[0] }

		checkbox = ""
		if isinstance(share, Folder) or isinstance(share, ShareDir):
			checkbox = html.get_users_form_checkbox % {
				"html_acl_setacl"	:loc["html_acl_setacl"]
			}

		add_button = ""
		rules = ""
		for rule in ACL_proxy.access_types[share_type.upper()]:
			rules += "<option value='%(rule)s'>%(title)s</option>" % {
				"rule" : rule.lower(), "title" : loc[rule]
			}
		add_button = html.get_users_form_button % {
			"html_acl_addnu" 	: loc["html_acl_addnu"], 	# Add new user,
			"avalible_users"	: avalible_users,
			"rules"				: rules,
			"checkbox"			: checkbox,
			"add" 				: loc["add"]
		}

		ret_data = html.get_users_form_data % {
			"html_acl_users"	: loc["html_acl_users"],   # Users,
			"cur_users"			: cur_users,
			"get_users_form_button" : add_button

		}



		return ret_data

	@classmethod
	def get_share_rename_form(self, doc):
		loc = localization.get_lang()
		result = u"""
		<form class='form' action="#" method="post">
			<h3 class="title">%(ds_change)s:</h3>
			<input name='share_rename' value="%(share_name)s" />
			<div class='form form2'>
          	<input type="submit" class="button" value='%(save)s' />
       		</div>

		</form>
		""" % {
		"save" : loc["save"],
		"share_name" : cgi.escape(doc.name),
		"ds_change" : loc["ds_change"]
		}
		return result

