	@classmethod # L
	def get_new_metafield_form(self, document_share, page, parent = '', folder = None):
		from class_metafield import Metas_type

		loc = localization.get_lang()

		metafields_list = u"""
			<table cellspacing="0" id="tabledocshare" class="doc-share">
			<tr class="hdr">
				<th class="left"><div></div></th>
				<th width='30%%'><div>%(mf_name)s</div></th>
				<th><div>%(mf_type)s</div></th>
				<th><div>%(mf_default)s</div></th>
				<th><div>%(mf_const)s</div></th>
				<th><div>%(mf_req)s</div></th>
				<th><div>%(mf_canmodify)s</div></th>
				<th><div>%(mf_visible)s</div></th>
				<th><div></div></th>
				<th class="right" colspan="2"><div></div></th>
			</tr>""" % {
			"mf_name" 		: loc["mf_name"],
			"mf_type" 		: loc["mf_type"],
			"mf_default" 	: loc["mf_default"],
			"mf_const" 		: loc["mf_const"],
			"mf_req" 		: loc["mf_req"],
			"mf_canmodify"	: loc["mf_canmodify"],
			"mf_visible"	: loc["mf_visible"]
			}

		avalible_shares = ""
		for metafield in document_share.get_metafields():
			delete_url = edit_url = ""
			if metafield.user_can_modify != "-1":
				delete_url = u""" <a href="/%(page)s?guid=%(guid)s%(parent)s&del_met=%(id)s" title='%(delete)s' class='adelete'><img src='/6ef08104-2be2-4474-a6e1-234bb75754b9.png' alt='%(delete)s' /></a>
					"""%{
					"page":page,"id":metafield.id, "guid":document_share.guid,
					"parent":parent, "delete" : loc["delete"]}
				edit_url = u""" <a title="%(edit)s" class="edit" href="#" metid="%(metid)s" type=%(m_type)s onclick="return showeditpropertydlg(this)"><img alt="%(edit)s" src="/5567fe98-efdc-4c45-adfa-5ecac8f9a999.res" /></a> """ % {
				"metid":metafield.id, "edit" : loc["edit"], "m_type" : metafield.type_id}


			required = loc["html_yes"] if  metafield.required == "1" else loc["html_no"]
			visible = loc["html_yes"] if  metafield.visible == "1" else loc["html_no"]
			user_can_modify = loc["html_yes"] if  metafield.user_can_modify == "1" else loc["html_no"]

			required_code = "1" if  metafield.required == "1" else "0"
			modify_code = "1" if  metafield.user_can_modify == "1" else "0"
			visible_code = "1" if  metafield.visible == "1" else "0"

			meta_name = loc[metafield.name]	if metafield.name in loc else metafield.name
			meta_type = Metas_type.get_by_id(metafield.type_id)
			if meta_type:
				type =	loc[meta_type.name] if meta_type.name in loc else meta_type.name
				mapped_folder = "(!)Archive not found(!)"
				if metafield.type_id == 5:
					fs_share = SmartFolder.get_by_guid(metafield.constraint)
					if fs_share:
						mapped_folder = cgi.escape(fs_share.name)


			metafields_list += u"""
			<tr>
			  <td></td>
			  <td>%(name)s</td>
			  <td>%(type)s</td>
			  <td>%(default)s</td>
			  <td>%(constraint)s</td>
			  <td rel='%(required_code)s'>%(required)s</td>
			  <td rel='%(modify_code)s'>%(user_can_modify)s</td>
			  <td rel='%(visible_code)s'>%(visible)s</td>
			  <td>%(delete_url)s</td>
			  <td>%(edit_url)s</td>
 			</tr>""" % {
					"name"				: cgi.escape(meta_name),
					"delete_url"		: delete_url,
					"type"				: type,
					"default"	 		: cgi.escape(metafield.default),
					"constraint" 		: cgi.escape(metafield.constraint)  if metafield.type_id != 5 else """
						<span class="arch_id" style="display: none">%s</span><img align="absmiddle" src='/c3871dcd-d73a-469e-a1ba-663c2ac51bab.png'> %s
					""" % (cgi.escape(metafield.constraint), mapped_folder),
					"required"			: required,
					"user_can_modify"	: user_can_modify,
					"required_code"		: required_code,
					"modify_code"		: modify_code,
					"visible_code"		: visible_code,
					"visible"			: visible,
					"edit_url"			: edit_url
				}

		metafields_list += u"</table>"


		mtypes = ""
		for mtype in Metas_type.get_all():
			selected = ""
			regexp =  mtype.regexp.encode("UTF-8")
			descr = mtype.description

			meta_descr = loc[descr] if descr in loc else descr
			meta_name = loc[mtype.name]	if mtype.name in loc else mtype.name

			if mtype.name == "mf_string":
				selected = " selected "
			mtypes +=  u"""<option value='%(value)s' %(selected)s  title='%(descr)s' regexp='%(regexp)s'>%(name)s</option>
					"""%{"value":mtype.id, "name":meta_name, "selected":selected, "descr":meta_descr, "regexp":regexp+"|(^$)"}

		archives = ""
		user = User.current()
		if folder:
			for share in SmartFolder.all():
				if user.can(user.rules["READ"], share.guid):
					archives += u"""<option value='%(value)s' title='%(title)s'>%(title)s</option>""" % {
						"value" : share.guid,
						"title" : share.name
					}


		ret_data = html.new_meta_to_doc_share%{
			"metafields_list": metafields_list, "avalible_shares": avalible_shares,
			"guid": document_share.guid, "parent": parent, "mtypes":mtypes,
			"archives"	: archives,
			"html_ds_prop" 		: loc["html_ds_prop"],
			"html_add_prop" 		: loc["html_add_prop"],
			"html_add_newprop" 	: loc["html_add_newprop"],
			"html_edit_newprop" 	: loc["html_edit_newprop"],
			"html_name" 			: loc["html_name"],
			"html_type" 			: loc["html_type"],
			"html_const" 			: loc["html_const"],
			"html_default" 		: loc["html_default"],
			"html_use_sep" 		: loc["html_use_sep"],
			"html_required" 		: loc["html_required"],
			"html_usercanmod" 		: loc["html_usercanmod"],
			"html_visible" 		: loc["html_visible"],
			"html_viewshare" 		: loc["html_viewshare"],
			"html_yes" 				: loc["html_yes"],
			"html_no" 				: loc["html_no"],
			"html_archive"		: loc["html_archive"],
			"edit" 					: loc["edit"],
			"add" 					: loc["add"]
		}

		return ret_data

	@classmethod # L
	def get_back_url(self, folder):
		loc = localization.get_lang()
		f_res = u"  "
		if folder:
			f_res +=  u""" /  <a href="/folder.vdom?guid=%(guid)s">%(name)s</a>  """%{"guid":folder.guid, "name":cgi.escape(folder.name)}
			parent =  folder.get_parent()

			f_res += Html_wrapper.get_back_url(parent) if parent else  u"""/  <a href="/Documents_share.vdom">%(ds_title)s</a>  """ % {	"ds_title"	: loc["ds_title"]	}
		else:
			f_res +=  u"""/  <a href="/Documents_share.vdom">%(ds_title)s</a>  """ % {
			"ds_title" 			: loc["ds_title"] #Documents share
			}
		return  f_res


	@classmethod # L
	def search(self, doc_share, parent, search):
		loc = localization.get_lang()
		return u"%(s_result)s!" % {"s_result" : loc["s_result"]}


	"""guid_map -> takes guid of folder/share and convert it into link of DS info."""
	@classmethod # L
	def get_title(self, title = "", backpath = "", editurl="", guid=""):
		loc = localization.get_lang()
		guid_map = Html_wrapper.get_guid_map(guid, loc["header_sitemap"]) if guid else ""
		if editurl != "":
			editurl =  u"""<a href='%(editurl)s' class='aedit' title='%(edit)s'
				><img src='/08244397-1e56-48a2-8bdf-4a66733f70bc.res' alt='%(edit)s' /></a>"""%{
				"editurl":editurl, "edit" : loc["edit"]
			}
		result =  u"""<div class='current-folder'
			><span>%(title)s %(edit_url)s</span> %(backpath)s %(guid_map)s</div> """%{
			"title": title, "backpath": backpath,
			"edit_url":editurl, "guid_map" : guid_map
		}
		return result

	@classmethod # L
	def get_title_search(self, title = "", backpath = ""):
		return u"""<div class='current-search'
			><span>%(title)s</span> %(backpath)s</div>""" % {
			"title": title, "backpath": backpath
		}

	"""
		if guid == "" -> it's a root folder
		else some folder.
	"""
	@classmethod
	def get_guid_map(self, guid="", title="Site map"):
		link = "/Tree"
		if guid:
			link += "?map=" + guid
		else:
			link += "?map"
		result = u"""<br /><a href='%(link)s' class='guid_map' title='%(title)s'
			> %(title)s</a>""" % {
			"title" : title, "link" : link
		}
		return result

	#def get_search_result(self, document_share, parent, search, is_custom_search=False, searchmethod="1"):
	@classmethod
	def get_search_result(self, document_share_list,  parent, search,
		sortby=None,searchmethod=None, max_docs=5, table_num=0):
		import utils_old
		from class_metafield import Metafield

		args = request.arguments
		loc = localization.get_lang()

		startrank = 0
		cur_page = 1
		items  = StringIO()
		res = ""

		if isinstance(document_share_list, Document_share ):
			document_share = document_share_list

		additional_metafields = []
		additional_metafields = list(additional_metafields) + list(meta.id for meta in document_share.get_additional_metafields())
		additional_metafields.append("text")

		""" get documents rank """


		if document_share.guid in args:
			cur_page = int(args[document_share.guid])
			startrank = (cur_page- 1) * max_docs

		""" get documents and them count """

		if searchmethod == None:
			""" simple view """
			document_share_documents, all_doc_count = document_share.custom_search(search, startrank, startrank+max_docs, sortby=sortby)
			is_custom_search=False
		elif searchmethod == "4":
			""" custom search  """
			document_share_documents, all_doc_count = document_share.custom_search(search, startrank, startrank+max_docs, sortby=sortby)
			is_custom_search=True
		else:
			""" simple search  """
			document_share_documents, all_doc_count = document_share.simple_search(search, searchmethod, startrank, startrank+max_docs, sortby=sortby )
			is_custom_search=False

		doc_counter  = startrank
		user = User.current()
		user_can_edit_share = user.has_write_access_into(document_share)

		""" values for each document
		"""
		for doc in document_share_documents:
			doc_counter += 1

			""" fill file name, creation date and autor values
			"""
			onerow = u"""<td>%(doc_count)s</td><td><input type='checkbox' name='%(db_id)s'/></td>
						<td><div><a href="/Document_share.vdom?guid=%(guid)s&parent=%(parent)s&download_doc=%(db_id)s"
						class="type-%(ftype)s" target="_blank">%(name)s</a>
						</div></td>"""%{
							"doc_count":str(doc_counter),
							"guid":document_share.guid,
							"name":doc.filename(),
							"db_id":doc.guid(),
							"parent":parent.guid,
							"ftype":doc.icon_class()
						}
			onerow += u"<td>%s</td>"%doc.created_date()
			onerow += u"<td>%s</td>"%doc.author()

			cols = 5
			meta_exists = False
			""" fill addition values
			"""
			for meta in additional_metafields:
				meta_is_date  = meta_is_folder =  False
				metafild = Metafield.get_by_id(str(meta))
				if metafild:
					if metafild.user_can_modify == '1':
						meta_exists = True
					if metafild.type_id == 2:
						meta_is_date = True
					if metafild.type_id == 5:
						meta_is_folder = True
				meta = str(meta)
				restext = ""
				cols += 1
				meta_folder = ""

				if meta in doc.meta() :
					meta_folder = "1"
					value = doc.meta()[meta]
					if meta_is_date:
						value = utils_old.date2norm(value) #datetime.datetime.strptime(value, "%Y-%m-%d").strftime("%d-%m-%Y")
					elif meta_is_folder:
						ds_share = SmartSubFolder.get_by_guid(value)
						if ds_share:
							share_name = cgi.escape(ds_share.name)
							meta_folder = u"""
								<div title='%s'>
									<a style="text-decoration: none !important;" href="/Search.vdom?smart=%s">
									<img align="absmiddle" src='/22807889-14f2-4c94-a8b1-d4b45c3f38f8.png'/> %s</a>
								</div>
							""" % (share_name, value, share_name)
						else:
							meta_folder = u"<img src='/b52fcbf2-3dd6-43f2-aade-ffb73604657e.png' title='%s'>"%loc["html_nosub_found"]

					onerow += u"<td>%s</td>"% (utils_old.html_escape(value) if not meta_is_folder else meta_folder)
				elif metafild and metafild.type_id == 5:
					meta_folder = "<img src='/b52fcbf2-3dd6-43f2-aade-ffb73604657e.png' title='%s'>" % loc["html_nosub_assign"]
					onerow += u"<td>%s</td>" % meta_folder
				else:
					onerow += u"<td></td>"


			if meta_exists and user_can_edit_share:
				onerow += u"""<td><a href='#' class='metka-ed-srch' guidshare='%(guid)s' guiddoc='%(editguid)s' title='Edit'></a></td>
							"""%{"guid":document_share.guid,"parent":parent.guid,"editguid":doc.guid()}
			else:
				onerow += u"""<td></td>"""

			restext =  u"""%(text)s"""%{"text":doc.fulltext()}
			if restext != "":
				onerow += u"""</tr><tr class='line'><td></td><td colspan='%(cols)s' class='sres'>%(t)s</td></tr>"""%{"cols": cols, "t": restext}
				drawline = u""
			else:
				drawline = u"class='line'"

			items.write( u"<tr valign='top' %(line)s><td></td>%(onerow)s</tr>"%{"line": drawline, "onerow": onerow} )


		search_res = ""
		items_str = items.getvalue()
		items.close()
		if items_str != "":
			""" header of documents share
			"""

			""" file name, creation date and autor  """
			metas =  u"<th><div>%(fs_filename)s</div></th>"%{"fs_filename":loc["fs_filename"]}
			metas += u"<th><div class='metka' title='%(name)s'>%(name)s</div></th>"%{"name":loc["mf_auto_cd"]}
			metas += u"<th><div class='metka' title='%(name)s'>%(name)s</div></th>"%{"name":loc["mf_auto_crtr"]}

			""" additional metafields """
			for meta in additional_metafields:
				metafild = Metafield.get_by_id(str(meta))
				if metafild:
					meta_name = loc[metafild.name] if metafild.name in loc else metafild.name
					metas += u"<th><div class='metka' title='%(name)s'>%(name)s</div></th>"%{"name":cgi.escape(meta_name)}

			search_res = html.search_result_form % {
				"metas": metas, "items": items_str, "guid": document_share.guid,
				"tabledocshare" : "tabledocshare_"+str(table_num),
				"formdocshare" : "formdocshare_"+str(table_num)
			}


		if searchmethod is None or items_str != "":
			""" toolbar"""
			toolbar =  self.get_toolbar(document_share, user_can_edit_share)
			""" paginator """
			if all_doc_count >= 2147483648:
				all_doc_count = 0

			pages_count = 	(all_doc_count/max_docs) + (1 if all_doc_count%max_docs >0 else 0)
			if is_custom_search or searchmethod is None:
				paginator = html.paginator2%{	"current_page": cur_page, "pages_count": pages_count,
							"guid": document_share.guid, "is_custom_search": is_custom_search,
							"doc_count":all_doc_count  , "html_acl_docs" : loc["html_acl_docs"],
							"more" : loc["more"]}
			else:
				paginator = html.paginator%{	"current_page": cur_page, "pages_count": pages_count,
							"guid": document_share.guid, "is_custom_search": is_custom_search,
							"doc_count":all_doc_count  , "html_acl_docs" : loc["html_acl_docs"],
							"more" : loc["more"]}


			res = html.document_share_form %{	"toolbar": toolbar, "paginator": paginator,
												"search_res":search_res}
		return  res

	@classmethod
	def get_toolbar(self, document_share, user_can_edit_share, is_add_file=1):
		user = User.current()
		dis_del = "" if user.can(rules["USER_ADMIN"], document_share.guid) else " dis"
		loc = localization.get_lang()
		dis = "" if user_can_edit_share else " dis"
		if is_add_file:
			add_file_button = u"""<a href='#shareaddformwrapres-%(uniq)s' class='tooladd %(dis)s' title="%(fs_addfile)s">%(fs_addfile)s</a> """ % {
				"fs_addfile" 	: loc["fs_addfile"],
				"uniq"			: document_share.guid,
				"dis" 			: dis,
			}
		else:
			add_file_button = ""

		return html.document_share_toolbar % {
			"metafields"	: Html_wrapper.get_add_new_doc_form(document_share),
			"uniq"			: document_share.guid,
			"add" 			: loc["add"],
			"dis" 			: dis,
			"dis_del"		: dis_del,
			"add_file_button" : add_file_button,
			"fs_add" 		: loc["fs_add"], 		# Add
			"fs_delete" 	: loc["fs_delete"], 	# Delete
			"fs_move" 		: loc["fs_move"], 		# Move
			"fs_copy" 		: loc["fs_copy"], 		# Copy
			"fs_download" 	: loc["fs_download"], 	# Download
			"fs_mark" 		: loc["fs_mark"], 		# Mark
			"fs_print" 		: loc["fs_print"], 	# Print
			"fs_email" 		: loc["fs_email"]  	# Email
			}


