	@classmethod # L
	def get_big_search_form(self, guid = "", parent = "", search="", checked=1):
		loc = localization.get_lang()
		checked1 = checked2 = checked3 = ""
		checked = str(checked)

		html_str_checked = u"checked='checked'"
		if checked == "2":
			checked2 = html_str_checked
		elif checked == "3":
			checked3 = html_str_checked
		else:
			checked1 = html_str_checked


		guid_url = u"""<input type='hidden' name='guid' value='%(guid)s' />"""%{"guid":guid} if guid != "" else u""

		if parent != "":
			parent = u"""<input type='hidden' name='parent' value='%(parent)s' />"""%{"parent":parent}

