		}
		return result



	@classmethod # L
	def get_custom_search_result(self,  search, f_search, f_connector):
		from class_user import User
		from class_folder import Folder

		user = User.current()
		res = ""
		def get_data_for_doc_share(doc_share, parent, x=0):
			share_res = ""
			doc_share.set_connector(f_connector)
			doc_share.set_search(f_search)

			search_get_query = search.get_query()
			if len(search_get_query) == 0:
				return share_res

			s_res =  Html_wrapper.get_search_result(
				doc_share, parent, search_get_query, searchmethod="4", table_num=x, sortby='-2')

			#s_res = Html_wrapper.get_search_result(s_doc_share, a_folder, search)
			if s_res != "":
				share_url = u"""<a href='Document_share.vdom?guid=%(guid)s&parent=%(parent)s' name='%(guid)s'>%(name)s</a>
							"""%{"guid":doc_share.guid, "parent":parent.guid, "name":doc_share.name }
				back =   Html_wrapper.get_back_url(parent)
				share_res += Html_wrapper.get_title_search(share_url, back) + s_res
			return share_res

		def get_data_for_folder(a_folder, x=0):
			folder_res = ""
			if a_folder:
				for s_doc_share  in user.get_documents_shares(a_folder):
					#x += 1
					folder_res += get_data_for_doc_share(s_doc_share, a_folder, x+1)

				for s_folder  in user.get_subfolders(a_folder):
					#x += 1
					folder_res += get_data_for_folder(s_folder, x+1)
			return folder_res

		for obj in search.get_objects():
			if obj.parent_guid !="":
				folder = Folder.get_by_guid(obj.parent_guid)
				doc_share = Document_share.get_by_guid(obj.object_guid)
				if doc_share and folder :
					res += get_data_for_doc_share(doc_share, folder, 0)
			else:
				folder = Folder.get_by_guid(obj.object_guid)
				if	folder:
					res += get_data_for_folder(folder, 0)
		return res

