				<div style='display: none'>%s</div>""" % Html_wrapper.get_custom_search_form(search)

	@classmethod # L - almost
	def get_custom_search_form(self, search, params={}, is_edit=True):
		from utils.uuid import uuid4
		loc = localization.get_lang()


		def get_text(name, value, regexp, valid=True, descr="", is_date=False):

			err_str = u""  if valid else	u"class='error'"
			date = u"fdate" if is_date else ""	 # IS NOT WORK

			ret = u"""<input name='%(name)s' class='vtip %(date)s' value='%(val)s' regexp='%(regexp)s' %(err_str)s title='%(title)s' %(date)s />	"""%{
					"name":name, "val":value,
					"regexp":regexp, "err_str":err_str,
					"title":descr, "date":date}
			return ret

		def get_selector(name, options, selected_value, targ=""):
			rr = ret = ""
			#if len(options) > 2:

			ch = u""" onchange="subsel('%(field)s',this)" """%{ "field": targ } if targ != "" else ""

			#ret = "<select name='%(name)s' >"%{ "name":name, "ch":ch }
			#raise Exception(str(selected_value))
			for option in options:
				opt0 = option[0]
				sel = u" selected " if opt0 == selected_value else ""
				rr +=	 u"""<option value='%(val)s' %(sel)s>%(name)s</option>"""%{"val":option[0], "name":option[1], "sel":sel }

			ret += u"""<select style='width:100px;' name='%(name)s' %(ch)s>%(rr)s</select>"""%{ "name":name, "ch":ch, "rr":rr }
			return ret


		opt1 =  search.get_metas()
		exprs = search.get_expressions_dic()
		#debug(str(exprs))
		#debug(str(exprs.decode("utf8")))
		#opt1 = [[ "db_id", "File name" ],	[ "1", "Date" ],[ "2", "Creator" ]	]
		#opt2 = [[ "contain", "Contain" ], [ "is", "Is"], [ "max", "Max"], [ "min", "Min"]]

		user_has_access = User.current().has_write_access_into(search)

		if not is_edit:
			del_button = savesr_panel = last_buttons = ""
			if user_has_access:
				last_buttons = "<input name='edit' type='submit' value='%(edit)s' class='submit' />" % {
					"edit" : loc["edit"]}
		else:
			last_buttons = """<input type='button' id='searchformadd' value='' /><input type='submit' value='%(s_button)s' class='submit' />"""	 % {
			"s_button" : loc["s_button"]}

			savebutton = ""
			saveasbutton = u"""
				<input type="radio" name="gsave" value="update" checked />%(update)s
				<input type="radio" name="gsave" value="save_as" />%(s_saveas)s
				""" % {
				"update" : loc["update"], "s_saveas" : loc["s_saveas"]}
			savename =  u"<input id='savenameinput' type='text' name='savename' value='%(s_name)s' class='vtip' title='%(s_typename)s' />" % {
				"s_typename" : loc["s_typename"], # Type the name of this search query
				"s_name" : search.name
				}

			if search.guid == "" :
				saveasbutton = """<input type="hidden" name="gsave" value="update" checked />"""

			savebutton = """ <input type='submit' value='%(save)s' name='save' id='savebutton' />""" % {
				"save" : loc["save"] }


			savesr_panel = """
				%(s_savesearch)s:
				%(savename)s
				%(savebutton)s
				%(saveasbutton)s	""" % {
					"savename" 		: savename,
					"savebutton" 	: savebutton,
					"saveasbutton" : saveasbutton,
					"s_savesearch" : loc["s_savesearch"],
			}
			del_button = u"""<input type='button' class='searchformdel' value='' onclick='return sfdelrow(this);' />"""

		top_meta = search.get_top_meta()
		opt2 = search.get_expr_types(top_meta)
		key =  str(uuid4())
		start_form = get_selector("meta_"+key, opt1, "" , "expr_"+key) \
			+ get_selector("expr_"+key, opt2, "") \
			+ get_text(	"value_"+key, "",
				search.get_regexp(top_meta), True,
				search.get_desription(top_meta),
				search.is_date(top_meta)) \
			+ last_buttons



		result = u"""
<h3>%(s_custom)s:</h3>
<div id='searchformbulid'>

<form method='post' action='' id='searchform'  name='searchform' onsubmit='return sfsubmit();'>

%(body)s

<div id='searchformtools' style='text-align: center'>

<div class='savesr'>
%(savesr)s

</div>

<input id='sfformat' type='hidden' name='format' value='%(format)s' />
<input id='sfdata' type='hidden' name='data' value='' />
<input id='order' type='hidden' name='order' value='' />
<input type='hidden' name='searchguid' value='' />
</div></form>
</div>
"""

		#result += """<script>var ssopt={ 'Creator':[['id','Contains'],['id','Equal'],['id','Not equal']],"""\
		#""" 'File name':[['id','123'],['id','323'],['id','4444'],['xx','gh533f']] };</script>"""
		#""" 'File name':{regexp:'.*',title:'ex:dd-mm-gg',expr:[['id','123'],['id','323'],['id','4444'],['xx','gh533f']]} };</script>"""
		#""" 'field1':{regexp:'.*',title:'ex:dd-mm-gg',expr:[['id','123'],['id','323']],field3:[] -- eto input } };</script>"""
		#""" 'field1':{regexp:'.*',title:'ex:dd-mm-gg',expr:[['id','123'],['id','323']],field3:[['x','1'],['x','2']] -- eto select} };</script>"""
		result += """<script>var ssopt=%(exprs)s ;</script>"""

		#debug( """<script>var ssopt=%(exprs)s ;</script>"""%{"exprs":exprs})

		if len(params) == 0:
			params = {'format': [
						{	'name': "f1",
							'type': "select",
						  'change': "f3" },
						{	'name': "f3",
							'type': "text"	}]}

		expressions = search.get_expressions()
		if len(expressions) == 0:
			a_res = u"<div class='sfrow'>"+start_form +"</div>"
		else:
			a_res = ""
			#  {"meta":args["meta_"], "expr":args["expr_"+key], "value":args["value_"+key], "key":key }
			first = 1
			for expression in expressions:
				a_res += u"<div class='sfrow'>"
				a_res += get_selector("meta_"+expression.key, opt1, expression.meta, "expr_"+expression.key )
				a_res += get_selector("expr_"+expression.key, search.get_expr_types(expression.meta), expression.expr_type )

				a_res += get_text("value_"+expression.key, expression.value,
					search.get_regexp(expression.meta),
					search.is_valid_value(expression),
					search.get_desription(expression.meta),
					search.is_date(expression.meta)
				)
				#a_res +=  add_button +" </div>"
				if first == len(expressions):
					a_res += last_buttons
				else:
					a_res += del_button
				a_res += "</div>"

				first += 1

		return self.js(params["format"], start_form) +"<br />" + result % {
		"body": a_res,
		"format": "",
		"exprs":exprs,
		"s_custom" : loc["s_custom"],
		"savesr" : savesr_panel
		}

	@classmethod
	def js(self, format, start_form):
		ret = u"""<script type='text/javascript'>

/* search select */
function subsel(o,t) {
  if (typeof ssopt != 'undefined') {
    var t=$(t);
    var x=t.parent().find('select[name="'+o+'"]');
    x.html('');
    var v=t.val();
    if (typeof ssopt[v]['expr'] != 'undefined') {
      $.each(ssopt[v]['expr'], function(i1,e1){
        $('<option value="'+e1[0]+'">'+e1[1]+'</option>').appendTo(x);
      });
    }
    if (typeof ssopt[v]['title'] != 'undefined') {
      var o = t.parent().find('input[name^="value_"]');
      o.attr('title',ssopt[v]['title']);
      var isdate=ssopt[v]['isdate'];/*is date?*/
      if (isdate=='1') {
	      o.addClass('fdate');
	      setfdate();
	  }
	  else {
	  	o.removeClass('fdate')
	  	var inpd=jQuery('#nyroModalContent input[name=default]');
		o.datepicker('destroy');
	  }
      if (typeof ssopt[v]['regexp'] != 'undefined') {
        o.attr('regexp', ssopt[v]['regexp']);
      }
    }
  }
}

function sfdelrow(t){
	jQuery(t).parent().remove();
	return false;
}
function sfsubmit(){
	var f=jQuery('form#searchform'), order='';
	f.find('input[regexp]').each(function(){
		var t=$(this);
		var r=t.attr('regexp') || '';
		var v=t.val() || '';
		if (r != '') {
			var re = new RegExp(r, 'ig');
		    if (re.test(v)) {
				t.removeClass('error');
			}else{
				t.addClass('error');
			}
		}
	});
	if (f.find('input.error').size() > 0) return false;
	jQuery('input#sfdata',f).val(f.serialize());
	jQuery('div.sfrow',f).each(function(){
      order+=$('select:first',this).attr('name')+',';
	});
	jQuery('input#order',f).val(order);
$('#searchformbulid').mask('');
	return true;
}
jQuery(document).ready(function($){
	$('input#searchformadd').click(function(){
	    var r=$("<div class='sfrow'> %(start_form)s <input type='button' class='searchformdel' value='' onclick='return sfdelrow(this);'/></div>");
	    $('#searchformadd',r).remove();
		var uu=xuuid();/*$.uuid();*/
		r.find('select[name^=meta_]').attr('name','meta_'+uu).removeAttr('onchange').change(function(){ subsel('expr_'+uu,this); return false; });
		r.find('select[name^=expr_]').attr('name','expr_'+uu);
		r.find('input[name^=value_]').attr('name','value_'+uu);
		r.find('input[type=submit]').remove();
		subsel('expr_'+uu,r.find('select[name^=meta_]'));
		//r.insertBefore('#searchformtools');
		r.insertBefore('#searchform .sfrow:last');
		vtip();

	});
	$('#savenameinput').keyup(function(e){
      if(e.keyCode==13){
        $('#savebutton').click();
      }
	});
});</script>"""%{"start_form":start_form.replace('"', '\\"')}
		return ret


	@classmethod # L - almost
	def get_args_form(self,  args):
		args = obsolete_request.arguments
		arg = dict( [ (id,args[id]) for id in args if id not  in ["file_filename","file"] ] )
		#raise Exception(str(arg)+"\n\n\n"+str(args))
		return """<script>var saveform='%(saveform)s'</script>"""%{"saveform":json.dumps(arg)}


	@classmethod
	def get_add_new_doc_form(self,document_share):
		def get_meta_selecter(meta, mark, descr):
			ret = opt = ""
			options = [word  for word in meta.constraint.split(",") if len(word) > 0 ]
			def_val = utils_old.html_escape(metafield.default)
			#raise Exception(str(mark))
			for option in options:
				sel = ""
				if def_val == option:
					sel =  " selected"
				opt +=	 """<option value='%(val)s' %(sel)s>%(val)s</option>"""%{"val":option, "sel":sel }
			ret += """<tr><td>%(name)s   </td><td><select style='width:100px;' name='%(id)s' title='%(descr)s'   >%(opt)s</select></td></tr>
					"""%{ "name":cgi.escape(meta.name)+mark,  "opt":opt, "descr":descr, "id":meta.id}
			return ret


		loc = localization.get_lang()
		res = metafields= ""
		metas_json = {}
		additional_metafields = document_share.get_additional_metafields()
		if len(additional_metafields) > 0:
			metafields += "<table cellpadding='2' cellspacing='0'>"
			for metafield in additional_metafields:
				meta = {}
				meta["expr"] = []
				meta["type"] = ""
				date = ""
				mark = " *"
				meta_descr = "no title"
				regexp = ".*"


				meta_type = Metas_type.get_by_id(metafield.type_id)

				""" set description and regexp """
				if meta_type :
					descr = meta_type.description.encode("utf8")
					meta_descr = loc[descr] if descr in loc else descr # loc of description
					regexp =  meta_type.regexp.encode("UTF-8")

				if 	str(metafield.required) == "0":
					regexp += "|(^$)"
					mark = ""


				if meta_type.id == 4:
					"""set data in selecter case """
					meta["expr"] =  [word  for word in metafield.constraint.split(",") if len(word) > 0 ]
					metafields +=  get_meta_selecter(metafield, mark, meta_descr )
				elif meta_type.id == 5:

					selection_template = """
						<select onchange='adddocsmartchange(this, "%s")'  id="smart_selection" style="width: 100%%;" name="%s"> %s </select>
					"""
					shares_options = ""
					is_display = "style='display:none'"
					parent_folder = SmartFolder.get_by_guid(metafield.constraint)
					if parent_folder:
						shares_options += """<option value="">---</option>"""
						for share in parent_folder.sub_folders:
							shares_options += """<option value="%s">%s</option>""" % (share.guid, cgi.escape(share.name))
						if not shares_options:
							is_display = ""
						shares_options += """<option value="new_smart">+ %s</option>"""  % loc["fs_add_smartsubfolder"]
					meta_folder = selection_template % ("new_archive_"+str(metafield.id), metafield.id, shares_options)

					meta_folder_new = """
						<tr class='%(tr_class)s' %(display)s>
							<td><div class='%(tr_class)s' >%(html_archive)s:</div></td>
							<td>
								<input type="text" name="new_sub_folder_%(meta_id)s" value="" />
							</td>
						</tr>
					""" % {"html_archive" : loc["html_sub-archive"], "display" : is_display, "tr_class" : "new_archive_"+str(metafield.id), "meta_id" : str(metafield.id)}

					metafields += """<tr><td>%(name)s</td><td>%(folder)s</td>
		 				"""%{"name":cgi.escape(metafield.name)+mark, "folder" : meta_folder}
					metafields += meta_folder_new

				else:
					if meta_type.id == 2:
						"""set data in date case """
					 	meta["type"] = "date"
						date += " fdate"
					value = utils_old.html_escape(metafield.default)
					metafields += """<tr><td>%(name)s</td><td><input type='text' value='%(value)s' title='%(descr)s' regexp='%(regexp)s' class="vtip%(date)s" name='%(id)s' autocomplete="off" /></td>
		 				"""%{"name":cgi.escape(metafield.name)+mark, "id":metafield.id, "value":value, "descr":meta_descr, "regexp":regexp, "date":date}

				meta["name"] = metafield.name + mark
				meta["regexp"] = regexp
				meta["title"] = meta_descr
				metas_json[metafield.id] = meta

			metafields += """</table>"""
		metas_json =  """<script>var ssmetas=%(metas_json)s;</script> """%{"metas_json":json.dumps(metas_json)}

		return metafields


	"""
		Document Share info builder.
		Determines user, his acl and those folders he can see.
		Appropriate structure:
			folder 	= "<ul> <li class='folder %(exp)s' > %(folder_name)s %(folder_content)s </ul>"
			share 	= "<li>	%(share_name)s %(attr)s"
			folder_content = ["<ul>" + share_list + </ul>] OR [folder]

	"""
	@classmethod
	def tree_builder(self, user, folder_root, connector, search, use_url=True):

		folder_guid = folder_root and folder_root.guid

		#"pack additional attributes into string"
		def get_attr(doc_share):
			doc_share.set_connector(connector)
			attr_ = list(meta for meta in doc_share.get_additional_metafields())
			if not attr_: return ""
			type = [type_str[key] for key in [meta.type_id for meta in attr_]]
			attr = [meta.name for meta in attr_]
			attr = map(lambda x,y: u"%s(%s)" % (x,y), attr, type)
			attr  = u"<b>%s</b>" % ", ".join(attr) if attr else ""
			return attr


		#"recursive function, gets all folders and shares"
		def get_folder(folder):
			exp = u" expanded" if folder.guid == folder_guid else ''
			content = ""

			for a_folder in user.get_subfolders(folder):
				content += get_folder(a_folder)

			for doc_share in user.get_documents_shares(folder):
				attr = get_attr(doc_share)
				doc_share.set_search(search)
				d_count = doc_share.get_doc_count_in_share()
				d_count = d_count if not d_count == 2147483648 else 0
				doc_count = doc_temp % {
					"fs_tree2" : loc["fs_tree2"],
					"doc_count" : str(d_count)
				}
				data_url = u"" if not use_url else \
					ds_url_temp % { "s_guid" : doc_share.guid, "f_guid" : folder.guid }

				content += share_temp % {
					"share_name"	: doc_share.name,
					"share_attr"	: attr,
					"data_url"		: data_url,
					"doc_count"		: doc_count,
					"id"			: id_temp % {"guid" : doc_share.guid if not use_url else data_url }
				}

			f_content = folder_content % {"content" : content}
			data_url = u"" if not use_url else \
				df_url_temp % { "f_guid"	: folder.guid }
			_res =  folder_temp % {
				"folder_name" 		: folder.name,
				"folder_content" 	: f_content,
				"exp"				: exp,
				"data_url" 			: data_url,
				"id"				: id_temp % {"guid" : folder.guid if not use_url else data_url }
			}
			#raise Exception(_res)
			return _res

		#"##################################"#

		# if admin, then print the whole tree,
		# else only visible folders
		loc = localization.get_lang()
		result 			= u"""<b>%(source)s:</b><br /><br /><ul>"""
		folder_temp 	= u"<li class='folder%(exp)s'%(data_url)s %(id)s>%(folder_name)s %(folder_content)s"
		share_temp 		= u"<li%(data_url)s %(id)s>%(share_name)s %(share_attr)s %(doc_count)s\n"
		folder_content = u"<ul>%(content)s</ul>"
		df_url_temp 	= u"""/Folder.vdom?guid=%(f_guid)s"""
		ds_url_temp		= u"""/Document_share.vdom?guid=%(s_guid)s&parent=%(f_guid)s"""
		id_temp 		= u""" id='%(guid)s'"""
		doc_temp		= u"<u>[%(doc_count)s %(fs_tree2)s]</u>"

		type_str = {	1:loc["mf_string"],	2:loc["mf_date"],
						3:loc["mf_float"], 	4:loc["mf_list"],
						5:loc["mf_folder"]}

		if folder_root:
			src_string = loc["header_f"]+" "+folder_root.name
		else:
			src_string = loc["ds_title"]

		result = result % {
				"source" : src_string
		}
		for document_share in user.get_top_folders():
			result += get_folder(document_share)

		result += u"</ul>"
		return result

	@classmethod
	def get_left_tree(self, cnt):
		loc = localization.get_lang()
		tree = u"""<div id='greentree'><div style='height:%(percent)s%%'></div></div><p class='green'>
		%(fs_tree1)s %(files)s%(max_files)s %(fs_tree2)s %(fs_tree3)s %(trees)s %(fs_tree4)s <br/><br/></p> """

		files = cnt.search.get_doccount()
		percent = -1.0/((1.0/200000.0) * files + 1.0/100.0 ) + 100.0
		trees = '%.2f'%(files/400.0)#  tr()

		from class_user import User
		user = User.current()

		unlim, max_documents = True, 0
		if user:
			max_documents = user.max_documents
			unlim = True if max_documents >= user.big_value else False

		result = tree % {
			"percent":percent, "files":files, "trees":trees,
			"max_files" : "" if unlim else "/%s" %  str(max_documents) if user else str(0),
			"fs_tree1" : loc["fs_tree1"],
			"fs_tree2" : loc["fs_tree2"],
			"fs_tree3" : loc["fs_tree3"],
			"fs_tree4" : loc["fs_tree4"]
		}

		return result

	@classmethod
	def get_left_searches(self):
		loc = localization.get_lang()
		searches_html = ""
		user = User.current()
		if user:
			accessed_searches = user.get_searches()
			if accessed_searches:
				searches_html = u"<div class='custom-searches'><h3>%(s_searches)s</h3><div class='list-searches'>" % {
					"s_searches" : loc["s_searches"]
				}
				for search in accessed_searches:
					searches_html += u"""<a href='Custom_search.vdom?guid=%(guid)s' title='%(name)s'> %(name)s</a> """%{"name":cgi.escape(search.name),"guid":search.guid}

				searches_html += u"</div></div>"
			else:
				searches_html = u"<div class='custom-searches-empty'></div>"
		return searches_html

	@classmethod
	def get_logout_back_url(self):
		import managers
		# first, get the REQUEST_URI, current address string, with all GET arguments.
		env = managers.request_manager.get_request().environment().environment()
		result = env["REQUEST_URI"] if "REQUEST_URI" in env else ""
		# replace "&" with %26 - in order to process several arguments
		result = result.replace('&',"%26")
		return result

	@classmethod
	def get_search_helper(self, hpt_id):
		template = u"""
			<div
				class='search_helper_header'
				onclick='return set_operator("%(hpt_id)s","%(op)s")'
			>	%(operator)s
				&nbsp;&nbsp;&nbsp;
				<img src='/24757dfe-0fd2-4cde-80b1-ec9faf6f724a.png'>
			</div>
			<p class='search_helper_text'>
				%(description)s
			</p>
			<div style='border-top: 1px dotted #959594; margin:6px 0px 6px 0px;'></div>
		"""

		loc = localization.get_lang()
		operators = {
			'AND'	: loc['AND'],
			'OR'	: loc['OR'],
			'NOT'	: loc['NOT'],
			'XOR'	: loc['XOR'],
			'()'	: loc['BRACKETS'],
			'+ , -'	: loc['+AND-'],
			'" "'	: loc['PHRASES'],
			'NEAR'	: loc['NEAR'],
			'ADJ'	: loc['ADJ'],
			' * '	: loc['WILDCARDS'],
		}

		from StringIO import StringIO
		res = StringIO()

		for k,v in sorted(operators.iteritems()):
			res.write(template % { "operator":k, "description":v,
				"op": k if k!='" "' else "quotes" , "hpt_id":hpt_id })

		getresult = res.getvalue()

		return res.close() or getresult


