class ISearch:

	@classmethod
	def get_all(self):
		query = """SELECT id, guid, name
					FROM search
					ORDER BY name """
		rows = Database.maindb().fetch_all(query,)

		return [ISearch(row) for row in rows]

	def __init__(self, row=None):
		if row:
			self.id = row[0]
			self.guid = row[1]
			self.name = row[2]
		else:
			self.id = ""
			self.guid = ""
			self.name = ""

	def delete(self):
		# delete old expressions
		for expression in  Expression.get_by_search_guid(self.guid):
			expression.delete()
		# delete old objects
		for obj in  Search_object.get_by_search_guid(self.guid):
			obj.delete()

		for acl in ACL.get_by_object(self):
				acl.delete()

		query = """DELETE FROM search
						WHERE guid=? """
		Database.maindb().commit(query, (str(self.guid),))



class Search():


	@classmethod
	def get_all(self):
		query = """SELECT id, guid, name
					FROM search
					"""
		rows = Database.maindb().fetch_all(query,)

		return [Search(row) for row in rows]


	@classmethod
	def get_by_guid(self, guid):
		query = """SELECT id, guid, name
					FROM search
					WHERE guid=?"""
		row = Database.maindb().fetch_one(query, (str(guid),))

		if row:
			return Search(row)
		return None

	def __init__(self, row=None):
		from class_user import User

		self.loc = localization.get_lang()


		self.__objects = []
		self.__doc_shares = []
		self.__user =User.current()
		self.__metas = {}
		self.__expressions  = []
		self.__query = []

		meta_type = Metas_type.get_by_id(1)
		if meta_type:
			item = {}
			descr = meta_type.description.encode("utf8")
			item["title"] = self.loc[descr] if descr in self.loc else descr
			item["regexp"] = meta_type.regexp.encode("UTF-8")
			item["expr"] = meta_type.get_expressions()
			item["name"] = self.loc["mf_auto_text"]
			item["isdate"] = 0;
			self.__metas["text"] = item

		if row:
			self.id = row[0]
			self.guid = row[1]
			self.name = row[2]

			self.__expressions =  Expression.get_by_search_guid(self.guid)
			self.__objects = Search_object.get_by_search_guid(self.guid)
			self.__validate_doc_shares()
			self.__validate_query()

		else:
			self.id = ""
			self.guid = ""
			self.name = ""

	def __str__(self):
		return self and self.name+"_"+self.guid

	def set_objects(self, objects, parent=None):
		self.__objects = objects
		self.__validate_doc_shares()

	def get_objects(self):
		return self.__objects

	def __load_objects(self):
		# load object from DB
		pass

	def delete(self):
		# delete old expressions
		for expression in  Expression.get_by_search_guid(self.guid):
			expression.delete()
		# delete old objects
		for obj in  Search_object.get_by_search_guid(self.guid):
			obj.delete()

		for acl in ACL.get_by_object(self.guid):
				acl.delete()

		query = """DELETE FROM search
						WHERE guid=? """
		Database.maindb().commit(query, (str(self.guid),))

	def save(self):
		# save search
		if self.guid !="" and Search.get_by_guid(self.guid):
			# delete old expressions
			for expression in  Expression.get_by_search_guid(self.guid):
				expression.delete()
			# delete old objects
			for obj in  Search_object.get_by_search_guid(self.guid):
				obj.delete()

			query = """UPDATE search
						SET name=? WHERE guid=?"""
			Database.maindb().commit(query, (self.name,self.guid ,))
		else:
			self.guid =  str(uuid4())

			query = """INSERT INTO search(guid, name)
						VALUES(?,?)"""
			row = Database.maindb().commit( query,( self.guid , self.name))


		# add new expressions
		for expression in self.__expressions:
			expression.search_guid =  self.guid
			expression.save()

		# add new objects
		for obj in self.__objects:
			obj.search_guid = self.guid
			obj.save()





	"""
	def get_metas(self):
		if self.__objects:
			return [ [meta.name, meta.name] for meta in self.__objects.get_metafields()]
		else:
			return []
	"""
	def get_metas(self):
		#raise Exception(str(self.__metas))
		return [[meta, self.__metas[meta]["name"]] for meta in self.__metas]


	def	__validate_doc_shares(self):
		#raise Exseption("No worc	")
		for obj in self.__objects:
			if obj.parent_guid != "":
				folder = Folder.get_by_guid(obj.parent_guid)
				doc_share = Document_share	.get_by_guid(obj.object_guid)

				if doc_share and folder :
					self.__doc_shares.append([doc_share, folder])
			else:
				folder = Folder.get_by_guid(obj.object_guid)
				if	folder:
					self.__set_doc_shares(folder)

		self.__validate_metas()

	def __validate_metas(self):
		from class_db import Database

		shares_arr = [doc[0].guid for doc in self.__doc_shares]
		shares_str = "','".join(shares_arr)

		if self.__doc_shares:
			query	= """SELECT name, type_id
							FROM (SELECT DISTINCT metafield_in_doc_share.doc_share_guid, name, type_id
								FROM metafield
								INNER JOIN metafield_in_doc_share
									ON metafield.id=metafield_in_doc_share.metafield_id
								WHERE metafield_in_doc_share.doc_share_guid IN ('"""+shares_str+ """')
								)
							GROUP BY name, type_id
							HAVING Count(name) ="""+ str(len(shares_arr))
			rows = Database.maindb().fetch_all(query,)
			#raise Exception(str([row[1] for row in rows]).replace("<","&lt;"))
			for row in rows:
				meta_type_id = row[1]
				meta_name = row[0]
				meta_type = Metas_type.get_by_id(meta_type_id)
				if meta_type:
					#metas.append([row[0], row[0]])
					item = {}
					descr = meta_type.description.encode("utf8")
					item["title"] = self.loc[descr] if descr in self.loc else descr
					item["regexp"] = meta_type.regexp.encode("UTF-8")
					item["expr"] = meta_type.get_expressions()
					item["field3"] = "[]"
					item["name"] = self.loc[meta_name]	if meta_name in self.loc else meta_name
					item["isdate"] = 0 if meta_type.id != 2 else 1
					self.__metas[row[0]] = item
			#return metas

	def get_expressions_dic(self,):
		import json
		#debug("metas arr: "+str(self.__metas))
		return json.dumps(self.__metas)

	def get_expressions(self,):
		return self.__expressions

	# set available to the user doc_shares
	def __set_doc_shares(self, folder):
		if folder:
			for doc_share  in self.__user.get_documents_shares(folder):
				self.__doc_shares.append([doc_share, folder])
			for s_folder  in self.__user.get_subfolders(folder):
				self.__set_doc_shares(s_folder)

	def get_top_meta(self):
		for meta in self.__metas:
			return meta
	#def get_star_expr(self):
	#	return  self.__metas["text"]["expr"]#  [[ "max", "Max"], [ "min", "Min"]]

	def get_expr_types(self, meta_name):
		if meta_name in self.__metas:
			return  self.__metas[meta_name]["expr"]
		return ""

	def get_desription(self, meta_name):
		if meta_name in self.__metas:
			return  self.__metas[meta_name]["title"]#  [[ "max", "Max"], [ "min", "Min"]]
		return ""


	def is_valid_value(self, exprn):
		regex = self.get_regexp(exprn.meta.encode("utf8"))#self.__metas[exprn.meta.encode("utf8")]["regexp"]
		reobj = re.compile(regex, re.MULTILINE)

		if reobj.search(exprn.value):
			return True

		return False

	def is_date(self, meta_name):
		if meta_name in self.__metas:
			return  self.__metas[meta_name]["isdate"] == 1
		return False




	def get_regexp(self, meta_name):
		if meta_name in self.__metas:
			res =  self.__metas[meta_name]["regexp"]
			if res and res != None:
				return 	res
		return 	""

	def set_expressions(self, data):
		self.__expressions = data
		self.__validate_query()

	def __validate_query(self):
		self.__query = []
		for exprn in self.__expressions:
			if self.is_valid_value(exprn):
				self.__query.append({"meta":exprn.meta, "value":exprn.value, "type":exprn.expr_type  })
			else:
				#raise Exception("No Valid value")
				self.__query = []
				return


	def get_query(self):
		return self.__query # [{"meta":str(meta.id), "value":word, "type":"" }]




class Search_object:

	@classmethod
	def get_by_search_guid(self, search_guid):
		query = """SELECT id,  search_guid, object_guid, parent_guid
					FROM search_object
					WHERE search_guid=?"""
		rows = Database.maindb().fetch_all(query, (str(search_guid),))

		return [Search_object(row) for row in rows]


	def __init__(self, row=None):
		if row:
			self.id = row[0]
			self.search_guid = row[1]
			self.object_guid = row[2]
			self.parent_guid = row[3]
		else:
			self.id = 0
			self.search_guid = ""
			self.object_guid = ""
			self.parent_guid = ""

	def save(self):
		query = """INSERT INTO search_object(search_guid, object_guid, parent_guid  )
					VALUES(?,?,?)"""
		row = Database.maindb().commit( query,(self.search_guid, self.object_guid, self.parent_guid))

	def delete(self):
		# delete
		query = """DELETE FROM search_object
					WHERE id=? """
		Database.maindb().commit(query, (self.id,))

from subprocess import *
import shlex
import chardet
from email.parser import Parser
import unicodedata
from  pyth.plugins.rtf15.reader import Rtf15Reader


def remove_accents(str):
	return u"".join([c for c in str if not unicodedata.combining(c)])

def normalize( unicode_text ):
	"""
	Should be used for all indexers!!
	TODO: rebuild the inderxer-call mechanizm (with decorator?) to auto-normalize
	Answers a normalised form of a UNICODE (not encoded) string (line):
		1) Decomposition of uncicode characters (Ã© -> eÂ´)
		2) legature manual decomposition
		3) Removal of all non-ascii symbols

	"""
	return unicode_text and remove_accents( unicodedata.normalize('NFKD', unicode(unicode_text)) \
		.replace( u"\xc6", "AE" )\
		.replace( u"\u0152", "OE" ) \
		.replace( u"\u0153", 'oe' )\
		.replace( u"\u00E6", 'ae' )\
		) or ""

def get_unicode(text):
	try:
		enc  = chardet.detect(text)['encoding']
		if enc == "ISO-8859-2":
			enc = "ISO-8859-1"
		elif enc == "windows-1250":
			enc = "windows-1252"
		s = u""+enc
		return u""+text.decode(s)
	except:
		pass

	try:
		return  u""+text.decode("cp1252") #hooray, no exception!
	except:
		pass
	try:
		return  u""+text.decode("latin-1") #hooray, no exception!
	except:
		pass

	try:
		return  u""+text.decode("ascii") #hooray, no exception!
	except:
		pass




	try:
		return  u""+text.decode("utf-8") #hooray, no exception!
	except:
		pass
	try:
		return  u""+text.decode("utf-16") #hooray, no exception!
	except:
		pass
	return u""+text



def stub(filepath):
	return u""

def pdf2txt(filepath):
	try:
		cmd = """/usr/local/bin/pdftotext -nopgbrk -q -f 1 -l 30 -enc "UTF-8" "%(file)s" -"""%{"file": application.storage.abs_path(filepath)}
		p = Popen( shlex.split( cmd.encode( "utf-8") ), stdin=PIPE,bufsize=-1, stdout=PIPE, stderr=PIPE, close_fds=True)
		return p.stdout.read().decode("UTF-8")
	except:
		return u""


def doc2txt(filepath):
	try:
	#if True:
		cmd = """/usr/local/bin/catdoc -s 'cp1252' -d 'utf-8' '%(file)s' """%{"file": application.storage.abs_path(filepath)}
		p = Popen( shlex.split( cmd.encode( "utf-8") ), stdin=PIPE, bufsize=-1, stdout=PIPE, stderr=PIPE, close_fds=True)
		#raise Exception(p.stdout.read().decode("utf-8"))
		return p.stdout.read().decode("utf-8")
	except Exception, e:
		raise Exception(str(e)+"its DOC!")
		return u""



def rtf2txt(filepath):
	try:
		#cmd = """/usr/local/bin/unrtf --text  '%(file)s' """%{"file": filepath}
		#p = Popen( shlex.split( cmd.encode( "utf-8") ), stdin=PIPE, bufsize=-1, stdout=PIPE, stderr=PIPE, close_fds=True)
		#result = get_unicode( p.stdout.read() )
		#delete comments from text (first 13 lines)
		#return "\n".join(result.split("\n")[13:])


		doc = Rtf15Reader.read(open(application.storage.abs_path(filepath),"rb"))
		rtf_index = u" ".join([text.content[0].content[0] for text in doc.content])
		return rtf_index
	except:
		raise
		return u""


def odt2txt(filepath):
	try:
		cmd = """/usr/local/bin/odt2txt --encoding='UTF-8' '%(file)s' """%{"file": application.storage.abs_path(filepath)}
		p = Popen( shlex.split( cmd.encode( "utf-8") ), stdin=PIPE, bufsize=-1, stdout=PIPE, stderr=PIPE, close_fds=True)
		return p.stdout.read().decode("UTF-8")
	except:
		return u""

def xls2txt(filepath):
	try:
		cmd = """/usr/local/bin/xls2csv -s cp1252 -d 'utf-8' '%(file)s' """%{"file": application.storage.abs_path(filepath)}
		p = Popen( shlex.split( cmd.encode( "utf-8") ), stdin=PIPE, bufsize=-1, stdout=PIPE, stderr=PIPE, close_fds=True)
		return p.stdout.read().decode("UTF-8")
	except:
		raise
		return u""


def docx2txt(filepath):
	import re
	try:
		cmd = """/usr/local/bin/unzip -p '%(file)s' 'word/document.xml' """%{"file": application.storage.abs_path(filepath)}
		p = Popen( shlex.split( cmd.encode( "utf-8") ), stdout=PIPE, bufsize=-1, close_fds=True)
		xml_output = p.stdout.read().decode("utf-8")
		txt_output = re.sub( "<[^>]*>", " ", xml_output )
		return txt_output
	except:

		return u""


def pptx2txt(filepath):
	import re
	try:
		cmd = """/usr/local/bin/unzip -p '%(file)s' 'ppt/slides/slide*.xml' """%{"file": application.storage.abs_path(filepath)}
		p = Popen( shlex.split( cmd.encode( "utf-8") ), stdout=PIPE, bufsize=-1, close_fds=True)
		xml_output = p.stdout.read().decode("utf-8")
		txt_output = re.sub( "<[^>]*>", " ", xml_output )
		return txt_output
	except:

		return u""

def txt2txt(filepath):
	result = application.storage.readall(filepath)
	return get_unicode( result )

def xlsx2txt(filepath):
	import re
	try:
		cmd = """/usr/local/bin/unzip -p '%(file)s' 'xl/sharedStrings.xml' """%{"file": application.storage.abs_path(filepath)}
		p = Popen( shlex.split( cmd.encode( "utf-8") ), stdin=PIPE, stdout=PIPE, bufsize=-1, stderr=PIPE, close_fds=True)
		xml_output = p.stdout.read().decode( "UTF-8" )
		txt_output = re.sub( "<[^>]*>", " ", xml_output )
		return txt_output
	except:
		return u""

def eml2txt(filepath):
	result = u""
	message = Parser().parsestr(application.storage.readall(filepath))

	header_titles = ['From','To','Subject','Date', 'Reply-to']

	for head in header_titles:
		result += u"""%(title)s: %(value)s """ % {
			"title": head,
			"value": message[head]
		}

	if message.get_content_maintype() == 'multipart':
		for part in message.walk():
			if part.get_content_maintype() == 'multipart':
				continue
	   		elif part.get_content_subtype() in ('text','html'):
	   			result += part.get_payload()
	   			continue
	    	filename = part.get_filename()
	    	if filename:
	    		result += " "+filename
	    	else:
		   		result += str(part)
	else:
		result += " " + message.get_payload()

	return result




#Mime to function dictionary
MIME_INDEXERS = {
	#basic types
	"text/plain": txt2txt,
	"application/rtf": rtf2txt,
	"application/pdf": pdf2txt,
	"application/xml": txt2txt,

	#office
	"application/msword": doc2txt,
	"application/vnd.ms-excel": xls2txt,
	"application/vnd.openxmlformats-officedocument.wordprocessingml.document": docx2txt,
	"application/vnd.oasis.opendocument.text": odt2txt,
	"application/vnd.openxmlformats-officedocument.spreadsheetml.sheet": xlsx2txt,
	"application/vnd.openxmlformats-officedocument.presentationml.presentation":pptx2txt,
	"application/email": eml2txt
}

def get_mime_indexer(mime):
	return MIME_INDEXERS[ mime ] if mime in MIME_INDEXERS else stub

