import localization_ru
import localization_en
import localization_fr

lang = session.get("lang", default = None)

def get_lang(not_used = None):

	lang = session.get("lang", default = None)

	if lang is None:
		lang = switch_lang( "en" )

	if lang == "en":
		return localization_en.localization_dict

	if lang == "ru":
		return localization_ru.localization_dict

	elif lang == "fr":
		return localization_fr.localization_dict

	else:
		return localization_en.localization_dict

def switch_lang( newlang ):
	session["lang"] = newlang
	return newlang

