def get_env_argument(key):
	from managers import request_manager
	env = request_manager.get_request().environment().environment()
	#raise Exception(str(env)) # to look through env on appropriate params
	result = env[key] if key in env else ""
	return result

def get_referer_url():
	result = get_env_argument("HTTP_REFERER")
	if "home_page" in result.lower():
		result = "/Documents_share.vdom"
	return result

def get_logout_back_url():
	result = get_env_argument("REQUEST_URI").replace('&',"%26")
	return result

def current_page_name():
	result = get_env_argument("SCRIPT_NAME").split("/")[1].split(".")[0]
	return result


def search_escape( text):
	html_escape_table = {
			"&": "&amp;",
			#'"': "&quot;",
			#"'": "&apos;",
			">": "&gt;",
			"<": "&lt;"

		}

	return "".join(html_escape_table.get(c,c) for c in text)

def html_escape( text):
	html_escape_table = {
			"&": "&amp;",
			'"': "&quot;",
			"'": "&apos;",
			">": "&gt;",
			"<": "&lt;"

		}

	return "".join(html_escape_table.get(c,c) for c in text)

def filename_escape(text):
	filename_escape_table = {
		"\\": "_",
		"/"	: "_",
		"|"	: "_",
		":"	: "_",
		"*"	: "_",
		"?"	: "_",
		'"'	: "_",
		"<"	: "_",
		">"	: "_",
		"&"	: "_",
		"#"	: "_",
		"'"	: "_",
		","	: "_",
		";"	: "_"
	}
	# Â«\Â», Â«/Â», Â«:Â», Â«*Â», Â«?Â», Â«"Â», Â«<Â», Â«>Â», Â«|Â»
	return "".join(filename_escape_table.get(c,c) for c in text)

def date2base(text=""):
	result = text
	re_obj = re.compile(r"(0[1-9]|[12][0-9]|3[01])[-](0[1-9]|1[012])[-](19|20)[0-9]{2}")

	for match in re_obj.finditer(result):
	    #print match.group()
	    datevalue = datetime.strptime(match.group(), "%d-%m-%Y").strftime("%Y-%m-%d")
	    result = result.replace(match.group(), datevalue)
	return result

def date2norm(text):
	result = text

	re_obj = re.compile(r"(19|20)[0-9]{2}[-](0[1-9]|1[012])[-](0[1-9]|[12][0-9]|3[01])")

	for match in re_obj.finditer(result):
	    #print match.group()
	    datevalue = datetime.strptime(match.group(), "%Y-%m-%d").strftime("%d-%m-%Y")
	    result = result.replace(match.group(), datevalue)
	return result

def collect_from_args_to(req_args, args_list):
	return dict([k, req_args[k] if k in req_args and req_args[k] else ""] for k in args_list)

def getVal(args, a=""):
	result = ""
	if a.__class__ == int:
		try:
			result = args and args[a] or ""
		except:
			result = ""
	else:
		result = args and a in args and args[a] or ""
	return result


def scalar(key, defaultvalue = None, castto = None):
	return request.arguments.get(key, defaultvalue, castto)
#    value = request.arguments[ key ] if key in request.arguments else defaultvalue
#    return castto(value) if castto and value else value

def ssn_scalar(key, defaultvalue = None, castto = None):
    notdecoded = session[ key ] if key in session else defaultvalue
    value = notdecoded.decode("utf8") if isinstance( notdecoded, str ) else notdecoded
    return castto(value) if castto and value else value

def popup_growl(growl, ttl, msg):
	growl.title, growl.text, growl.active = ttl, msg, "1"


def encode_xml(xml):
	""" encodes an xml document so that it can be stored in the database (avoids <![CDATA[ ]]> issue) """
	return xml.replace("]]>", "]]]]><![CDATA[>")

def decode_xml(xml):
	""" decodes an xml document stored in the database to usual format """
	return xml.replace("]]]]><![CDATA[>", "]]>")


def str_to_html(str):
	""" converts a specified string so it can be displayed as html """
	return "<pre>%s</pre>" % escape(str)


def log_bugs(message):
	from log.log import VDOM_log_manager as log
	log().info_bug(message)


ERROR_XML = "<error><code>%s</code><description>%s</description></error>"

ERROR_RESPONSES = {
	"00" : "User is not logged in",
	"01" : "Wrong login or password",
	"02" : "Argument is absent: '%s'",
	"03" : "Argument is bad or empty: '%s' : '%s'",
	"04" : "Access error: %s",
	"05" : "Creation error: %s",
	"06" : "Failed: %s",
	"13" : "Server error: %s",
}


def xml_error_response(code, *args):
	code = str(code)
	if 		code == "00":
		result = ERROR_RESPONSES[code]
	elif 	code == "01":
		result = ERROR_RESPONSES[code]
	elif 	code == "02":
		result = ERROR_RESPONSES[code] % str(args[0])
	elif 	code == "03":
		result = ERROR_RESPONSES[code] % (str(args[0]),str(args[1]))
	elif 	code == "04":
		result = ERROR_RESPONSES[code] % str(args[0])
	elif 	code == "05":
		result = ERROR_RESPONSES[code] % str(args[0])
	elif 	code == "06":
		result = ERROR_RESPONSES[code] % str(args[0])
	elif 	code == "13":
		from class_trace import Trace
		result = ERROR_RESPONSES[code] % str(Trace.exception_trace())
	else:
		result = "no code for this error: %s" % str(code)
	return ERROR_XML % (code, result)

from class_share_dir import ShareDir
import json
from operator import attrgetter
import localization
import cgi

EDIT_BUTTON = u"""
	<a style='cursor:pointer;' title='%(edit)s'><img align='right' src='/08244397-1e56-48a2-8bdf-4a66733f70bc.res' alt='%(edit)s' /></a>
"""

class WidgetShareFileTable:

	def __init__(self, dtable=None):
		loc = localization.get_lang()

		self.__dtable 			= dtable
		self.__dirs		 		= [] 	# set_data
		self.__files	 		= [] 	# set_data
		self.__parent_dir		= None 	# set_data
		self.__sort_column 		= 0
		self.__is_descending 	= False
		self.__folders_first 	= True
		self.__header			= [
			"guid", loc["fs_filename"], loc["fs_date"], loc["mf_functions"]
		]

	def set_data(self, dirs=[], files=[], parent_dir=""):
		self.__dirs		= dirs
		self.__files	= files
		self.__parent_dir = parent_dir

	def set_sort_column(self, column_index):
		self.__sort_column = column_index if isinstance(column_index, int) else 0

	def set_descending(self):
		self.__is_descending = True

	def set_ascending(self):
		self.__is_descending = False

	def set_folders_first(self):
		self.__folders_first = True

	def set_files_first(self):
		self.__folders_first = False

	def set_header(self, header):
		loc = localization.get_lang()
		self.__header = header if isinstance(header, list) else [
			"guid", loc["fs_filename"], loc["fs_date"], loc["mf_functions"]
		]

	@staticmethod
	def header_format():
		loc = localization.get_lang()
		return [ "guid", loc["fs_filename"], loc["fs_date"], loc["mf_functions"] ]

	@classmethod
	def dir_row_format(self, sd):
		loc = localization.get_lang()
		return [
			sd.guid, 					# key
			self.dir_string_format(sd), # folder title
			sd.date_created, 			# date
			"<span>%s</span>" % (	# functions available
				EDIT_BUTTON % {"edit":loc["edit"]}
			)
		]

	@classmethod
	def file_row_format(self, sf):
		loc = localization.get_lang()
		return [
			sf.guid,
			self.file_string_format(sf),
			sf.date_created,
			"<span>%s</span>" % (	# functions available
				EDIT_BUTTON % {"edit":loc["edit"]}
			)
		]

	@staticmethod
	def dir_string_format(sd):
		return u"""<div style='cursor: pointer;'>
			<img src='/7b54f544-3dd9-4f18-b949-714de441ba84.res'>&nbsp;&nbsp;%s
		</div>""" % cgi.escape(sd.name)

	@staticmethod
	def file_string_format(sf):
		return u"""<div style='cursor: pointer;'>
			<img src='/9927e1a7-2474-41ec-b1b1-9c0bbffa6f4b.res'>&nbsp;&nbsp;%s
		</div>""" % cgi.escape(sf.name)

	def render(self):
		result = []
		loc = localization.get_lang()

		# sorting dirs and files by choosed attribute (sort_column)
		sort_column = ["name", "date_created"][self.__sort_column]
		dirs 	= sorted(
			self.__dirs,
			key = attrgetter( sort_column ),
			reverse = self.__is_descending
		)
		files 	= sorted(
			self.__files,
			key 	= attrgetter( sort_column ),
			reverse = self.__is_descending
		)

		# put link to the parent folder of current one (only in case if parent folder is not a top one)
		if not self.__parent_dir.is_top_folder:
			self.__parent_dir.parent.name = "[..]"
			result.append( self.dir_row_format(self.__parent_dir.parent) )

		if self.__folders_first:
			# directories
			[ result.append( self.dir_row_format(sd) ) for sd in dirs ]
			# files
			[ result.append( self.file_row_format(sf) ) for sf in files]

		else:
			# files
			[ result.append( self.file_row_format(sf) ) for sf in files]
			# directories
			[ result.append( self.dir_row_format(sd) ) for sd in dirs ]

		if result:
			self.__dtable.header 	= json.dumps(self.__header)
			self.__dtable.data		= json.dumps(result)
		else:
			self.__dtable.visible 	= "0"

