					</td>
				</tr>
			""" % {"html_archive" : loc["html_archive"], "display" : is_display, "tr_class" : "new_archive_"+str(metafield.id), "meta_id" : str(metafield.id)}

			metafields += """<tr><td>%(name)s</td><td>%(folder)s</td>
				"""%{"name":metafield.name+mark, "folder" : meta_folder}
			metafields += meta_folder_new

		else:
			if meta_type.id == 2:
				date += " fdate"


			metafields += """<tr><td>%(name)s</td><td><input type='text' value='%(value)s' title='%(descr)s' regexp='%(regexp)s' class="vtip%(date)s" name='%(id)s' autocomplete="off" /></td>
 				"""%{"name":metafield.name+mark,
 				 "id":metafield.id, "value":value,
 				  "descr":meta_descr, "regexp":regexp, "date":date}
	metafields +=  """</table>
<script>
function adddocsmartchange(t, tr_id) {
  var o=jQuery('#editformmetka tr.'+tr_id);
  if ($(':selected',t).val() == 'new_smart') {
  	o.show();
  }
  else {
  	o.hide();
  }

};
</script>"""

	#raise Exception(str(args))

	s = """
<div id='editformmetka' style='display:none' title='%(edit)s'>
	<div class='inbox2'>
	    <form action='' method='post' onsubmit='if (!metkaedcheck2()) return false; $(".ui-dialog").mask(""); '>
	      <input type='hidden' name='editing_end' value='' />
	      <input type='hidden' name='edit_guid_share' value='%(edit_guid_share)s' />
	      <input type='hidden' name='edit_guid_doc' value='%(edit_guid_doc)s' />
          %(metafields)s
          <!--br/><div align='center'><input type='submit' class='button' value='%(save)s' onclick='if (!metkaedcheck()) return false; jQuery("#nyroMadalContent").mask("")' /></div-->
	      <input type='hidden' name='saveform' value='%(saveform)s' />
	    </form>
	</div>
</div>
<script>
jQuery(document).ready(function($){
    $("#editformmetka").dialog('destroy').dialog({
      modal: true, resizable: false,
      buttons: {
        %(ok)s: function() {
          if (!metkaedcheck2()) return false;
          $(".ui-dialog").mask("");
          $(".ui-dialog #editformmetka form").submit();
        },
        %(cancel)s: function() {
          $(this).dialog("close");
        }
      }
    });
});
</script>
"""%{"metafields":metafields, "saveform":json.dumps(obsolete_request.arguments),
"edit" 		: loc["edit"],
"cancel" 	: loc["cancel"],
"ok" 		: loc["ok"],
"save" 		: loc["save"],
"edit_guid_share": document_share.guid,
"edit_guid_doc": doc.guid()}
	return	s

def search_escape( text):
	html_escape_table = {
			"&": "&amp;",
			#'"': "&quot;",
			#"'": "&apos;",
			">": "&gt;",
			"<": "&lt;"

		}

	return "".join(html_escape_table.get(c,c) for c in text)

def html_escape( text):
	html_escape_table = {
			"&": "&amp;",
			'"': "&quot;",
			"'": "&apos;",
			">": "&gt;",
			"<": "&lt;"

		}

	return "".join(html_escape_table.get(c,c) for c in text)

def filename_escape(text):
	filename_escape_table = {
		"\\": "_",
		"/"	: "_",
		"|"	: "_",
		":"	: "_",
		"*"	: "_",
		"?"	: "_",
		'"'	: "_",
		"<"	: "_",
		">"	: "_",
		"&"	: "_",
		"#"	: "_",
		"'"	: "_",
		","	: "_",
		";"	: "_"
	}
	# Â«\Â», Â«/Â», Â«:Â», Â«*Â», Â«?Â», Â«"Â», Â«<Â», Â«>Â», Â«|Â»
	return "".join(filename_escape_table.get(c,c) for c in text)

def date2base(text=""):
	result = text
	re_obj = re.compile(r"(0[1-9]|[12][0-9]|3[01])[-](0[1-9]|1[012])[-](19|20)[0-9]{2}")

	for match in re_obj.finditer(result):
	    #print match.group()
	    datevalue = datetime.strptime(match.group(), "%d-%m-%Y").strftime("%Y-%m-%d")
	    result = result.replace(match.group(), datevalue)
	return result

def date2norm(text):
	result = text

	re_obj = re.compile(r"(19|20)[0-9]{2}[-](0[1-9]|1[012])[-](0[1-9]|[12][0-9]|3[01])")

	for match in re_obj.finditer(result):
	    #print match.group()
	    datevalue = datetime.strptime(match.group(), "%Y-%m-%d").strftime("%d-%m-%Y")
	    result = result.replace(match.group(), datevalue)
	return result

def get_referer_url():
	from managers import request_manager
	env = request_manager.get_request().environment().environment()
	#raise Exception(str(env)) # to look through env on appropriate params
	result = env["HTTP_REFERER"] if "HTTP_REFERER" in env else "/Documents_share.vdom"
	if "home_page" in result.lower():
		result = "/Documents_share.vdom"
	#result = result.replace('&',"%26") # to process several arguments
	return result

def collect_from_args_to(req_args, args_list):
	return dict([k, req_args[k] if k in req_args and req_args[k] else ""] for k in args_list)

def getVal(args, a=""):
	result = ""
	if a.__class__ == int:
		try:
			result = args and args[a] or ""
		except:
			result = ""
	else:
		result = args and a in args and args[a] or ""
	return result

def get_logout_back_url():
	import managers

	# first, get the REQUEST_URI, current address string, with all GET arguments.
	env = managers.request_manager.get_request().environment().environment()
	result = env["REQUEST_URI"] if "REQUEST_URI" in env else ""
	# replace "&" with %26 - in order to process several arguments
	result = result.replace('&',"%26")
	return result

def scalar(key, defaultvalue = None, castto = None):
    notdecoded = request.arguments[ key ] if key in request.arguments else defaultvalue
    value = notdecoded.decode( 'utf8' ) if isinstance( notdecoded, str ) else notdecoded
    return castto(value) if castto and value else value

from xml.dom.minidom import parseString

class xml_event(object):
	def __init__(self, xmldata):
		from engine_core import global_log
		#global_log.insert(0,xmldata.replace("<", "&lt;"))
		self.parse_map = {"fileId":"file_name","mappedFolder":"mapped_folder","ocr":"run_ocr","content":"ocr_content","timeStamp":"timestamp","status":"delete_status","answerValue":"answer_value"}
		self.workflow_id = None
		self.user_id = None
		#self.attributes = {}

		dom = parseString(xmldata.encode("utf8"))
		event = dom.childNodes[0] if dom.childNodes else None
		if not event or event.nodeName != "event":
			raise Exception ("Invalid event")
		self.event_type = event.getAttribute("name")
		for attibute in event.childNodes:
			if attibute.nodeType != attibute.ELEMENT_NODE:
				continue
			if attibute.nodeName == "user":
				self.user_id = attibute.getAttribute("id")
			elif attibute.nodeName == "workflow":
				self.workflow_id = attibute.getAttribute("id")
			elif attibute.nodeName == "attribute":
				attr_name = attibute.getAttribute("name")
				if attr_name in self.parse_map:
					setattr(self, self.parse_map[attr_name], attibute.childNodes[0].nodeValue if attibute.childNodes else "")
				#self.attributes[attibute.getAttribute("name")] = attibute.childNodes[0].nodeValue if attibute.childNodes else ""
		if not self.user_id:
			raise Exception ("Invalid event: no user_id")
		if not self.workflow_id and self.event_type not in ("insert","modify"):
			raise Exception ("Invalid event: no workflow_id")

	def toxml(self):
		wf_id = "<workflow id=\"%s\"/>"%self.workflow_id if self.workflow_id else ""
		attributes = ""
		for key,value in self.parse_map.iteritems():
			if getattr(self, value,None):
				attributes += u"<attribute name=\"%s\">%s</attribute>"%(key, getattr(self, value))
		return u"""<event name="{type}">
 	<user id="{user_id}"/>
 	{workflow_id}
	{attributes}
 </event>""".format(type=self.event_type, user_id = self.user_id, workflow_id = wf_id, attributes = attributes)

