class WidgetButton(object):

	def __init__(self, vdom_button):
		self.__vdom_button	= vdom_button
		self.__title		= u"Button"
		self.__css_class	= "widget_button_%s" % vdom_button.id.replace("-","_")
		self.__use_local_css= True
		self._icon			= "add"
		self._text_color	= COLORS["green"]
		self._left			= vdom_button.left
		self._width			= vdom_button.width

	# if you do not overide the css_class, that will use current one.
	def set_data(self, title_key, css_class=""):
		import localization
		loc = localization.get_lang()
		self.__title		= loc.get(title_key, u"Button")
		if css_class:
			self.__css_class = css_class
			self.__use_local_css = False

	def render(self):

		self.__vdom_button.left		= self._left
		self.__vdom_button.width		= self._width
		self.__vdom_button.classname 	= self.__css_class+"_bg"
		styles = "%s%s" % (
			CSS_BACKGROUND % {"css_class_bg"	: self.__css_class+"_bg" },
			CSS_TEXT % {
				"css_class_text": self.__css_class+"_text",
				"icon"			: self._icon,
				"text_color"	: self._text_color
			}
		)
		self.__vdom_button.text = TITLE_TEMPLATE % {
			"style"				: styles if self.__use_local_css else "",
			"css_class_text" 	: (self.__css_class+"_text") if self.__use_local_css else "",
			"title"				: self.__title
		}

	@property
	def icon(self):
		return self._icon

	@icon.setter
	def icon(self, value):
		self._icon = ICONS[value] if value in ICONS.keys() else value

	@property
	def text_color(self):
		return self._text_color

	@text_color.setter
	def text_color(self, value):
		self._text_color = COLORS[value] if value in COLORS.keys() else value

	@property
	def left(self):
		return self._left

	@left.setter
	def left(self, value):
		self._left = value if isinstance(value, int) else 286

	@property
	def width(self):
		return self._width

	@width.setter
	def width(self, value):
		self._width = value if isinstance(value, int) else 124

