class WidgetWorkflowToolbar(object):

	def __init__(self,  action_html_guid, workflow_guid, is_template):
		self.__workflow_guid	= workflow_guid		# link to concrete workflow
		self.__is_template		= is_template		#
		self.__action_html_guid = action_html_guid.replace("-","_")	# which html container is using 'custom' event.

		self.__css_class		= "widget_wf_cnt_%s" % action_html_guid
		self.__local_css		= True

		self._status			= 0 	# 0: stopped, 1: pause, 2: active;
		self._icon_error		= False # if error during workflow compilation
		self._icon_run_compile	= True
		self._icon_locked		= True
		self._icon_design		= True

	# if you do not override the css_class, that will use local one.
	def set_data(
			self, status,
			icon_error	= False, icon_locked		= False,
			icon_design	= False, icon_run_compile	= False,
			css_class=""
		):
		self._status		= status if isinstance(status, int) and status in [0,1,2] and not is_error else 0 # 0: stopped, 1: pause, 2: active;
		self._icon_error		= icon_error
		self._icon_run_compile	= icon_run_compile
		self._icon_locked		= icon_locked
		self._icon_design		= icon_design
		if css_class:
			self.__css_class = css_class
			self.__local_css = False

	def render(self):
		from io import StringIO
		contents = StringIO()

		icon_template = ICON_TEMPLATE % {
			"css_class_icon"	: self.__css_class+"_icon",
			"action_html_guid"	: self.__action_html_guid,
			"is_template"		: self.__is_template,
			"wf_guid"			: self.__workflow_guid,
			"icon"				: "%(icon)s",
			"command"			: "%(command)s"
		}

		if self.__local_css:
			contents.write(u"<style>")
			contents.write(
				CSS_BACKGROUND % {	"css_class_bg" : self.__css_class+"_bg"	}
			)
			contents.write(
				CSS_ICON % { "css_class_icon" : self.__css_class+"_icon" }
			)
			contents.write(u"</style>")

		contents.write(u"""<div style="text-align: right;" class="%(css_class_bg)s" >""" % {"css_class_bg" : self.__css_class+"_bg"})

		if self._icon_error:
			contents.write(	icon_template % { "icon":ICONS["error"], 		"command":"error" } )
		if self._icon_run_compile:
			contents.write(	icon_template % { "icon":ICONS["run_compile"], 	"command":"run_compile" } )
		if self._icon_locked:
			contents.write(	icon_template % { "icon":ICONS["locked"],		"command":"locked" } )
#		if self._icon_design:
#			contents.write(	icon_template % { "icon":ICONS["design"], 		"command":"design" } )

		for icon_name in STATUS_ICONS[self._status]:
			contents.write(	icon_template % { "icon":ICONS[icon_name], 		"command":icon_name	} )

		contents.write(u"</div>")
		result = contents.getvalue()


		contents.close()
		return result

	@property
	def icon_error(self):
		return self._icon_error

	@icon_error.setter
	def icon_error(self, value):
		self._icon_error = value

	@property
	def icon_run_compile(self):
		return self._icon_run_compile

	@icon_run_compile.setter
	def icon_run_compile(self, value):
		self._icon_run_compile = value

	@property
	def icon_locked(self):
		return self._icon_locked

	@icon_locked.setter
	def icon_locked(self, value):
		self._icon_locked = value

	@property
	def icon_design(self):
		return self._icon_design

	@icon_design.setter
	def icon_design(self, value):
		self._icon_design = value

	@property
	def status(self):
		return self._status

	@status.setter
	def status(self, value):
		self._status = value

