class WidgetWorkflowTable(object):

	def __init__(self, dtable, hpt_id):
		self.__dtable 			= dtable
		self.__hpt_custom_id 	= hpt_id
		self._workflows			= []
		#self._active			= []

		self._selected		= "" 		# must be guid of selected workflow class
		self._workflow_processes = []

		self._header		= []

	def set_data(self, workflows):
		self._workflows		= workflows
		#self._active		= active

	@property
	def header(self):
		if not self._header:
			self._header = ["wf_guid","expand_button","title","toolbar"]
		return self._header

	@header.setter
	def header(self, header):
		self._header = header if isinstance(header, list) else self._header

	@property
	def selected(self):
		return self._selected

	@selected.setter
	def selected(self, value):
		self._selected = value if any(value == wf.guid for wf in self._workflows) else ""

	@property
	def workflow_processes(self):
		return self._workflow_processes

	@workflow_processes.setter
	def workflow_processes(self, value):
		self._workflow_processes = value

	def wf_class_row_format(self, wf_class):
		if wf_class.guid != self.selected:
			content = wf_class.label#wf_class.name
			row = [wf_class.guid, "+", content, self.wf_toolbar(wf_class)]
		else:
			content = wf_class.name
			for wf_doc in self._workflow_processes:
				content += "<br/>%s" % " | ".join(self.wf_doc_row_format(wf_doc))

			row = [wf_class.guid, "-", content, self.wf_toolbar(wf_class)]

		return row


	def wf_toolbar(self, wf_object):
		#raise Exception(str(Workflow))
		#wf_object.status = 2 if wf_object.guid in self._active else 0

		main_toolbar = WidgetWorkflowToolbar(
			self.__hpt_custom_id,
			wf_object.guid,
			isinstance(wf_object, Workflow)
		)
		main_toolbar.status	= int(wf_object.status) # start

		if isinstance(wf_object, Workflow):
			main_toolbar.icon_run_compile 	= False if int(wf_object.status) == 2 else True
			main_toolbar.icon_design 		= True
			main_toolbar.icon_locked 		= wf_object.modified
			main_toolbar.icon_error 		= wf_object.error
		else:
			main_toolbar.icon_run_compile 	= False
			main_toolbar.icon_design 		= False
			main_toolbar.icon_locked 		= False
			main_toolbar.icon_error 		= False
		return main_toolbar.render()

	def wf_doc_row_format(self, wf_doc):
		return [
			"%s %s" % (wf_doc.author.first_name, wf_doc.author.last_name),
			wf_doc.document.name if wf_doc.document else "(!) Document not found (!)",
			wf_doc.state,
			self.wf_toolbar(wf_doc)]

	def render(self):
		result = []

		for wf_class in self._workflows:
			result.append(self.wf_class_row_format(wf_class))

		if result:
			self.__dtable.header 	= json.dumps(self.header)
			self.__dtable.data		= json.dumps(result)
		else:
			self.__dtable.visible 	= "0"

from engine_core import engine
import re

# ~~~~~~~~~~ Workflow Script Object ~~~~~~~~~~~~~	#

def WF_proxy(wf_inst_guid, wf_class_name):
	return WorkflowScriptObject(wf_inst_guid, wf_class_name)

class WorkflowScriptObject(object):

	def __init__(self, wf_inst_guid, wf_class_name):
		from class_workflow import Workflow
		from class_share_file import ShareFile

		wf_instance = self.engine.running_workflows[wf_inst_guid] if wf_inst_guid else None

		if not wf_instance:
			self.__workflow = Workflow.get_by_name(wf_class_name)
		else:
			self.__workflow = Workflow.get_by_name(wf_instance.__class__.__name__)

		self.__currentDocument = ShareFile.get_by_guid(wf_instance.current_document) if wf_instance else None

	@property
	def folders(self):
		return FolderContainerScriptObject(self.__workflow.folders)

	@property
	def activeUsers(self):
		from class_user import User
		return UserContainerScriptObject(User.get_all())

	@property
	def currentDocument(self):
		return DocumentScriptObject(self.__currentDocument)

	@property
	def label(self):
		return self.__workflow.label

# ~~~~~~~~~~ Step Script Objects ~~~~~~~~~~~~~	#

def Step_proxy(wf_inst_guid, step_name, step_type):
	xml_event = engine.running_workflows[wf_inst_guid].saved_events.get(step_name)
	debug("-------> Step_proxy: %s "%str(xml_event))
	return {	"start": StartStepScriptObject,
				"documentEvent": DocumentEventStepScriptObject,
				"move": MoveStepScriptObject,
				"stop": StopStepScriptObject,
				"gateway": GatewayStepScriptObject	}[step_type](xml_event)

class AbstractStepScriptObject(object):

	def __init__(self, xml_event_input):
		self.xml_event = xml_event_input

class StartStepScriptObject(AbstractStepScriptObject):
	pass

class StopStepScriptObject(AbstractStepScriptObject):
	pass

class GatewayStepScriptObject(AbstractStepScriptObject):
	pass

class DocumentEventStepScriptObject(AbstractStepScriptObject):

	@property
	def user(self):
		from class_user import User
		return UserScriptObject(User.get_by_guid(self.xml_event.user_id))

	@property
	def contentStored(self):
		return bool(self.xml_event.run_ocr)

	@property
	def document(self):
		from class_share_file import ShareFile
		return DocumentScriptObject(ShareFile.get_by_guid(self.xml_event.file_name))

	@property
	def searchUsers(self):
		from class_user import User
		from engine_core import engine, global_log
		found_users = []
		if hasattr(self.xml_event, "ocr_content"):
			#global_log.insert(0, self.xml_event.ocr_content)
			#engine.log(self.xml_event.workflow_id, self.xml_event.ocr_content)
			found_users = [
				u for u in User.get_all() if u.first_name and u.last_name and re.search(
					r"\b(%(f)s\s+?%(l)s|%(l)s\s+?%(f)s)\b" % { "f":u.first_name, "l":u.last_name }, self.xml_event.ocr_content, re.IGNORECASE
				)
			]
			#global_log.insert(0, ", ".join([u.first_name for u in found_users]))

		return UserContainerScriptObject(found_users)

	@property
	def search(self):
		return SearchScriptObject(self.xml_event.ocr_content)

	@property
	def searchRegExp(self):
		return SearchRegExpScriptObject(self.xml_event.ocr_content)

	@property
	def barCodeValue(self):
		return "abrabrabr"

