	def document(self):
		from class_share_file import ShareFile
		return DocumentScriptObject(ShareFile.get_by_guid(self.xml_event.file_name))

	@property
	def user(self):
		from class_user import User
		return UserScriptObject(User.get_by_guid(self.xml_event.user_id))

class NotificationStepScriptObject(AbstractStepScriptObject):

	def __init__(self, id):
		AbstractStepScriptObject.__init__(self, id)

	@property
	def user(self):
		from class_user import User
		return User()

	@property
	def notified(self):
		return True

class ArchiveStepScriptObject(AbstractStepScriptObject):

