		AbstractStepScriptObject.__init__(self, id)

	@property
	def user(self):
		from class_user import User
		return User()

# ~~~~~~~~~~~ Specific Scalar Script Objects ~~~~~~~~~~~~ #

class UserScriptObject(object):

	def __init__(self, user):
		self.__user = user

	def __repr__(self):
		return self.__user.guid

	def __call__(self):
		return self.__user

	@property
	def firstName(self):
		return self.__user.first_name

	@property
	def lastName(self):
		return self.__user.last_name

	@property
	def email(self):
		return self.__user.email

class FolderScriptObject(object):

	def __init__(self, share_dir):
		self.__share_dir = share_dir

	def __repr__(self):
		return self.__share_dir.guid

	def __call__(self):
		return self.__share_dir

	@property
	def name(self):
		return self.__share_dir.name

	@property
	def path(self):
		return self.__share_dir.path()

	@property
	def users(self):
		users = self.__share_dir.users_acl()
		script_users = [UserScriptObject(u) for u in users]
		return ContainerScriptObject(script_users)

class DocumentScriptObject(object):

	def __init__(self, share_file):
		self.__share_file = share_file

	def __repr__(self):
		return self.__share_file.guid

	@property
	def name(self):
		return self.__share_file.name

	@property
	def type(self):
		return self.__share_file.extension()

	@property
	def size(self):
		return self.__share_file.size()

	@property
	def userRights(self):
		users = self.__share_file.users_acl()
		script_users = [UserScriptObject(u) for u in users]
		return ContainerScriptObject(script_users)



# ~~~~~~~~~~~ Container Script Objects ~~~~~~~~~~~~ #

class AbstractContainerScriptObject(object):

	def __init__(self, container):
		self.container 	= container
		self.index		= len(container)

	def __call__(self, index):
		return self.container[index]

	def __getitem__(self, index):
		if index in range(len(container)):
			return self.container[index]
		else:
			raise KeyError

	def __iter__(self):
		return self

	def next(self):
		if self.index == 0:
			self.index = len(self.container)
			raise StopIteration
		self.index -= 1
		return self.container[self.index]


	@property
	def count(self):
		return len(self.container)

	@property
	def isEmpty(self):
		return not bool(self.container)

class FolderContainerScriptObject(AbstractContainerScriptObject):

	def __init__(self, share_dir_list):
		container = dict([(sd.name, FolderScriptObject(sd)) for sd in share_dir_list])
		AbstractContainerScriptObject.__init__(self, container)

class UserContainerScriptObject(AbstractContainerScriptObject):

	def __init__(self, user_list):
		container = [UserScriptObject(u) for u in user_list]
		AbstractContainerScriptObject.__init__(self, container)



class StringContainerScriptObject(AbstractContainerScriptObject):

	def __init__(self, string_list):
		AbstractContainerScriptObject.__init__(self, string_list)

# ~~~~~~~~~~~ Search Script Objects ~~~~~~~~~~~~ #

class SearchRegExpScriptObject(object):

	def __init__(self, content):
		self.__content = content

	def __call__(self, search_pattern):
		return StringContainerScriptObject(re.findall(search_pattern, self.__content))

class SearchScriptObject(object):

	def __init__(self, content):
		self.__content = content

	def __call__(self, word):
		return StringContainerScriptObject([line for line in WorkflowScriptDebugger.get_content().split("\n") if word in line])

